  TextField,
  InputAdornment,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Alert,
} from '@mui/material';
import {
  Person,
  ShoppingCart,
  Restaurant,
  Favorite,
  FavoriteBorder,
  Search as SearchIcon,
  Add,
  Remove,
  Delete as DeleteIcon,
} from '@mui/icons-material';

const SimpleUserDashboard = () => {
  const navigate = useNavigate();

  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [productsLoading, setProductsLoading] = useState(true);
  const [productsError, setProductsError] = useState(null);
  const [currentView, setCurrentView] = useState('dashboard');
  const [vendorProducts, setVendorProducts] = useState([]);
  const [selectedProductId, setSelectedProductId] = useState(null);
  const [showProductDetail, setShowProductDetail] = useState(false);

  const [cartItems, setCartItems] = useState([]);
  const [favoriteItems, setFavoriteItems] = useState([]);
  const [userStats, setUserStats] = useState({
    totalOrders: 0,
    totalSpent: 0,
    points: 0,
    favoriteItemsCount: 0,
    memberSince: new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long' }),
    lastOrder: 'No orders yet',
    avgOrderValue: 0,
  });

  const [searchQuery, setSearchQuery] = useState('');
  const [filterCategory, setFilterCategory] = useState('all');

  const imageFallback = 'https://via.placeholder.com/300x200?text=No+Image';

  // Auth check
  useEffect(() => {
    if (!localStorage.getItem('token')) {
      navigate('/login');
    }
  }, [navigate]);

  // Load user
  useEffect(() => {
    try {
      const stored = localStorage.getItem('user');
      setUserData(stored ? JSON.parse(stored) : { name: 'Guest', email: 'guest@example.com', role: 'USER' });
    } catch {
      setUserData({ name: 'Guest', email: 'guest@example.com', role: 'USER' });
    }
    setLoading(false);
  }, []);

  // Load & persist favorites + cart
  useEffect(() => {
    const storedFavorites = localStorage.getItem('favorites');
    if (storedFavorites) setFavoriteItems(JSON.parse(storedFavorites));

    const storedCart = localStorage.getItem('cart');
    if (storedCart) setCartItems(JSON.parse(storedCart));
  }, []);

  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favoriteItems));
  }, [favoriteItems]);

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cartItems));
  }, [cartItems]);

  // Load products with error handling
  useEffect(() => {
    const loadProducts = async () => {
      setProductsLoading(true);
      setProductsError(null);
      try {
        const res = await fetch('http://localhost:8081/api/products');
        if (!res.ok) {
          throw new Error(`Server returned ${res.status} - ${res.statusText}`);
        }
        const data = await res.json();
        const products = Array.isArray(data?.data) ? data.data : data || [];

        const normalized = products.map(p => ({
          ...p,
          vendor: p?.vendor?.shopName || p?.vendor?.name || p?.vendor || 'Unknown Vendor',
          image: p.image || imageFallback,
          ingredients: p.ingredients || 'No ingredients listed',
          nutrition: p.nutrition || 'No nutrition info',
          reviews: p.reviews || [],
        }));

        setVendorProducts(normalized);
      } catch (err) {
        console.error('Products failed:', err);
        setProductsError(err.message);
        setVendorProducts([]);
      } finally {
        setProductsLoading(false);
      }
    };

    loadProducts();
  }, []);

  // ── Handlers ──────────────────────────────────────────────────
  const handleLogout = () => {
    localStorage.clear();
    navigate('/home');
  };

  const handleAddToFavorites = (product) => {
    if (favoriteItems.some(f => f.id === product.id)) {
      alert('Already in favorites!');
      return;
    }

    const newFav = {
      id: product.id,
      name: product.name,
      vendor: product.vendor,
      price: product.price,
      rating: product.rating,
      image: product.image,
    };

    setFavoriteItems(prev => {
      const updated = [...prev, newFav];
      // Update stats right away
      setUserStats(prevStats => ({
        ...prevStats,
        favoriteItemsCount: updated.length,
      }));
      return updated;
    });

    alert(`"${product.name}" added to favorites!`);
  };

  const handleRemoveFromFavorites = (id) => {
    setFavoriteItems(prev => {
      const updated = prev.filter(f => f.id !== id);
      setUserStats(prevStats => ({
        ...prevStats,
        favoriteItemsCount: updated.length,
      }));
      return updated;
    });
  };

  const handleAddToCart = (product, qty = 1) => {
    setCartItems(prev => {
      const existing = prev.find(item => item.id === product.id);
      if (existing) {
        return prev.map(item =>
          item.id === product.id ? { ...item, quantity: item.quantity + qty } : item
        );
      }
      return [...prev, { ...product, quantity: qty }];
    });
  };

  // ... other cart handlers (unchanged for brevity)
  const handleUpdateCartQuantity = (id, delta) => {
    setCartItems(prev =>
      prev.map(item =>
        item.id === id ? { ...item, quantity: Math.max(1, item.quantity + delta) } : item
      )
    );
  };

  const handleRemoveFromCart = (id) => {
    setCartItems(prev => prev.filter(item => item.id !== id));
  };

  const handleCheckout = () => {
    if (cartItems.length === 0) return alert('Cart is empty');
    alert('Checkout simulation: Total NPR ' + cartItems.reduce((s, i) => s + i.price * i.quantity, 0).toFixed(2));
    setCartItems([]);
  };

  // ── Computed ──────────────────────────────────────────────────
  const filteredProducts = vendorProducts.filter(p => {
    const q = searchQuery.toLowerCase();
    return (
      (p.name.toLowerCase().includes(q) || p.vendor.toLowerCase().includes(q)) &&
      (filterCategory === 'all' || p.category === filterCategory)
    );
  });

  const categories = ['all', ...new Set(vendorProducts.map(p => p.category).filter(Boolean))];

  if (loading) {
    return <Box sx={{ display: 'flex', justifyContent: 'center', minHeight: '80vh', alignItems: 'center' }}>
      <CircularProgress size={60} />
    </Box>;
  }

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      {/* Dashboard */}
      {currentView === 'dashboard' && (
        <>
          <Paper sx={{ p: 3, mb: 4, bgcolor: 'primary.light' }}>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <Button variant="contained" fullWidth startIcon={<Restaurant />} onClick={() => setCurrentView('meals')}>
                  Browse Products
                </Button>
              </Grid>
              <Grid item xs={12} sm={6}>
                <Button variant="outlined" fullWidth startIcon={<ShoppingCart />} onClick={() => setCurrentView('cart')}>
                  Cart ({cartItems.length})
                </Button>
              </Grid>
            </Grid>
          </Paper>

          <Box sx={{ mb: 4, display: 'flex', alignItems: 'center' }}>
            <Avatar sx={{ width: 64, height: 64, bgcolor: 'primary.main', mr: 2 }}>
              <Person />
            </Avatar>
            <Box>
              <Typography variant="h5">Welcome, {userData?.name || 'User'}!</Typography>
              <Typography color="text.secondary">
                Member since {userStats.memberSince} • {userStats.points} points
              </Typography>
            </Box>
          </Box>

          <Grid container spacing={3} sx={{ mb: 5 }}>
            <Grid item xs={6} sm={3}>
              <Paper sx={{ p: 2, textAlign: 'center' }}>
                <Typography variant="h4">{userStats.totalOrders}</Typography>
                <Typography color="text.secondary">Orders</Typography>
              </Paper>
            </Grid>
            <Grid item xs={6} sm={3}>
              <Paper sx={{ p: 2, textAlign: 'center', bgcolor: 'success.main', color: 'white' }}>
                <Typography variant="h4">NPR {userStats.totalSpent.toFixed(0)}</Typography>
                <Typography>Total Spent</Typography>
              </Paper>
            </Grid>
            <Grid item xs={6} sm={3}>
              <Paper sx={{ p: 2, textAlign: 'center' }}>
                <Typography variant="h4">{userStats.favoriteItemsCount}</Typography>
                <Typography color="text.secondary">Favorites</Typography>
              </Paper>
            </Grid>
            <Grid item xs={6} sm={3}>
              <Paper sx={{ p: 2, textAlign: 'center' }}>
                <Typography variant="h4">{userStats.points}</Typography>
                <Typography color="text.secondary">Points</Typography>
              </Paper>
            </Grid>
          </Grid>
        </>
      )}

      {/* Browse Products */}
      {currentView === 'meals' && (
        <>
          <Typography variant="h4" gutterBottom>Browse Products</Typography>

          <Box sx={{ display: 'flex', gap: 2, mb: 3, flexWrap: 'wrap' }}>
            <TextField
              fullWidth
              placeholder="Search..."
              value={searchQuery}
              onChange={e => setSearchQuery(e.target.value)}
              InputProps={{ startAdornment: <InputAdornment position="start"><SearchIcon /></InputAdornment> }}
            />
            <FormControl sx={{ minWidth: 140 }}>
              <InputLabel>Category</InputLabel>
              <Select value={filterCategory} label="Category" onChange={e => setFilterCategory(e.target.value)}>
                {categories.map(c => <MenuItem key={c} value={c}>{c}</MenuItem>)}
              </Select>
            </FormControl>
          </Box>

          {productsLoading ? (
            <Box sx={{ textAlign: 'center', py: 8 }}>
              <CircularProgress />
            </Box>
          ) : productsError ? (
            <Alert severity="error" action={
              <Button color="inherit" size="small" onClick={() => window.location.reload()}>
                Retry
              </Button>
            }>
              Failed to load products: {productsError}
            </Alert>
          ) : filteredProducts.length === 0 ? (
            <Alert severity="info">No products match your search/filter</Alert>
          ) : (
            <Grid container spacing={3}>
              {filteredProducts.map(product => {
                const isFav = favoriteItems.some(f => f.id === product.id);
                return (
                  <Grid item xs={12} sm={6} md={4} key={product.id}>
                    <Card sx={{ height: '100%' }}>
                      <Box sx={{ position: 'relative', pt: '75%' }}>
                        <img
                          src={product.image}
                          alt={product.name}
                          style={{ position: 'absolute', inset: 0, objectFit: 'cover' }}
                          onError={e => e.target.src = imageFallback}
                        />
                        <Chip label={`NPR ${product.price}`} color="success" sx={{ position: 'absolute', top: 8, right: 8 }} />
                      </Box>
                      <CardContent>
                        <Typography variant="h6">{product.name}</Typography>
                        <Typography variant="body2" color="text.secondary">{product.vendor}</Typography>
                        <Rating value={product.rating || 0} size="small" readOnly />
                        <Box sx={{ mt: 2, display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                          <Button variant="contained" size="small" onClick={() => handleAddToCart(product)}>
                            Add to Cart
                          </Button>
                          <Button
                            variant={isFav ? "contained" : "outlined"}
                            size="small"
                            color={isFav ? "error" : "primary"}
                            startIcon={isFav ? <Favorite /> : <FavoriteBorder />}
                            onClick={() => handleAddToFavorites(product)}
                          >
                            {isFav ? 'Favorited' : 'Favorite'}
                          </Button>
                        </Box>
                      </CardContent>
                    </Card>
                  </Grid>
                );
              })}
            </Grid>
          )}
        </>
      )}

      {/* Favorites */}
      {currentView === 'favorites' && (
        <Box>
          <Typography variant="h4" gutterBottom>My Favorites ({userStats.favoriteItemsCount})</Typography>
          {/* ... rest of favorites view ... */}
        </Box>
      )}

      {/* Cart, Profile, Dialog - unchanged for brevity */}
      {/* Add them back from your original code if needed */}
    </Container>
  );
};

export default SimpleUserDashboard;
