import React, { useState, useEffect, useCallback } from 'react';
import vendorService from '../services/vendorService';
import {
  Container, Paper, Typography, Box, Grid, Card, CardContent, Button,
  Avatar, List, ListItem, ListItemText, ListItemIcon, Divider, Alert,
  CircularProgress, Dialog, DialogTitle, DialogContent, DialogActions,
  TextField, FormControl, InputLabel, Select, Table, TableBody,
  TableCell, TableContainer, TableHead, TableRow, Chip, IconButton,
  MenuItem, LinearProgress, Badge, Drawer, useTheme, useMediaQuery,
  Snackbar,
} from '@mui/material';
import {
  ShoppingCart, Assessment, TrendingUp, AttachMoney, Logout, Add,
  Edit, Delete, CheckCircle, Store, Menu as MenuIcon, Close as CloseIcon,
  Inventory, LocalOffer, BarChart, Dashboard, Warning, Refresh,
} from '@mui/icons-material';

// ─────────────────────────────────────────────────────────────────────────────
// API helpers — talk directly to your Spring Boot backend
// ─────────────────────────────────────────────────────────────────────────────
const BASE = 'http://localhost:8081/api';

const authHeaders = () => ({
  'Content-Type': 'application/json',
  Authorization: `Bearer ${localStorage.getItem('token')}`,
});

/**
 * Orders API
 * Expected endpoints:
 *   GET  /api/orders/vendor          → { data: Order[] }
 *   PATCH /api/orders/{id}/status    → { data: Order }
 */
const vendorOrderAPI = {
  getAll: async () => {
    const res = await fetch(`${BASE}/orders/vendor`, { headers: authHeaders() });
    if (!res.ok) throw new Error(`Orders API ${res.status}`);
    const json = await res.json();
    // Support both { data: [] } and bare []
    return Array.isArray(json?.data) ? json.data : Array.isArray(json) ? json : [];
  },

  updateStatus: async (orderId, status) => {
    const res = await fetch(`${BASE}/orders/${orderId}/status`, {
      method: 'PATCH',
      headers: authHeaders(),
      body: JSON.stringify({ status }),
    });
    if (!res.ok) throw new Error(`Status API ${res.status}`);
    const json = await res.json();
    return json?.data || json;
  },
};

/**
 * Promotions API
 * Expected endpoints:
 *   GET    /api/promotions/vendor    → { data: Promo[] }
 *   POST   /api/promotions           → { data: Promo }
 *   PATCH  /api/promotions/{id}      → { data: Promo }
 *   DELETE /api/promotions/{id}      → 204
 */
const promoAPI = {
  getAll: async () => {
    const res = await fetch(`${BASE}/promotions/vendor`, { headers: authHeaders() });
    if (!res.ok) throw new Error(`Promotions API ${res.status}`);
    const json = await res.json();
    return Array.isArray(json?.data) ? json.data : Array.isArray(json) ? json : [];
  },
  create: async (body) => {
    const res = await fetch(`${BASE}/promotions`, { method: 'POST', headers: authHeaders(), body: JSON.stringify(body) });
    if (!res.ok) throw new Error(`Create promo ${res.status}`);
    const json = await res.json();
    return json?.data || json;
  },
  toggle: async (id, active) => {
    const res = await fetch(`${BASE}/promotions/${id}`, { method: 'PATCH', headers: authHeaders(), body: JSON.stringify({ active }) });
    if (!res.ok) throw new Error(`Toggle promo ${res.status}`);
  },
  remove: async (id) => {
    const res = await fetch(`${BASE}/promotions/${id}`, { method: 'DELETE', headers: authHeaders() });
    if (!res.ok) throw new Error(`Delete promo ${res.status}`);
  },
};

// ─────────────────────────────────────────────────────────────────────────────
// Small reusable UI pieces
// ─────────────────────────────────────────────────────────────────────────────
const StatCard = ({ label, value, icon, bgcolor }) => (
  <Paper sx={{ p: 3, textAlign: 'center', bgcolor, color: 'white', borderRadius: 2 }}>
    <Box sx={{ mb: 1, opacity: 0.85 }}>{icon}</Box>
    <Typography variant="h4" fontWeight={800}>{value}</Typography>
    <Typography variant="body2" sx={{ opacity: 0.9, mt: 0.5 }}>{label}</Typography>
  </Paper>
);

const StatusChip = ({ status }) => {
  const map = { Pending: 'default', Preparing: 'warning', Ready: 'info', Delivered: 'success', Cancelled: 'error' };
  return <Chip label={status} color={map[status] || 'default'} size="small" />;
};

const SideNavItem = ({ icon, label, view, currentView, count, onClick }) => (
  <ListItem disablePadding>
    <ListItem button selected={currentView === view} onClick={() => onClick(view)}
      sx={{ borderRadius: 1, mx: 1, my: 0.25, cursor: 'pointer', '&.Mui-selected': { bgcolor: 'primary.50', color: 'primary.main' } }}>
      <ListItemIcon sx={{ minWidth: 36, color: currentView === view ? 'primary.main' : 'inherit' }}>
        {count !== undefined ? <Badge badgeContent={count} color="error" max={99}>{icon}</Badge> : icon}
      </ListItemIcon>
      <ListItemText primary={label}
        primaryTypographyProps={{ fontSize: 14, fontWeight: currentView === view ? 700 : 400 }} />
    </ListItem>
  </ListItem>
);

// ─────────────────────────────────────────────────────────────────────────────
// Main Component
// ─────────────────────────────────────────────────────────────────────────────
const SimpleVendorDashboard = () => {
  const theme    = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  // ── state ──────────────────────────────────────
  const [vendorData,  setVendorData]  = useState(null);
  const [products,    setProducts]    = useState([]);
  const [orders,      setOrders]      = useState([]);
  const [promotions,  setPromotions]  = useState([]);

  const [loading,       setLoading]       = useState(true);
  const [pageError,     setPageError]     = useState('');
  const [toast,         setToast]         = useState({ open: false, msg: '', severity: 'success' });

  const [currentView,        setCurrentView]        = useState('dashboard');
  const [mobileDrawerOpen,   setMobileDrawerOpen]   = useState(false);
  const [selectedCategory,   setSelectedCategory]   = useState('All');

  // product form
  const [showProductForm, setShowProductForm] = useState(false);
  const [editingProduct,  setEditingProduct]  = useState(null);
  const [savingProduct,   setSavingProduct]   = useState(false);
  const emptyProduct = { name:'', price:'', category:'Clothing', description:'', image:'', photoFile:null, stock:0 };
  const [productForm, setProductForm] = useState(emptyProduct);

  // promotion form
  const [promoDialogOpen, setPromoDialogOpen] = useState(false);
  const emptyPromo = { name:'', code:'', discount:'', duration:'' };
  const [promoForm, setPromoForm] = useState(emptyPromo);

  // ── helpers ────────────────────────────────────
  const showToast = (msg, severity = 'success') => setToast({ open: true, msg, severity });
  const closeToast = () => setToast(t => ({ ...t, open: false }));
  const switchView = (view) => { setCurrentView(view); setMobileDrawerOpen(false); };

  // ── loaders ────────────────────────────────────
  const loadProfile  = useCallback(async () => { const p = await vendorService.getVendorProfile();  setVendorData(p); },  []);
  const loadProducts = useCallback(async () => { const d = await vendorService.getVendorProducts(); setProducts(d);    }, []);
  const loadOrders   = useCallback(async () => {
    try   { const d = await vendorOrderAPI.getAll(); setOrders(d); }
    catch (e) { console.error('Orders:', e); showToast('Could not load orders.', 'warning'); }
  }, []);
  const loadPromos   = useCallback(async () => {
    try   { const d = await promoAPI.getAll(); setPromotions(d); }
    catch (e) { console.error('Promos:', e); /* endpoint may not exist yet */ }
  }, []);

  const refreshAll = useCallback(async () => {
    await Promise.allSettled([loadProfile(), loadProducts(), loadOrders(), loadPromos()]);
    showToast('Dashboard refreshed.');
  }, [loadProfile, loadProducts, loadOrders, loadPromos]);

  // ── init ───────────────────────────────────────
  useEffect(() => {
    const init = async () => {
      setLoading(true);
      try {
        const raw  = localStorage.getItem('user');
        if (!raw) { setPageError('No user found. Redirecting…'); setTimeout(() => { window.location.href='/login'; }, 2000); return; }
        const user = JSON.parse(raw);
        if (user.role !== 'VENDOR') { setPageError('Access denied. Vendor account required.'); setTimeout(() => { window.location.href='/login'; }, 2000); return; }
        vendorService.setCurrentVendor(user.id);
        await Promise.allSettled([loadProfile(), loadProducts(), loadOrders(), loadPromos()]);
      } catch (e) {
        setPageError('Failed to load dashboard. Please refresh.');
      } finally {
        setLoading(false);
      }
    };
    init();

    const handleMsg = (e) => {
      if (e.data?.type === 'PRODUCT_DELETED')  setProducts(prev => prev.filter(p => p.id !== e.data.product?.id));
      if (e.data?.type === 'PRODUCT_UPDATED')  setProducts(prev => prev.map(p => p.id === e.data.product?.id ? e.data.product : p));
    };
    window.addEventListener('message', handleMsg);
    return () => window.removeEventListener('message', handleMsg);
  }, [loadProfile, loadProducts, loadOrders, loadPromos]);

  // ── product CRUD ───────────────────────────────
  const resetProductForm = () => { setProductForm(emptyProduct); setEditingProduct(null); setShowProductForm(false); };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    if (!file.type.startsWith('image/'))        { showToast('Select an image file.', 'error'); return; }
    if (file.size > 5 * 1024 * 1024)            { showToast('Photo must be <5 MB.', 'error');  return; }
    setProductForm(p => ({ ...p, photoFile: file, image: URL.createObjectURL(file) }));
  };

  const handleSaveProduct = async () => {
    const { name, price, category, description } = productForm;
    if (!name || !price || !category || !description) { showToast('Fill all required fields.', 'error'); return; }
    setSavingProduct(true);
    try {
      let img = productForm.image;
      if (productForm.photoFile) {
        img = await new Promise((res, rej) => {
          const r = new FileReader();
          r.onload  = e => res(e.target.result);
          r.onerror = () => rej(new Error('Read failed'));
          r.readAsDataURL(productForm.photoFile);
        });
      }
      const payload = { name, price: parseFloat(price), description, category, image: img, stock: parseInt(productForm.stock) || 0 };

      if (editingProduct) {
        const updated = await vendorService.updateProduct(editingProduct.id, payload);
        setProducts(prev => prev.map(p => p.id === editingProduct.id ? updated : p));
        window.postMessage({ type: 'PRODUCT_UPDATED', product: updated, vendorId: vendorData?.id }, '*');
        showToast(`"${updated.name}" updated.`);
      } else {
        const saved = await vendorService.addProduct(payload);
        setProducts(prev => [...prev, saved]);
        showToast(`"${saved.name}" added — NPR ${saved.price}.`);
      }
      resetProductForm();
      await loadProfile();
    } catch (e) {
      showToast(`Save failed: ${e.message}`, 'error');
    } finally {
      setSavingProduct(false);
    }
  };

  const handleEditProduct = (product) => {
    setEditingProduct(product);
    setProductForm({ name: product.name, price: product.price, category: product.category,
      description: product.description || '', image: product.image || '', photoFile: null, stock: product.stock || 0 });
    setShowProductForm(true);
  };

  const handleDeleteProduct = async (id) => {
    const p = products.find(x => x.id === id);
    if (!p || !window.confirm(`Delete "${p.name}"?`)) return;
    try {
      const deleted = await vendorService.deleteProduct(id);
      setProducts(prev => prev.filter(x => x.id !== id));
      window.postMessage({ type: 'PRODUCT_DELETED', product: deleted, vendorId: vendorData?.id }, '*');
      showToast(`"${p.name}" deleted.`);
      await loadProfile();
    } catch (e) { showToast(`Delete failed: ${e.message}`, 'error'); }
  };

  const handleToggleStock = async (id) => {
    const p = products.find(x => x.id === id);
    if (!p) return;
    try {
      const updated = await vendorService.updateProduct(id, { inStock: !p.inStock });
      setProducts(prev => prev.map(x => x.id === id ? updated : x));
    } catch (e) { showToast(`Update failed: ${e.message}`, 'error'); }
  };

  const handleClearAll = async () => {
    if (!window.confirm('Delete ALL products?')) return;
    try {
      await Promise.all(products.map(p => vendorService.deleteProduct(p.id)));
      setProducts([]);
      window.postMessage({ type: 'VENDOR_PRODUCTS_DELETED', vendorId: vendorData?.id }, '*');
      showToast('All products deleted.');
    } catch (e) { showToast(`Error: ${e.message}`, 'error'); }
  };

  // ── order actions ──────────────────────────────
  const handleUpdateOrderStatus = async (orderId, newStatus) => {
    try {
      const updated = await vendorOrderAPI.updateStatus(orderId, newStatus);
      setOrders(prev => prev.map(o => o.id === orderId ? { ...o, status: updated?.status || newStatus } : o));
      showToast(`Order #${orderId} → ${newStatus}.`);
    } catch (e) {
      // graceful local update when API not yet implemented
      setOrders(prev => prev.map(o => o.id === orderId ? { ...o, status: newStatus } : o));
      showToast(`Updated locally (${e.message}).`, 'warning');
    }
  };

  // ── promotions ─────────────────────────────────
  const handleCreatePromo = async () => {
    const { name, code, discount, duration } = promoForm;
    if (!name || !code || !discount || !duration) { showToast('Fill all promo fields.', 'error'); return; }
    const body = { name, code, discount: parseInt(discount), duration: parseInt(duration), active: true };
    try {
      const saved = await promoAPI.create(body);
      setPromotions(prev => [...prev, saved]);
      showToast(`Promotion "${name}" created.`);
    } catch {
      // local fallback if endpoint missing
      setPromotions(prev => [...prev, { id: Date.now(), ...body }]);
      showToast('Saved locally (promotions API unavailable).', 'warning');
    }
    setPromoForm(emptyPromo);
    setPromoDialogOpen(false);
  };

  const handleTogglePromo = async (id) => {
    const promo = promotions.find(p => p.id === id);
    if (!promo) return;
    try   { await promoAPI.toggle(id, !promo.active); }
    catch { /* silent */ }
    setPromotions(prev => prev.map(p => p.id === id ? { ...p, active: !p.active } : p));
  };

  const handleDeletePromo = async (id) => {
    try   { await promoAPI.remove(id); }
    catch { /* silent */ }
    setPromotions(prev => prev.filter(p => p.id !== id));
  };

  // ── derived ────────────────────────────────────
  const filteredProducts = products.filter(p => selectedCategory === 'All' || p.category === selectedCategory);
  const pendingOrders    = orders.filter(o => o.status === 'Pending').length;
  const totalRevenue     = vendorData?.revenue || 0;
  const lowStock         = products.filter(p => (p.stock||0) > 0 && (p.stock||0) <= 5).length;
  const avgOrder         = orders.length ? Math.round(orders.reduce((s,o) => s+(o.total||o.totalAmount||0), 0) / orders.length) : 0;

  const navItems = [
    { view:'dashboard',  label:'Dashboard',  icon:<Dashboard /> },
    { view:'products',   label:'Products',   icon:<Inventory />,    count: products.length || undefined },
    { view:'orders',     label:'Orders',     icon:<ShoppingCart />, count: pendingOrders   || undefined },
    { view:'analytics',  label:'Analytics',  icon:<BarChart /> },
    { view:'promotions', label:'Promotions', icon:<LocalOffer /> },
  ];

  // ── early returns ──────────────────────────────
  if (loading) return <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh"><CircularProgress size={60} /></Box>;
  if (pageError) return (
    <Container maxWidth="sm" sx={{ py:4 }}>
      <Alert severity="error" sx={{ mb:2 }}>{pageError}</Alert>
      <Button variant="contained" onClick={() => { window.location.href='/login'; }}>Go to Login</Button>
    </Container>
  );

  // ── sidebar ────────────────────────────────────
  const SidebarContent = () => (
    <Box sx={{ width: 220, pt: 2 }}>
      <Box sx={{ px:2, pb:2, display:'flex', alignItems:'center', gap:1.5 }}>
        <Avatar sx={{ bgcolor:'primary.main', width:40, height:40 }}><Store fontSize="small" /></Avatar>
        <Box>
          <Typography variant="subtitle2" fontWeight={700} noWrap>{vendorData?.name||'Vendor'}</Typography>
          <Typography variant="caption" color="text.secondary">Vendor</Typography>
        </Box>
      </Box>
      <Divider />
      <List dense>
        {navItems.map(n => <SideNavItem key={n.view} {...n} currentView={currentView} onClick={switchView} />)}
      </List>
      <Divider />
      <List dense>
        <ListItem disablePadding>
          <ListItem button onClick={() => { ['token','user'].forEach(k => localStorage.removeItem(k)); window.location.href='/login'; }}
            sx={{ borderRadius:1, mx:1, my:0.25, cursor:'pointer' }}>
            <ListItemIcon sx={{ minWidth:36 }}><Logout color="error" /></ListItemIcon>
            <ListItemText primary="Logout" primaryTypographyProps={{ fontSize:14, color:'error.main' }} />
          </ListItem>
        </ListItem>
      </List>
    </Box>
  );

  // ─────────────────────────────────────────────────────────────────────────
  return (
    <Box sx={{ display:'flex', minHeight:'100vh', bgcolor:'grey.50' }}>

      {/* Desktop sidebar */}
      {!isMobile && (
        <Paper elevation={2} sx={{ width:220, flexShrink:0, position:'sticky', top:0, height:'100vh', overflowY:'auto', borderRadius:0 }}>
          <SidebarContent />
        </Paper>
      )}

      {/* Mobile drawer */}
      {isMobile && (
        <Drawer open={mobileDrawerOpen} onClose={() => setMobileDrawerOpen(false)}>
          <Box sx={{ display:'flex', justifyContent:'flex-end', p:1 }}>
            <IconButton onClick={() => setMobileDrawerOpen(false)}><CloseIcon /></IconButton>
          </Box>
          <SidebarContent />
        </Drawer>
      )}

      <Box sx={{ flexGrow:1, overflow:'auto' }}>

        {/* Mobile topbar */}
        {isMobile && (
          <Paper elevation={1} sx={{ px:2, py:1.5, display:'flex', alignItems:'center', gap:2, borderRadius:0, position:'sticky', top:0, zIndex:100 }}>
            <IconButton onClick={() => setMobileDrawerOpen(true)}><MenuIcon /></IconButton>
            <Typography variant="h6" fontWeight={700} sx={{ flexGrow:1 }}>
              {navItems.find(n => n.view===currentView)?.label||'Dashboard'}
            </Typography>
            <Badge badgeContent={pendingOrders} color="error">
              <IconButton onClick={() => switchView('orders')}><ShoppingCart /></IconButton>
            </Badge>
          </Paper>
        )}

        <Container maxWidth="lg" sx={{ py:3 }}>

          {/* ══ DASHBOARD ══════════════════════════════════════════ */}
          {currentView === 'dashboard' && (
            <>
              <Box sx={{ mb:3, display:'flex', justifyContent:'space-between', alignItems:'flex-start', flexWrap:'wrap', gap:2 }}>
                <Box>
                  <Typography variant="h4" fontWeight={800}>Welcome back, {vendorData?.name} 👋</Typography>
                  <Typography color="text.secondary">{vendorData?.email}</Typography>
                </Box>
                <Button variant="outlined" startIcon={<Refresh />} onClick={refreshAll} sx={{ borderRadius:2 }}>Refresh</Button>
              </Box>

              <Grid container spacing={3} sx={{ mb:4 }}>
                <Grid item xs={6} md={3}><StatCard label="Total Products" value={products.length}                                    icon={<Inventory />}    bgcolor="primary.main" /></Grid>
                <Grid item xs={6} md={3}><StatCard label="In Stock"       value={products.filter(p=>(p.stock||0)>0).length}          icon={<CheckCircle />}  bgcolor="success.main" /></Grid>
                <Grid item xs={6} md={3}><StatCard label="Low Stock"      value={lowStock}                                           icon={<Warning />}      bgcolor="warning.main" /></Grid>
                <Grid item xs={6} md={3}><StatCard label="Total Orders"   value={orders.length}                                      icon={<ShoppingCart />} bgcolor="info.main"    /></Grid>
              </Grid>

              {pendingOrders > 0 && (
                <Alert severity="warning" sx={{ mb:3 }}
                  action={<Button color="inherit" size="small" onClick={() => switchView('orders')}>View</Button>}>
                  {pendingOrders} pending order{pendingOrders>1?'s':''} awaiting action.
                </Alert>
              )}

              <Grid container spacing={3} sx={{ mb:4 }}>
                <Grid item xs={12} md={6}>
                  <Paper sx={{ p:3, height:'100%' }}>
                    <Typography variant="h6" fontWeight={700} sx={{ mb:2 }}>Quick Actions</Typography>
                    <List dense>
                      {[
                        { icon:<Add color="primary"/>,        label:'Add New Product',  sub:'List a new item',                     action:()=>setShowProductForm(true) },
                        { icon:<Inventory color="primary"/>,  label:'Manage Products',  sub:`${products.length} products`,          action:()=>switchView('products') },
                        { icon:<ShoppingCart color="primary"/>,label:'View Orders',     sub:`${pendingOrders} pending`,             action:()=>switchView('orders') },
                        { icon:<BarChart color="primary"/>,   label:'Analytics',        sub:`NPR ${totalRevenue.toFixed(0)} rev`,   action:()=>switchView('analytics') },
                        { icon:<LocalOffer color="primary"/>, label:'Promotions',       sub:`${promotions.filter(p=>p.active).length} active`, action:()=>switchView('promotions') },
                      ].map(({ icon, label, sub, action }) => (
                        <ListItem key={label} button onClick={action} sx={{ borderRadius:1, mb:0.5 }}>
                          <ListItemIcon sx={{ minWidth:36 }}>{icon}</ListItemIcon>
                          <ListItemText primary={label} secondary={sub}
                            primaryTypographyProps={{ fontWeight:600, fontSize:14 }}
                            secondaryTypographyProps={{ fontSize:12 }} />
                        </ListItem>
                      ))}
                    </List>
                  </Paper>
                </Grid>
                <Grid item xs={12} md={6}>
                  <Paper sx={{ p:3, height:'100%' }}>
                    <Typography variant="h6" fontWeight={700} sx={{ mb:2 }}>Recent Orders</Typography>
                    {orders.length === 0
                      ? <Typography variant="body2" color="text.secondary">No orders yet.</Typography>
                      : (
                        <List dense>
                          {orders.slice(0,5).map(o => (
                            <ListItem key={o.id} sx={{ borderRadius:1, mb:0.5, bgcolor:'grey.50' }}>
                              <ListItemText
                                primary={`#${o.id} — ${o.customer||o.customerName||'Customer'}`}
                                secondary={`NPR ${o.total||o.totalAmount||0} • ${o.time||o.createdAt||''}`}
                                primaryTypographyProps={{ fontSize:13, fontWeight:600 }}
                                secondaryTypographyProps={{ fontSize:12 }} />
                              <StatusChip status={o.status} />
                            </ListItem>
                          ))}
                        </List>
                      )
                    }
                    <Button variant="outlined" fullWidth sx={{ mt:2 }} onClick={() => switchView('orders')}>
                      View All Orders ({orders.length})
                    </Button>
                  </Paper>
                </Grid>
              </Grid>

              <Paper sx={{ p:3, mb:3 }}>
                <Typography variant="h6" fontWeight={700} gutterBottom>Monthly Revenue Goal</Typography>
                <Box sx={{ mb:1, display:'flex', justifyContent:'space-between' }}>
                  <Typography variant="body2" color="text.secondary">NPR {totalRevenue.toFixed(0)} / NPR 15,000</Typography>
                  <Typography variant="body2" color="primary.main" fontWeight={600}>{Math.min(100, Math.round(totalRevenue/150))}%</Typography>
                </Box>
                <LinearProgress variant="determinate" value={Math.min(100, totalRevenue/150)} sx={{ height:8, borderRadius:4 }} />
              </Paper>

              <Paper sx={{ p:3 }}>
                <Typography variant="h6" fontWeight={700} gutterBottom>Vendor Profile</Typography>
                <Box sx={{ display:'flex', gap:2, flexWrap:'wrap', mt:1 }}>
                  <Chip label={`⭐ ${vendorData?.rating||0} Rating`} color="primary" />
                  <Chip label={`${orders.length} Orders`}            color="success" />
                  <Chip label={`NPR ${totalRevenue.toFixed(0)} Revenue`} color="warning" />
                  <Chip label={`${products.length} Products`}        color="info" />
                </Box>
              </Paper>
            </>
          )}

          {/* ══ PRODUCTS ═══════════════════════════════════════════ */}
          {currentView === 'products' && (
            <>
              <Box sx={{ display:'flex', justifyContent:'space-between', alignItems:'center', mb:3, flexWrap:'wrap', gap:2 }}>
                <Typography variant="h4" fontWeight={700}>Product Management</Typography>
                <Box sx={{ display:'flex', gap:1 }}>
                  <Button variant="contained" startIcon={<Add />} onClick={() => { setProductForm(emptyProduct); setEditingProduct(null); setShowProductForm(true); }}>Add Product</Button>
                  {products.length>0 && <Button variant="outlined" color="error" startIcon={<Delete />} onClick={handleClearAll}>Clear All</Button>}
                </Box>
              </Box>

              <Paper sx={{ p:2, mb:3 }}>
                <FormControl size="small" sx={{ minWidth:180 }}>
                  <InputLabel>Category</InputLabel>
                  <Select value={selectedCategory} label="Category" onChange={e => setSelectedCategory(e.target.value)}>
                    <MenuItem value="All">All Categories</MenuItem>
                    {['Clothing','Electronic','Foods','Books'].map(c => <MenuItem key={c} value={c}>{c}</MenuItem>)}
                  </Select>
                </FormControl>
                <Typography variant="caption" color="text.secondary" sx={{ ml:2 }}>
                  {filteredProducts.length} of {products.length} products
                </Typography>
              </Paper>

              {filteredProducts.length === 0 ? (
                <Paper sx={{ p:5, textAlign:'center' }}>
                  <Inventory sx={{ fontSize:64, color:'grey.300', mb:2 }} />
                  <Typography variant="h6" color="text.secondary" gutterBottom>No products yet</Typography>
                  <Button variant="contained" startIcon={<Add />} onClick={() => setShowProductForm(true)}>Add Your First Product</Button>
                </Paper>
              ) : (
                <TableContainer component={Paper}>
                  <Table>
                    <TableHead>
                      <TableRow sx={{ bgcolor:'grey.50' }}>
                        <TableCell>Product</TableCell>
                        <TableCell>Category</TableCell>
                        <TableCell align="right">Price (NPR)</TableCell>
                        <TableCell align="center">Stock</TableCell>
                        <TableCell align="center">Orders</TableCell>
                        <TableCell align="center">Rating</TableCell>
                        <TableCell align="center">Status</TableCell>
                        <TableCell align="center">Actions</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {filteredProducts.map(p => (
                        <TableRow key={p.id} hover>
                          <TableCell>
                            <Box sx={{ display:'flex', alignItems:'center', gap:1.5 }}>
                              <Box component="img"
                                src={p.image||`https://via.placeholder.com/48x48?text=${encodeURIComponent((p.name||'?')[0])}`}
                                alt={p.name} sx={{ width:44, height:44, objectFit:'cover', borderRadius:1 }}
                                onError={e => { e.target.src='https://via.placeholder.com/48x48?text=N/A'; }} />
                              <Box>
                                <Typography variant="body2" fontWeight={600}>{p.name}</Typography>
                                <Typography variant="caption" color="text.secondary" noWrap sx={{ maxWidth:160, display:'block' }}>{p.description}</Typography>
                              </Box>
                            </Box>
                          </TableCell>
                          <TableCell><Chip label={p.category} size="small" variant="outlined" /></TableCell>
                          <TableCell align="right" sx={{ fontWeight:600 }}>{(p.price||0).toFixed(2)}</TableCell>
                          <TableCell align="center">
                            <Chip label={p.stock||0} size="small" color={(p.stock||0)===0?'error':(p.stock||0)<=5?'warning':'success'} />
                          </TableCell>
                          <TableCell align="center">{p.orders||0}</TableCell>
                          <TableCell align="center">⭐ {p.rating||'—'}</TableCell>
                          <TableCell align="center">
                            <Chip label={p.inStock?'In Stock':'Out'} size="small" color={p.inStock?'success':'default'}
                              onClick={() => handleToggleStock(p.id)} sx={{ cursor:'pointer' }} />
                          </TableCell>
                          <TableCell align="center">
                            <Box sx={{ display:'flex', gap:0.5, justifyContent:'center' }}>
                              <IconButton size="small" color="primary" onClick={() => handleEditProduct(p)}><Edit fontSize="small" /></IconButton>
                              <IconButton size="small" color="error"   onClick={() => handleDeleteProduct(p.id)}><Delete fontSize="small" /></IconButton>
                            </Box>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>
              )}
            </>
          )}

          {/* ══ ORDERS ═════════════════════════════════════════════ */}
          {currentView === 'orders' && (
            <>
              <Box sx={{ display:'flex', justifyContent:'space-between', alignItems:'center', mb:3 }}>
                <Typography variant="h4" fontWeight={700}>Order Management</Typography>
                <Button startIcon={<Refresh />} onClick={loadOrders} variant="outlined" sx={{ borderRadius:2 }}>Refresh</Button>
              </Box>

              <Grid container spacing={2} sx={{ mb:3 }}>
                {['Pending','Preparing','Ready','Delivered'].map(s => (
                  <Grid item xs={6} sm={3} key={s}>
                    <Paper sx={{ p:2, textAlign:'center' }}>
                      <Typography variant="h5" fontWeight={700}>{orders.filter(o=>o.status===s).length}</Typography>
                      <StatusChip status={s} />
                    </Paper>
                  </Grid>
                ))}
              </Grid>

              {orders.length === 0 ? (
                <Paper sx={{ p:5, textAlign:'center' }}>
                  <ShoppingCart sx={{ fontSize:64, color:'grey.300', mb:2 }} />
                  <Typography variant="h6" color="text.secondary">No orders yet</Typography>
                </Paper>
              ) : (
                <TableContainer component={Paper}>
                  <Table>
                    <TableHead>
                      <TableRow sx={{ bgcolor:'grey.50' }}>
                        <TableCell>Order ID</TableCell><TableCell>Customer</TableCell>
                        <TableCell>Items</TableCell><TableCell align="right">Total (NPR)</TableCell>
                        <TableCell align="center">Status</TableCell><TableCell>Time</TableCell>
                        <TableCell align="center">Actions</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {orders.map(o => (
                        <TableRow key={o.id} hover>
                          <TableCell sx={{ fontWeight:700 }}>#{o.id}</TableCell>
                          <TableCell>{o.customer||o.customerName||'—'}</TableCell>
                          <TableCell>
                            <Typography variant="body2" noWrap sx={{ maxWidth:180 }}>
                              {o.items||o.itemsSummary||`${o.orderItems?.length||0} item(s)`}
                            </Typography>
                          </TableCell>
                          <TableCell align="right" sx={{ fontWeight:600 }}>
                            {(o.total||o.totalAmount||0).toFixed(0)}
                          </TableCell>
                          <TableCell align="center"><StatusChip status={o.status} /></TableCell>
                          <TableCell><Typography variant="caption" color="text.secondary">{o.time||o.createdAt||''}</Typography></TableCell>
                          <TableCell align="center">
                            <Box sx={{ display:'flex', gap:0.5, justifyContent:'center', flexWrap:'wrap' }}>
                              {o.status==='Pending'   && <><Button size="small" variant="contained" color="success" onClick={()=>handleUpdateOrderStatus(o.id,'Preparing')}>Accept</Button><Button size="small" variant="outlined" color="error" onClick={()=>handleUpdateOrderStatus(o.id,'Cancelled')}>Reject</Button></>}
                              {o.status==='Preparing' && <Button size="small" variant="contained" color="info"    onClick={()=>handleUpdateOrderStatus(o.id,'Ready')}>Mark Ready</Button>}
                              {o.status==='Ready'     && <Button size="small" variant="contained" color="success" onClick={()=>handleUpdateOrderStatus(o.id,'Delivered')}>Delivered</Button>}
                              {['Delivered','Cancelled'].includes(o.status) && <Chip label={o.status} size="small" color={o.status==='Delivered'?'success':'error'} />}
                            </Box>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>
              )}
            </>
          )}

          {/* ══ ANALYTICS ══════════════════════════════════════════ */}
          {currentView === 'analytics' && (
            <>
              <Typography variant="h4" fontWeight={700} sx={{ mb:3 }}>Analytics</Typography>
              <Grid container spacing={3} sx={{ mb:4 }}>
                <Grid item xs={6} md={3}><StatCard label="Total Revenue" value={`NPR ${totalRevenue.toFixed(0)}`} icon={<AttachMoney />}  bgcolor="success.main" /></Grid>
                <Grid item xs={6} md={3}><StatCard label="Total Orders"  value={orders.length}                    icon={<ShoppingCart />} bgcolor="primary.main" /></Grid>
                <Grid item xs={6} md={3}><StatCard label="Avg. Order"    value={`NPR ${avgOrder}`}                icon={<TrendingUp />}   bgcolor="info.main"    /></Grid>
                <Grid item xs={6} md={3}><StatCard label="Rating"        value={`${vendorData?.rating||0} ⭐`}    icon={<Assessment />}   bgcolor="warning.main" /></Grid>
              </Grid>
              <Grid container spacing={3}>
                <Grid item xs={12} md={6}>
                  <Paper sx={{ p:3 }}>
                    <Typography variant="h6" fontWeight={700} gutterBottom>Stock Insights</Typography>
                    {[
                      { label:'Total Products',  value:products.length,                                        color:'text.primary' },
                      { label:'In Stock',        value:products.filter(p=>(p.stock||0)>0).length,             color:'success.main' },
                      { label:'Low Stock (≤5)',  value:lowStock,                                               color:'warning.main' },
                      { label:'Out of Stock',    value:products.filter(p=>(p.stock||0)===0).length,           color:'error.main' },
                    ].map(({ label, value, color }) => (
                      <Box key={label} sx={{ display:'flex', justifyContent:'space-between', py:1.5, borderBottom:'1px solid', borderColor:'divider' }}>
                        <Typography color="text.secondary">{label}</Typography>
                        <Typography fontWeight={700} color={color}>{value}</Typography>
                      </Box>
                    ))}
                  </Paper>
                </Grid>
                <Grid item xs={12} md={6}>
                  <Paper sx={{ p:3 }}>
                    <Typography variant="h6" fontWeight={700} gutterBottom>Products by Category</Typography>
                    {['Clothing','Electronic','Foods','Books'].map(cat => {
                      const count = products.filter(p=>p.category===cat).length;
                      const pct   = products.length ? Math.round(count/products.length*100) : 0;
                      return (
                        <Box key={cat} sx={{ mb:2 }}>
                          <Box sx={{ display:'flex', justifyContent:'space-between', mb:0.5 }}>
                            <Typography variant="body2">{cat}</Typography>
                            <Typography variant="body2" color="text.secondary">{count} ({pct}%)</Typography>
                          </Box>
                          <LinearProgress variant="determinate" value={pct} sx={{ height:6, borderRadius:3 }} />
                        </Box>
                      );
                    })}
                  </Paper>
                </Grid>
                <Grid item xs={12}>
                  <Paper sx={{ p:3 }}>
                    <Typography variant="h6" fontWeight={700} gutterBottom>Order Status Breakdown</Typography>
                    {['Pending','Preparing','Ready','Delivered','Cancelled'].map(s => {
                      const count = orders.filter(o=>o.status===s).length;
                      const pct   = orders.length ? Math.round(count/orders.length*100) : 0;
                      return (
                        <Box key={s} sx={{ mb:2 }}>
                          <Box sx={{ display:'flex', justifyContent:'space-between', mb:0.5 }}>
                            <Typography variant="body2">{s}</Typography>
                            <Typography variant="body2" color="text.secondary">{count} ({pct}%)</Typography>
                          </Box>
                          <LinearProgress variant="determinate" value={pct} sx={{ height:6, borderRadius:3 }} />
                        </Box>
                      );
                    })}
                  </Paper>
                </Grid>
                <Grid item xs={12}>
                  <Paper sx={{ p:3 }}>
                    <Typography variant="h6" fontWeight={700} gutterBottom>Top Products by Orders</Typography>
                    <TableContainer>
                      <Table size="small">
                        <TableHead>
                          <TableRow>
                            <TableCell>Product</TableCell><TableCell>Category</TableCell>
                            <TableCell align="right">Price (NPR)</TableCell><TableCell align="right">Orders</TableCell>
                            <TableCell align="right">Est. Revenue</TableCell><TableCell align="center">Rating</TableCell>
                          </TableRow>
                        </TableHead>
                        <TableBody>
                          {[...products].sort((a,b)=>(b.orders||0)-(a.orders||0)).slice(0,10).map(p => (
                            <TableRow key={p.id} hover>
                              <TableCell sx={{ fontWeight:600 }}>{p.name}</TableCell>
                              <TableCell><Chip label={p.category} size="small" variant="outlined" /></TableCell>
                              <TableCell align="right">{(p.price||0).toFixed(2)}</TableCell>
                              <TableCell align="right">{p.orders||0}</TableCell>
                              <TableCell align="right">{((p.orders||0)*(p.price||0)).toFixed(2)}</TableCell>
                              <TableCell align="center">⭐ {p.rating||'—'}</TableCell>
                            </TableRow>
                          ))}
                          {products.length===0 && (
                            <TableRow><TableCell colSpan={6} align="center">
                              <Typography color="text.secondary" sx={{ py:2 }}>No products yet</Typography>
                            </TableCell></TableRow>
                          )}
                        </TableBody>
                      </Table>
                    </TableContainer>
                  </Paper>
                </Grid>
              </Grid>
            </>
          )}

          {/* ══ PROMOTIONS ═════════════════════════════════════════ */}
          {currentView === 'promotions' && (
            <>
              <Box sx={{ display:'flex', justifyContent:'space-between', alignItems:'center', mb:3 }}>
                <Typography variant="h4" fontWeight={700}>Promotions</Typography>
                <Button variant="contained" startIcon={<Add />} onClick={() => setPromoDialogOpen(true)}>Create Promotion</Button>
              </Box>
              {promotions.length === 0 ? (
                <Paper sx={{ p:5, textAlign:'center' }}>
                  <LocalOffer sx={{ fontSize:64, color:'grey.300', mb:2 }} />
                  <Typography variant="h6" color="text.secondary" gutterBottom>No promotions yet</Typography>
                  <Button variant="contained" onClick={() => setPromoDialogOpen(true)}>Create First Promotion</Button>
                </Paper>
              ) : (
                <Grid container spacing={3}>
                  {promotions.map(promo => (
                    <Grid item xs={12} sm={6} md={4} key={promo.id}>
                      <Card sx={{ border:'2px solid', borderColor:promo.active?'success.light':'grey.200', '&:hover':{ boxShadow:4 }, transition:'all 0.2s' }}>
                        <CardContent>
                          <Box sx={{ display:'flex', justifyContent:'space-between', mb:1 }}>
                            <Typography variant="h6" fontWeight={700}>{promo.name}</Typography>
                            <Chip label={promo.active?'Active':'Inactive'} color={promo.active?'success':'default'} size="small" />
                          </Box>
                          <Typography variant="h3" color="primary.main" fontWeight={800} sx={{ mb:0.5 }}>{promo.discount}%</Typography>
                          <Typography variant="body2" color="text.secondary">OFF</Typography>
                          <Divider sx={{ my:2 }} />
                          <Box sx={{ display:'flex', justifyContent:'space-between', mb:0.5 }}>
                            <Typography variant="body2" color="text.secondary">Code</Typography>
                            <Chip label={promo.code} size="small" variant="outlined" sx={{ fontFamily:'monospace', fontWeight:700 }} />
                          </Box>
                          <Box sx={{ display:'flex', justifyContent:'space-between' }}>
                            <Typography variant="body2" color="text.secondary">Duration</Typography>
                            <Typography variant="body2" fontWeight={600}>{promo.duration} days</Typography>
                          </Box>
                          <Box sx={{ mt:2, display:'flex', gap:1 }}>
                            <Button variant={promo.active?'outlined':'contained'} size="small" fullWidth onClick={() => handleTogglePromo(promo.id)}>
                              {promo.active?'Deactivate':'Activate'}
                            </Button>
                            <IconButton size="small" color="error" onClick={() => handleDeletePromo(promo.id)}><Delete fontSize="small" /></IconButton>
                          </Box>
                        </CardContent>
                      </Card>
                    </Grid>
                  ))}
                </Grid>
              )}
            </>
          )}

        </Container>
      </Box>

      {/* ══ PRODUCT DIALOG ══════════════════════════════════════ */}
      <Dialog open={showProductForm} onClose={resetProductForm} maxWidth="md" fullWidth>
        <DialogTitle sx={{ bgcolor:'primary.main', color:'white', fontWeight:700 }}>
          {editingProduct ? `Edit: ${editingProduct.name}` : 'Add New Product'}
        </DialogTitle>
        <DialogContent sx={{ p:3, mt:1 }}>
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              <TextField fullWidth label="Product Name *" value={productForm.name} onChange={e => setProductForm(p=>({...p, name:e.target.value}))} />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField fullWidth label="Price (NPR) *" type="number" value={productForm.price}
                onChange={e => setProductForm(p=>({...p, price:e.target.value}))} inputProps={{ min:0, step:0.01 }} />
            </Grid>
            <Grid item xs={12} md={6}>
              <FormControl fullWidth>
                <InputLabel>Category *</InputLabel>
                <Select value={productForm.category} label="Category *" onChange={e => setProductForm(p=>({...p, category:e.target.value}))}>
                  {['Clothing','Electronic','Foods','Books'].map(c => <MenuItem key={c} value={c}>{c}</MenuItem>)}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField fullWidth label="Initial Stock" type="number" value={productForm.stock}
                onChange={e => setProductForm(p=>({...p, stock:parseInt(e.target.value)||0}))} inputProps={{ min:0 }} />
            </Grid>
            <Grid item xs={12}>
              <TextField fullWidth label="Description *" multiline rows={3} value={productForm.description}
                onChange={e => setProductForm(p=>({...p, description:e.target.value}))} />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField fullWidth label="Image URL (optional)" value={productForm.image}
                onChange={e => setProductForm(p=>({...p, image:e.target.value, photoFile:null}))} placeholder="https://..." />
            </Grid>
            <Grid item xs={12} md={6}>
              <Typography variant="body2" color="text.secondary" sx={{ mb:1 }}>Or Upload Photo</Typography>
              <input type="file" accept="image/*" onChange={handlePhotoChange}
                style={{ width:'100%', padding:10, border:'2px dashed #ccc', borderRadius:4 }} />
            </Grid>
            {productForm.image && (
              <Grid item xs={12}>
                <Box sx={{ display:'flex', alignItems:'center', gap:2 }}>
                  <Typography variant="body2" color="text.secondary">Preview:</Typography>
                  <Box component="img" src={productForm.image} alt="preview"
                    sx={{ width:80, height:80, objectFit:'cover', borderRadius:1, border:'1px solid #ddd' }}
                    onError={e => { e.target.style.display='none'; }} />
                </Box>
              </Grid>
            )}
          </Grid>
        </DialogContent>
        <DialogActions sx={{ p:2, bgcolor:'grey.50' }}>
          <Button onClick={resetProductForm} disabled={savingProduct}>Cancel</Button>
          <Button variant="contained" onClick={handleSaveProduct} disabled={savingProduct}
            startIcon={savingProduct ? <CircularProgress size={16} color="inherit" /> : null}>
            {savingProduct ? 'Saving…' : editingProduct ? 'Update Product' : 'Save Product'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* ══ PROMOTION DIALOG ════════════════════════════════════ */}
      <Dialog open={promoDialogOpen} onClose={() => setPromoDialogOpen(false)} maxWidth="sm" fullWidth>
        <DialogTitle sx={{ fontWeight:700 }}>Create Promotion</DialogTitle>
        <DialogContent sx={{ pt:2 }}>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField fullWidth label="Promotion Name" value={promoForm.name}
                onChange={e => setPromoForm(p=>({...p, name:e.target.value}))} />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField fullWidth label="Promo Code" value={promoForm.code}
                onChange={e => setPromoForm(p=>({...p, code:e.target.value.toUpperCase()}))} placeholder="e.g. SAVE20" />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField fullWidth label="Discount %" type="number" value={promoForm.discount}
                onChange={e => setPromoForm(p=>({...p, discount:e.target.value}))} inputProps={{ min:1, max:100 }} />
            </Grid>
            <Grid item xs={12}>
              <TextField fullWidth label="Duration (days)" type="number" value={promoForm.duration}
                onChange={e => setPromoForm(p=>({...p, duration:e.target.value}))} inputProps={{ min:1 }} />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions sx={{ p:2 }}>
          <Button onClick={() => setPromoDialogOpen(false)}>Cancel</Button>
          <Button variant="contained" onClick={handleCreatePromo}>Create</Button>
        </DialogActions>
      </Dialog>

      {/* ══ TOAST ═══════════════════════════════════════════════ */}
      <Snackbar open={toast.open} autoHideDuration={3000} onClose={closeToast}
        anchorOrigin={{ vertical:'bottom', horizontal:'center' }}>
        <Alert severity={toast.severity} onClose={closeToast} sx={{ borderRadius:2 }}>
          {toast.msg}
        </Alert>
      </Snackbar>

    </Box>
  );
};

export default SimpleVendorDashboard;
