import React, { useState, useEffect, useMemo } from 'react';
import {
  Container, Grid, Card, CardContent, Typography, Button, Box, Chip,
  CircularProgress, Alert, Dialog, DialogTitle, DialogContent, DialogActions,
  TextField, MenuItem, IconButton, useTheme, Paper, Divider, LinearProgress,
  Tooltip, Collapse,
} from '@mui/material';
import {
  Restaurant, CalendarToday, LocalFireDepartment, AttachMoney, AutoAwesome,
  Add, Edit, Delete, Close, ChevronLeft, ChevronRight, ExpandMore, ExpandLess,
  CheckCircleOutline, FoodBank,
} from '@mui/icons-material';
import { useAuth } from '../contexts/AuthContext';
import { mealPlanAPI } from '../services/api';
import { format, addDays, startOfWeek, isSameDay, parseISO } from 'date-fns';

// â”€â”€ Constants â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const MEAL_TYPES = ['BREAKFAST', 'LUNCH', 'DINNER', 'SNACK'];

const MEAL_META = {
  BREAKFAST: { icon: 'ðŸŒ…', color: 'warning',   label: 'Breakfast', time: '7â€“9 AM'   },
  LUNCH:     { icon: 'â˜€ï¸', color: 'info',      label: 'Lunch',     time: '12â€“2 PM'  },
  DINNER:    { icon: 'ðŸŒ™', color: 'secondary', label: 'Dinner',    time: '7â€“9 PM'   },
  SNACK:     { icon: 'ðŸ¿', color: 'success',   label: 'Snack',     time: 'Anytime'  },
};

const DAILY_GOALS = { calories: 2000, cost: 500 };

// â”€â”€ Helpers â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const dateStr = (d) => format(d, 'yyyy-MM-dd');
const displayDate = (d) => format(d, 'EEE, MMM d');

// â”€â”€ Weekly Calendar Strip â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const WeekStrip = ({ selectedDate, onSelectDate, mealPlans }) => {
  const [weekStart, setWeekStart] = useState(startOfWeek(new Date(), { weekStartsOn: 1 }));
  const days = Array.from({ length: 7 }, (_, i) => addDays(weekStart, i));

  const hasPlan = (day) => mealPlans.some(p => p.planDate === dateStr(day) || isSameDay(parseISO(p.planDate || selectedDate), day));

  return (
    <Paper sx={{ p: 2, mb: 3, borderRadius: 3 }}>
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 1.5 }}>
        <IconButton size="small" onClick={() => setWeekStart(d => addDays(d, -7))}><ChevronLeft /></IconButton>
        <Typography variant="subtitle2" fontWeight={700} color="text.secondary">
          {format(weekStart, 'MMM d')} â€” {format(addDays(weekStart, 6), 'MMM d, yyyy')}
        </Typography>
        <IconButton size="small" onClick={() => setWeekStart(d => addDays(d, 7))}><ChevronRight /></IconButton>
      </Box>
      <Box sx={{ display: 'flex', gap: 1, justifyContent: 'space-between' }}>
        {days.map(day => {
          const selected = dateStr(day) === selectedDate;
          const today = isSameDay(day, new Date());
          const planned = hasPlan(day);
          return (
            <Box
              key={dateStr(day)}
              onClick={() => onSelectDate(dateStr(day))}
              sx={{
                flex: 1, textAlign: 'center', cursor: 'pointer', borderRadius: 2, py: 1, px: 0.5,
                bgcolor: selected ? 'primary.main' : today ? 'primary.50' : 'transparent',
                border: today && !selected ? '1px solid' : 'none',
                borderColor: 'primary.light',
                transition: 'all 0.15s',
                '&:hover': { bgcolor: selected ? 'primary.dark' : 'action.hover' },
              }}
            >
              <Typography variant="caption" sx={{ color: selected ? 'white' : 'text.secondary', fontWeight: 600, display: 'block' }}>
                {format(day, 'EEE')}
              </Typography>
              <Typography variant="body2" sx={{ color: selected ? 'white' : today ? 'primary.main' : 'text.primary', fontWeight: 700 }}>
                {format(day, 'd')}
              </Typography>
              {planned && (
                <Box sx={{ width: 5, height: 5, borderRadius: '50%', bgcolor: selected ? 'white' : 'success.main', mx: 'auto', mt: 0.5 }} />
              )}
            </Box>
          );
        })}
      </Box>
    </Paper>
  );
};

// â”€â”€ Nutrition Summary Bar â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const NutritionSummary = ({ plans }) => {
  const totalCal = plans.reduce((s, p) => s + (p.calories || 0), 0);
  const totalCost = plans.reduce((s, p) => s + (p.estimatedCost || 0), 0);
  const calPct = Math.min(100, Math.round((totalCal / DAILY_GOALS.calories) * 100));
  const costPct = Math.min(100, Math.round((totalCost / DAILY_GOALS.cost) * 100));

  return (
    <Paper sx={{ p: 2.5, mb: 3, borderRadius: 3 }}>
      <Typography variant="subtitle1" fontWeight={700} gutterBottom>Today's Summary</Typography>
      <Grid container spacing={3}>
        <Grid item xs={6}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 0.5 }}>
            <Typography variant="body2" color="text.secondary">ðŸ”¥ Calories</Typography>
            <Typography variant="body2" fontWeight={700}>{totalCal} / {DAILY_GOALS.calories} kcal</Typography>
          </Box>
          <LinearProgress variant="determinate" value={calPct} color={calPct > 100 ? 'error' : calPct > 75 ? 'warning' : 'success'} sx={{ height: 8, borderRadius: 4 }} />
          <Typography variant="caption" color="text.secondary">{calPct}% of daily goal</Typography>
        </Grid>
        <Grid item xs={6}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 0.5 }}>
            <Typography variant="body2" color="text.secondary">ðŸ’° Est. Cost</Typography>
            <Typography variant="body2" fontWeight={700}>NPR {totalCost.toFixed(0)} / {DAILY_GOALS.cost}</Typography>
          </Box>
          <LinearProgress variant="determinate" value={costPct} color={costPct > 100 ? 'error' : 'primary'} sx={{ height: 8, borderRadius: 4 }} />
          <Typography variant="caption" color="text.secondary">{costPct}% of daily budget</Typography>
        </Grid>
        <Grid item xs={6} sm={3}>
          <Paper variant="outlined" sx={{ p: 1.5, textAlign: 'center', borderRadius: 2 }}>
            <Typography variant="h6" fontWeight={800} color="warning.main">{plans.length}</Typography>
            <Typography variant="caption" color="text.secondary">Planned Meals</Typography>
          </Paper>
        </Grid>
        <Grid item xs={6} sm={3}>
          <Paper variant="outlined" sx={{ p: 1.5, textAlign: 'center', borderRadius: 2 }}>
            <Typography variant="h6" fontWeight={800} color="success.main">
              {MEAL_TYPES.length - plans.length < 0 ? 0 : MEAL_TYPES.length - plans.length}
            </Typography>
            <Typography variant="caption" color="text.secondary">Remaining</Typography>
          </Paper>
        </Grid>
        <Grid item xs={6} sm={3}>
          <Paper variant="outlined" sx={{ p: 1.5, textAlign: 'center', borderRadius: 2 }}>
            <Typography variant="h6" fontWeight={800} color="info.main">
              {plans.filter(p => p.isRecommended).length}
            </Typography>
            <Typography variant="caption" color="text.secondary">AI-Picked</Typography>
          </Paper>
        </Grid>
        <Grid item xs={6} sm={3}>
          <Paper variant="outlined" sx={{ p: 1.5, textAlign: 'center', borderRadius: 2 }}>
            <Typography variant="h6" fontWeight={800} color="primary.main">
              {Math.max(0, DAILY_GOALS.calories - totalCal)}
            </Typography>
            <Typography variant="caption" color="text.secondary">Cal Left</Typography>
          </Paper>
        </Grid>
      </Grid>
    </Paper>
  );
};

// â”€â”€ Meal Type Section â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const MealSection = ({ mealType, plans, onEdit, onDelete, onAdd }) => {
  const { icon, color, label, time } = MEAL_META[mealType];
  const sectionPlans = plans.filter(p => p.mealType === mealType);
  const [expanded, setExpanded] = useState(true);

  return (
    <Paper sx={{ mb: 2, borderRadius: 3, overflow: 'hidden' }}>
      {/* Header */}
      <Box
        onClick={() => setExpanded(e => !e)}
        sx={{
          px: 3, py: 1.5, display: 'flex', alignItems: 'center', justifyContent: 'space-between',
          cursor: 'pointer', bgcolor: `${color}.50`,
          '&:hover': { bgcolor: `${color}.100` }, transition: 'background 0.15s',
        }}
      >
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5 }}>
          <Typography sx={{ fontSize: 24 }}>{icon}</Typography>
          <Box>
            <Typography variant="subtitle1" fontWeight={700}>{label}</Typography>
            <Typography variant="caption" color="text.secondary">{time}</Typography>
          </Box>
          {sectionPlans.length > 0 && (
            <Chip label={sectionPlans.length} size="small" color={color} sx={{ fontWeight: 700 }} />
          )}
        </Box>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <Tooltip title={`Add ${label}`}>
            <IconButton
              size="small"
              color={color}
              onClick={e => { e.stopPropagation(); onAdd(mealType); }}
              sx={{ bgcolor: 'white', '&:hover': { bgcolor: 'white', transform: 'scale(1.1)' } }}
            >
              <Add fontSize="small" />
            </IconButton>
          </Tooltip>
          {expanded ? <ExpandLess /> : <ExpandMore />}
        </Box>
      </Box>

      <Collapse in={expanded}>
        {sectionPlans.length === 0 ? (
          <Box sx={{ py: 3, textAlign: 'center' }}>
            <Typography variant="body2" color="text.secondary" gutterBottom>No {label.toLowerCase()} planned</Typography>
            <Button size="small" startIcon={<Add />} onClick={() => onAdd(mealType)} sx={{ mt: 0.5 }}>
              Add {label}
            </Button>
          </Box>
        ) : (
          <Grid container spacing={2} sx={{ p: 2 }}>
            {sectionPlans.map(plan => (
              <Grid item xs={12} sm={6} md={4} key={plan.id}>
                <MealCard plan={plan} onEdit={onEdit} onDelete={onDelete} />
              </Grid>
            ))}
          </Grid>
        )}
      </Collapse>
    </Paper>
  );
};

// â”€â”€ Meal Card â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const MealCard = ({ plan, onEdit, onDelete }) => {
  const { color } = MEAL_META[plan.mealType] || {};
  const [showDetails, setShowDetails] = useState(false);

  return (
    <Card
      sx={{
        height: '100%', borderRadius: 2,
        border: plan.isRecommended ? '2px solid' : '1px solid',
        borderColor: plan.isRecommended ? 'success.main' : 'divider',
        transition: 'box-shadow 0.2s',
        '&:hover': { boxShadow: 4 },
      }}
    >
      <CardContent sx={{ p: 2, '&:last-child': { pb: 2 } }}>
        {/* Title row */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 1 }}>
          <Typography variant="subtitle2" fontWeight={700} sx={{ flexGrow: 1, pr: 1 }}>
            {plan.planName}
          </Typography>
          {plan.isRecommended && (
            <Tooltip title="AI Recommended">
              <AutoAwesome sx={{ fontSize: 16, color: 'success.main', flexShrink: 0 }} />
            </Tooltip>
          )}
        </Box>

        {/* Chips */}
        <Box sx={{ display: 'flex', gap: 0.5, flexWrap: 'wrap', mb: 1.5 }}>
          <Chip icon={<LocalFireDepartment sx={{ fontSize: 12 }} />} label={`${plan.calories || 0} cal`} size="small" color="warning" variant="outlined" />
          <Chip icon={<AttachMoney sx={{ fontSize: 12 }} />} label={`NPR ${(plan.estimatedCost || 0).toFixed(0)}`} size="small" color="success" variant="outlined" />
        </Box>

        {/* Toggle details */}
        <Button
          size="small"
          variant="text"
          onClick={() => setShowDetails(s => !s)}
          endIcon={showDetails ? <ExpandLess /> : <ExpandMore />}
          sx={{ fontSize: 11, color: 'text.secondary', p: 0, minWidth: 0, mb: 0.5 }}
        >
          {showDetails ? 'Hide' : 'Details'}
        </Button>

        <Collapse in={showDetails}>
          {plan.aiRecommendation && (
            <Alert severity="info" sx={{ mb: 1, py: 0.5, fontSize: 12 }} icon={<AutoAwesome sx={{ fontSize: 16 }} />}>
              {plan.aiRecommendation}
            </Alert>
          )}
          <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mb: 0.5 }}>
            <strong>Ingredients:</strong> {plan.ingredients || 'â€”'}
          </Typography>
          <Typography variant="caption" color="text.secondary" sx={{ display: 'block' }}>
            <strong>Instructions:</strong> {plan.instructions || 'â€”'}
          </Typography>
        </Collapse>

        {/* Actions */}
        <Box sx={{ display: 'flex', gap: 0.5, mt: 1.5 }}>
          <Button size="small" startIcon={<Edit sx={{ fontSize: 14 }} />} onClick={() => onEdit(plan)} sx={{ fontSize: 12 }}>Edit</Button>
          <Button size="small" color="error" startIcon={<Delete sx={{ fontSize: 14 }} />} onClick={() => onDelete(plan.id)} sx={{ fontSize: 12 }}>Delete</Button>
        </Box>
      </CardContent>
    </Card>
  );
};

// â”€â”€ Main Component â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const MealPlanner = () => {
  const { user } = useAuth();
  const theme = useTheme();

  const [mealPlans, setMealPlans] = useState([]);
  const [allPlans, setAllPlans] = useState([]); // for week strip dots
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [selectedDate, setSelectedDate] = useState(dateStr(new Date()));
  const [openDialog, setOpenDialog] = useState(false);
  const [editingPlan, setEditingPlan] = useState(null);
  const [aiRecommendation, setAiRecommendation] = useState(null);
  const [aiLoading, setAiLoading] = useState(false);

  const [formData, setFormData] = useState({
    planName: '', mealType: 'BREAKFAST', ingredients: '', instructions: '',
  });

  // â”€â”€ Fetch â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  useEffect(() => {
    if (!user) { setError('Please log in to view meal plans'); setLoading(false); return; }
    fetchMealPlans();
  }, [selectedDate, user]);

  const fetchMealPlans = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await mealPlanAPI.getPlansByDate(selectedDate);
      if (response.data?.success) {
        setMealPlans(response.data.data || []);
      } else {
        setError(response.data?.message || 'Failed to load meal plans');
      }
    } catch (err) {
      if (err.response) {
        setError(err.response.data?.message || `Server error (${err.response.status})`);
      } else if (err.request) {
        setError('Cannot reach the server. Check if backend is running.');
      } else {
        setError('Unexpected error: ' + err.message);
      }
    } finally {
      setLoading(false);
    }
  };

  // â”€â”€ AI Recommendation â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  const handleGenerateAI = async () => {
    if ((formData.planName || formData.ingredients || formData.instructions) &&
      !window.confirm('Generate AI suggestion? Current fields will be overwritten.')) return;
    try {
      setAiLoading(true);
      const response = await mealPlanAPI.getAIRecommendation(formData.mealType, selectedDate);
      if (response.data?.success) {
        const rec = response.data.data;
        setAiRecommendation(rec);
        setFormData(prev => ({
          ...prev,
          planName: rec.suggestedName || `AI ${prev.mealType} Plan`,
          ingredients: rec.ingredients || prev.ingredients,
          instructions: rec.instructions || prev.instructions,
        }));
        setSuccess('AI recommendation applied!');
      }
    } catch {
      setError('Failed to generate AI recommendation');
    } finally {
      setAiLoading(false);
    }
  };

  // â”€â”€ Save â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  const isFormValid = () => formData.planName.trim() && formData.mealType && formData.ingredients.trim() && formData.instructions.trim();

  const handleSave = async () => {
    if (!isFormValid()) { setError('Please fill in all required fields'); return; }
    try {
      setSaving(true);
      const planData = {
        ...formData,
        planDate: selectedDate,
        calories: aiRecommendation?.calories || 400,
        estimatedCost: aiRecommendation?.estimatedCost || 150,
        aiRecommendation: aiRecommendation?.aiRecommendation || '',
        isRecommended: !!aiRecommendation,
      };
      if (editingPlan) {
        await mealPlanAPI.update(editingPlan.id, planData);
        setSuccess('Meal plan updated!');
      } else {
        await mealPlanAPI.create(planData);
        setSuccess('Meal plan created!');
      }
      closeDialog();
      fetchMealPlans();
    } catch {
      setError('Failed to save meal plan');
    } finally {
      setSaving(false);
    }
  };

  // â”€â”€ Edit / Delete â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  const handleEdit = (plan) => {
    setEditingPlan(plan);
    setFormData({ planName: plan.planName || '', mealType: plan.mealType || 'BREAKFAST', ingredients: plan.ingredients || '', instructions: plan.instructions || '' });
    setAiRecommendation(null);
    setOpenDialog(true);
  };

  const handleDelete = async (planId) => {
    if (!window.confirm('Delete this meal plan?')) return;
    try {
      await mealPlanAPI.delete(planId);
      setSuccess('Meal plan deleted.');
      fetchMealPlans();
    } catch {
      setError('Failed to delete meal plan');
    }
  };

  const handleAddForType = (mealType) => {
    setEditingPlan(null);
    setAiRecommendation(null);
    setFormData({ planName: '', mealType, ingredients: '', instructions: '' });
    setOpenDialog(true);
  };

  const closeDialog = () => {
    setOpenDialog(false);
    setEditingPlan(null);
    setAiRecommendation(null);
    setFormData({ planName: '', mealType: 'BREAKFAST', ingredients: '', instructions: '' });
  };

  // â”€â”€ Render â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  return (
    <Box sx={{ bgcolor: 'grey.50', minHeight: '100vh' }}>

      {/* Hero Header */}
      <Box sx={{
        background: `linear-gradient(135deg, ${theme.palette.primary.dark} 0%, ${theme.palette.secondary.main} 100%)`,
        color: 'white', py: 5, px: 2, textAlign: 'center',
      }}>
        <Typography variant="overline" sx={{ letterSpacing: 4, opacity: 0.8 }}>Smart Planning</Typography>
        <Typography variant="h3" fontWeight={900} sx={{ mb: 1 }}>ðŸ½ï¸ Meal Planner</Typography>
        <Typography variant="h6" sx={{ opacity: 0.88, fontWeight: 400 }}>Plan, track, and optimise your meals with AI.</Typography>
      </Box>

      <Container maxWidth="lg" sx={{ py: 4 }}>

        {/* Alerts */}
        {error && <Alert severity="error" sx={{ mb: 2, borderRadius: 2 }} onClose={() => setError(null)}>{error}</Alert>}
        {success && <Alert severity="success" sx={{ mb: 2, borderRadius: 2 }} onClose={() => setSuccess(null)}>{success}</Alert>}

        {/* Date Controls */}
        <Box sx={{ display: 'flex', gap: 2, mb: 3, alignItems: 'center', flexWrap: 'wrap', justifyContent: 'space-between' }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5 }}>
            <IconButton onClick={() => setSelectedDate(d => dateStr(addDays(parseISO(d), -1)))} size="small">
              <ChevronLeft />
            </IconButton>
            <TextField
              type="date"
              size="small"
              value={selectedDate}
              onChange={e => setSelectedDate(e.target.value)}
              InputLabelProps={{ shrink: true }}
              sx={{ width: 160 }}
            />
            <IconButton onClick={() => setSelectedDate(d => dateStr(addDays(parseISO(d), 1)))} size="small">
              <ChevronRight />
            </IconButton>
            <Button
              size="small"
              variant="outlined"
              onClick={() => setSelectedDate(dateStr(new Date()))}
              sx={{ borderRadius: 2 }}
            >
              Today
            </Button>
            <Typography variant="subtitle2" color="text.secondary" sx={{ ml: 1 }}>
              {displayDate(parseISO(selectedDate))}
            </Typography>
          </Box>
          <Button
            variant="contained"
            startIcon={<Add />}
            onClick={() => { setEditingPlan(null); setAiRecommendation(null); setFormData({ planName: '', mealType: 'BREAKFAST', ingredients: '', instructions: '' }); setOpenDialog(true); }}
            sx={{ borderRadius: 2 }}
          >
            Add Meal Plan
          </Button>
        </Box>

        {/* Weekly Calendar */}
        <WeekStrip selectedDate={selectedDate} onSelectDate={setSelectedDate} mealPlans={allPlans} />

        {/* Nutrition Summary */}
        {!loading && mealPlans.length > 0 && <NutritionSummary plans={mealPlans} />}

        {/* Loading */}
        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 8 }}>
            <CircularProgress size={50} />
          </Box>
        ) : mealPlans.length === 0 ? (
          /* Empty State */
          <Paper sx={{ p: 6, textAlign: 'center', borderRadius: 3 }}>
            <FoodBank sx={{ fontSize: 72, color: 'grey.300', mb: 2 }} />
            <Typography variant="h6" gutterBottom>No meals planned for {displayDate(parseISO(selectedDate))}</Typography>
            <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
              Start by adding a meal or let AI suggest one for you.
            </Typography>
            <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', flexWrap: 'wrap' }}>
              <Button variant="contained" startIcon={<Add />} onClick={() => { setFormData(f => ({ ...f, mealType: 'BREAKFAST' })); setOpenDialog(true); }}>
                Add Meal
              </Button>
              <Button variant="outlined" startIcon={<AutoAwesome />} onClick={() => { setFormData(f => ({ ...f, mealType: 'BREAKFAST' })); setOpenDialog(true); setTimeout(handleGenerateAI, 200); }}>
                AI Suggest
              </Button>
            </Box>
          </Paper>
        ) : (
          /* Meal Type Sections */
          <Box>
            {MEAL_TYPES.map(type => (
              <MealSection
                key={type}
                mealType={type}
                plans={mealPlans}
                onEdit={handleEdit}
                onDelete={handleDelete}
                onAdd={handleAddForType}
              />
            ))}
          </Box>
        )}

      </Container>

      {/* â”€â”€ Add / Edit Dialog â”€â”€ */}
      <Dialog open={openDialog} onClose={closeDialog} maxWidth="md" fullWidth PaperProps={{ sx: { borderRadius: 3 } }}>
        <DialogTitle sx={{ fontWeight: 700, pr: 6 }}>
          {editingPlan ? `Edit: ${editingPlan.planName}` : 'Create Meal Plan'}
          <IconButton onClick={closeDialog} sx={{ position: 'absolute', right: 8, top: 8 }}>
            <Close />
          </IconButton>
        </DialogTitle>

        <DialogContent sx={{ pt: 1 }}>
          <Grid container spacing={2}>
            {/* Meal Type */}
            <Grid item xs={12} sm={6}>
              <TextField
                select fullWidth label="Meal Type *" size="small"
                value={formData.mealType}
                onChange={e => setFormData(p => ({ ...p, mealType: e.target.value }))}
              >
                {MEAL_TYPES.map(type => (
                  <MenuItem key={type} value={type}>
                    {MEAL_META[type].icon} {MEAL_META[type].label}
                  </MenuItem>
                ))}
              </TextField>
            </Grid>

            {/* Plan Name */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth label="Plan Name *" size="small"
                value={formData.planName}
                onChange={e => setFormData(p => ({ ...p, planName: e.target.value }))}
                placeholder="e.g. Oatmeal & Fruits"
              />
            </Grid>

            {/* Ingredients */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth label="Ingredients *" multiline rows={4} size="small"
                value={formData.ingredients}
                onChange={e => setFormData(p => ({ ...p, ingredients: e.target.value }))}
                placeholder="e.g. Oats, banana, honey..."
              />
            </Grid>

            {/* Instructions */}
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth label="Instructions *" multiline rows={4} size="small"
                value={formData.instructions}
                onChange={e => setFormData(p => ({ ...p, instructions: e.target.value }))}
                placeholder="e.g. Boil oats, slice banana..."
              />
            </Grid>

            {/* AI Button */}
            <Grid item xs={12}>
              <Button
                variant="outlined"
                startIcon={aiLoading ? <CircularProgress size={16} /> : <AutoAwesome />}
                onClick={handleGenerateAI}
                disabled={aiLoading}
                fullWidth
                sx={{ borderRadius: 2, borderStyle: 'dashed' }}
              >
                {aiLoading ? 'Generatingâ€¦' : 'âœ¨ Generate AI Recommendation'}
              </Button>
            </Grid>

            {/* AI Result */}
            {aiRecommendation && (
              <Grid item xs={12}>
                <Alert severity="success" icon={<CheckCircleOutline />} sx={{ borderRadius: 2 }}>
                  <Typography variant="body2" fontWeight={700} gutterBottom>AI Suggestion Applied</Typography>
                  <Typography variant="body2">{aiRecommendation.aiRecommendation}</Typography>
                  <Box sx={{ display: 'flex', gap: 1, mt: 1, flexWrap: 'wrap' }}>
                    <Chip size="small" icon={<LocalFireDepartment />} label={`${aiRecommendation.calories} kcal`} color="warning" />
                    <Chip size="small" icon={<AttachMoney />} label={`NPR ${(aiRecommendation.estimatedCost || 0).toFixed(0)}`} color="success" />
                  </Box>
                </Alert>
              </Grid>
            )}
          </Grid>
        </DialogContent>

        <DialogActions sx={{ px: 3, pb: 3 }}>
          <Button onClick={closeDialog} disabled={saving}>Cancel</Button>
          <Button
            variant="contained"
            onClick={handleSave}
            disabled={!isFormValid() || saving}
            startIcon={saving ? <CircularProgress size={16} color="inherit" /> : null}
            sx={{ borderRadius: 2, px: 3 }}
          >
            {saving ? 'Savingâ€¦' : editingPlan ? 'Update' : 'Save Plan'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default MealPlanner;
