import React, { useState } from "react";

const IceWorldDashboard = () => {
  // --- User Stats ---
  const [userStats, setUserStats] = useState({
    totalOrders: 0,
    totalSpent: 0.0,
    favoriteItems: 0,
    points: 0,
    memberSince: new Date().toLocaleDateString("en-US", { year: "numeric", month: "long" }),
    lastOrder: "No orders yet",
    avgOrderValue: 0.0,
  });

  // --- Favorites ---
  const [favoriteItems, setFavoriteItems] = useState([]);

  // --- Vendor Products ---
  const [vendorProducts, setVendorProducts] = useState([
    {
      id: 1,
      name: "Vanilla Ice Cream",
      vendor: "IceCream Co.",
      price: 250,
      rating: 4,
      description: "Classic vanilla flavor",
      image: "vanilla.jpg",
      totalRatings: 1,
    },
    {
      id: 2,
      name: "Chocolate Ice Cream",
      vendor: "ChocoDelight",
      price: 300,
      rating: 5,
      description: "Rich chocolate flavor",
      image: "chocolate.jpg",
      totalRatings: 1,
    },
    // Add more products as needed
  ]);

  // --- Add to Favorites ---
  const handleAddToFavorites = (product) => {
    if (favoriteItems.some((item) => item.id === product.id)) {
      alert("This item is already in your favorites!");
      return;
    }

    const updatedFavorites = [
      ...favoriteItems,
      {
        id: product.id,
        name: product.name,
        vendor: product.vendor,
        price: product.price,
        rating: product.rating,
        description: product.description,
        image: product.image,
      },
    ];

    setFavoriteItems(updatedFavorites);

    setUserStats((prev) => ({
      ...prev,
      favoriteItems: updatedFavorites.length,
    }));

    alert(`"${product.name}" added to favorites!`);
  };

  // --- Remove from Favorites ---
  const handleRemoveFromFavorites = (itemId) => {
    const item = favoriteItems.find((item) => item.id === itemId);
    if (!item) return;
    if (!window.confirm(`Remove "${item.name}" from your favorites?`)) return;

    const updatedFavorites = favoriteItems.filter((item) => item.id !== itemId);
    setFavoriteItems(updatedFavorites);

    setUserStats((prev) => ({
      ...prev,
      favoriteItems: updatedFavorites.length,
    }));

    alert(`"${item.name}" removed from favorites!`);
  };

  // --- Place Order ---
  const handlePlaceOrder = (product) => {
    const quantity = prompt("Enter quantity:", "1");
    if (!quantity || isNaN(quantity) || parseInt(quantity) <= 0) {
      alert("Please enter a valid quantity");
      return;
    }

    const orderQuantity = parseInt(quantity);
    const totalCost = product.price * orderQuantity;

    setUserStats((prev) => {
      const newTotalOrders = prev.totalOrders + 1;
      const newTotalSpent = prev.totalSpent + totalCost;
      const newPoints = prev.points + Math.floor(totalCost);
      const avgOrderValue = newTotalSpent / newTotalOrders;
      const lastOrder = new Date().toLocaleDateString("en-US", {
        year: "numeric",
        month: "short",
        day: "numeric",
      });

      return {
        ...prev,
        totalOrders: newTotalOrders,
        totalSpent: newTotalSpent,
        points: newPoints,
        avgOrderValue,
        lastOrder,
      };
    });

    alert(`Order placed: ${orderQuantity} x "${product.name}" = NPR ${totalCost.toFixed(2)}`);
  };

  // --- Rate Product ---
  const handleRateProduct = (productId, rating) => {
    setVendorProducts((prev) =>
      prev.map((p) =>
        p.id === productId
          ? {
              ...p,
              rating,
              totalRatings: (p.totalRatings || 0) + 1,
            }
          : p
      )
    );

    const product = vendorProducts.find((p) => p.id === productId);
    alert(`You rated "${product?.name}" ${rating} stars!`);
  };

  return (
    <div>
      <h1>Ice World Dashboard</h1>

      {/* --- User Stats --- */}
      <div>
        <h2>User Stats</h2>
        <p>Total Orders: {userStats.totalOrders}</p>
        <p>Total Spent: NPR {userStats.totalSpent.toFixed(2)}</p>
        <p>Favorite Items: {userStats.favoriteItems}</p>
        <p>Points: {userStats.points}</p>
        <p>Member Since: {userStats.memberSince}</p>
        <p>Last Order: {userStats.lastOrder}</p>
        <p>Average Order Value: NPR {userStats.avgOrderValue.toFixed(2)}</p>
      </div>

      {/* --- Vendor Products --- */}
      <div>
        <h2>Vendor Products</h2>
        {vendorProducts.map((product) => (
          <div key={product.id} style={{ border: "1px solid #ccc", margin: "10px", padding: "10px" }}>
            <img src={product.image} alt={product.name} width={100} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>Price: NPR {product.price}</p>
            <p>Vendor: {product.vendor}</p>
            <p>Rating: {product.rating} ⭐</p>
            <button onClick={() => handleAddToFavorites(product)}>Add to Favorites</button>
            <button onClick={() => handlePlaceOrder(product)}>Place Order</button>
            <button onClick={() => handleRateProduct(product.id, 5)}>Rate 5⭐</button>
          </div>
        ))}
      </div>

      {/* --- Favorite Items --- */}
      <div>
        <h2>Favorite Items</h2>
        {favoriteItems.length === 0 ? (
          <p>No favorites yet.</p>
        ) : (
          favoriteItems.map((item) => (
            <div key={item.id} style={{ border: "1px dashed #666", margin: "5px", padding: "5px" }}>
              <h4>{item.name}</h4>
              <button onClick={() => handleRemoveFromFavorites(item.id)}>Remove from Favorites</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default IceWorldDashboard;
