import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  Button,
  Box,
  Chip,
  CircularProgress,
  Alert,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  MenuItem,
  IconButton,
  useTheme,
} from '@mui/material';
import {
  Restaurant,
  CalendarToday,
  LocalFireDepartment,
  AttachMoney,
  AutoAwesome,
  Add,
  Edit,
  Delete,
  Close,
} from '@mui/icons-material';
import { useAuth } from '../contexts/AuthContext';
import { mealPlanAPI } from '../services/api';
import { format } from 'date-fns';

const MealPlanner = () => {
  const { user } = useAuth();
  const theme = useTheme();

  const [mealPlans, setMealPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedDate, setSelectedDate] = useState(format(new Date(), 'yyyy-MM-dd'));
  const [openDialog, setOpenDialog] = useState(false);
  const [editingPlan, setEditingPlan] = useState(null);
  const [aiRecommendation, setAiRecommendation] = useState(null);

  const [formData, setFormData] = useState({
    planName: '',
    mealType: 'BREAKFAST',
    ingredients: '',
    instructions: '',
  });

  const mealTypes = ['BREAKFAST', 'LUNCH', 'DINNER', 'SNACK'];

  useEffect(() => {
    fetchMealPlans();
  }, [selectedDate]);

  const fetchMealPlans = async () => {
    if (!user) return;
    try {
      setLoading(true);
      const response = await mealPlanAPI.getPlansByDate(selectedDate);
      if (response.data.success) {
        setMealPlans(response.data.data || []);
      }
    } catch (err) {
      setError('Failed to load meal plans');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleGenerateAIRecommendation = async () => {
    if (
      formData.planName.trim() ||
      formData.ingredients.trim() ||
      formData.instructions.trim()
    ) {
      if (!window.confirm('Generate new AI suggestion? Current fields will be overwritten.')) {
        return;
      }
    }

    try {
      setLoading(true);
      const response = await mealPlanAPI.getAIRecommendation(formData.mealType, selectedDate);
      if (response.data.success) {
        const rec = response.data.data;
        setAiRecommendation(rec);
        setFormData({
          planName: rec.suggestedName || `AI ${formData.mealType} Suggestion`,
          mealType: formData.mealType,
          ingredients: rec.ingredients || '',
          instructions: rec.instructions || '',
        });
      }
    } catch (err) {
      setError('Failed to generate AI recommendation');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const isFormValid = () =>
    formData.planName.trim() !== '' &&
    formData.mealType &&
    formData.ingredients.trim() !== '' &&
    formData.instructions.trim() !== '';

  const handleSaveMealPlan = async () => {
    if (!isFormValid()) return;

    try {
      const planData = {
        ...formData,
        planDate: selectedDate,
        calories: aiRecommendation?.calories || 400,
        estimatedCost: aiRecommendation?.estimatedCost || 150, // NPR example
        aiRecommendation: aiRecommendation?.aiRecommendation || '',
        isRecommended: !!aiRecommendation,
      };

      if (editingPlan) {
        await mealPlanAPI.update(editingPlan.id, planData);
      } else {
        await mealPlanAPI.create(planData);
      }

      setOpenDialog(false);
      setEditingPlan(null);
      setAiRecommendation(null);
      setFormData({ planName: '', mealType: 'BREAKFAST', ingredients: '', instructions: '' });
      fetchMealPlans();
    } catch (err) {
      setError('Failed to save meal plan');
      console.error(err);
    }
  };

  // ... rest of your handlers (handleEdit, handleDelete, handleOpenDialog, getMealTypeIcon, getMealTypeColor)

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Box sx={{ mb: 4, textAlign: 'center' }}>
        <Typography variant="h3" component="h1" gutterBottom>
          üçΩÔ∏è Smart Meal Planner
        </Typography>
        <Typography variant="h6" color="text.secondary">
          Plan your daily meals with AI suggestions
        </Typography>
      </Box>

      <Box sx={{ display: 'flex', gap: 2, mb: 4, flexWrap: 'wrap', alignItems: 'center' }}>
        <TextField
          type="date"
          label="Select Date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
          sx={{ minWidth: 200 }}
          InputProps={{
            startAdornment: <CalendarToday sx={{ mr: 1, color: 'action.active' }} />,
          }}
        />
        <Button variant="contained" startIcon={<Add />} onClick={handleOpenDialog}>
          Add Meal
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }} onClose={() => setError(null)}>
          {error}
        </Alert>
      )}

      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', py: 8 }}>
          <CircularProgress />
        </Box>
      ) : mealPlans.length === 0 ? (
        <Box sx={{ textAlign: 'center', py: 8 }}>
          <Restaurant sx={{ fontSize: 64, color: 'text.secondary', mb: 2 }} />
          <Typography variant="h6" color="text.secondary" gutterBottom>
            No meals planned for {format(new Date(selectedDate), 'PPP')}
          </Typography>
          <Button variant="contained" startIcon={<Add />} onClick={handleOpenDialog} sx={{ mt: 2 }}>
            Create First Meal
          </Button>
        </Box>
      ) : (
        <Grid container spacing={3}>
          {mealPlans.map((plan) => (
            <Grid item xs={12} sm={6} md={4} key={plan.id}>
              <Card
                sx={{
                  height: '100%',
                  position: 'relative',
                  border: plan.isRecommended ? `2px solid ${theme.palette.success.main}` : 'none',
                }}
              >
                {plan.isRecommended && (
                  <Chip
                    icon={<AutoAwesome />}
                    label="AI Recommended"
                    color="success"
                    size="small"
                    sx={{ position: 'absolute', top: 12, right: 12 }}
                  />
                )}

                <CardContent>
                  {/* your card content ‚Äì just change currency */}
                  <Chip
                    icon={<AttachMoney />}
                    label={`NPR ${(plan.estimatedCost || 0).toFixed(0)}`}
                    size="small"
                    color="success"
                    variant="outlined"
                  />
                  {/* ... rest unchanged */}
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}

      {/* Dialog ‚Äì mostly unchanged, just add disabled state on save */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)} maxWidth="md" fullWidth>
        {/* ... your dialog content ... */}
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Cancel</Button>
          <Button
            variant="contained"
            onClick={handleSaveMealPlan}
            disabled={!isFormValid() || loading}
          >
            {editingPlan ? 'Update' : 'Save'}
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default MealPlanner;
