import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  Button,
  Avatar,
  CircularProgress,
  Chip,
  Rating,
  IconButton,
  TextField,
  InputAdornment,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Alert,
} from '@mui/material';
import {
  Person,
  ShoppingCart,
  Restaurant,
  Favorite,
  FavoriteBorder,
  Logout,
  Search as SearchIcon,
  Add,
  Remove,
  Delete as DeleteIcon,
} from '@mui/icons-material';

const SimpleUserDashboard = () => {
  const navigate = useNavigate();

  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentView, setCurrentView] = useState('dashboard');
  const [vendorProducts, setVendorProducts] = useState([]);
  const [selectedProductId, setSelectedProductId] = useState(null);
  const [showProductDetail, setShowProductDetail] = useState(false);

  // Core state
  const [cartItems, setCartItems] = useState([]);
  const [favoriteItems, setFavoriteItems] = useState([]);
  const [userStats, setUserStats] = useState({
    totalOrders: 0,
    totalSpent: 0,
    points: 0,
    favoriteItemsCount: 0,
    memberSince: new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long' }),
    lastOrder: 'No orders yet',
    avgOrderValue: 0,
  });

  // Product filters
  const [searchQuery, setSearchQuery] = useState('');
  const [filterCategory, setFilterCategory] = useState('all');

  const imageFallback = 'https://via.placeholder.com/300x200?text=No+Image';

  // ────────────────────────────────────────────────
  // Auth & Persistence
  // ────────────────────────────────────────────────
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) navigate('/login');
  }, [navigate]);

  useEffect(() => {
    try {
      const stored = localStorage.getItem('user');
      setUserData(stored ? JSON.parse(stored) : { name: 'Guest', email: 'guest@example.com', role: 'USER' });
    } catch {
      setUserData({ name: 'Guest', email: 'guest@example.com', role: 'USER' });
    }
    setLoading(false);
  }, []);

  // Load saved favorites & cart
  useEffect(() => {
    const storedFavorites = localStorage.getItem('favorites');
    if (storedFavorites) setFavoriteItems(JSON.parse(storedFavorites));

    const storedCart = localStorage.getItem('cart');
    if (storedCart) setCartItems(JSON.parse(storedCart));
  }, []);

  // Save favorites & cart whenever they change
  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favoriteItems));
  }, [favoriteItems]);

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cartItems));
  }, [cartItems]);

  // ────────────────────────────────────────────────
  // Load Products
  // ────────────────────────────────────────────────
  useEffect(() => {
    const loadProducts = async () => {
      try {
        const res = await fetch('http://localhost:8081/api/products');
        if (!res.ok) {
          throw new Error(`Server error: ${res.status} ${res.statusText}`);
        }
        const data = await res.json();
        const products = Array.isArray(data?.data) ? data.data : data || [];

        const normalized = products.map((p) => ({
          ...p,
          vendor: p?.vendor?.shopName || p?.vendor?.name || p?.vendor || 'Unknown Vendor',
          image: p.image || imageFallback,
          ingredients: p.ingredients || 'No ingredients listed',
          nutrition: p.nutrition || 'No nutrition info',
          reviews: p.reviews || [],
        }));

        setVendorProducts(normalized);
      } catch (err) {
        console.error('Failed to load products:', err);
        alert(`Could not load products:\n${err.message}\n\nIs backend running at http://localhost:8081 ?`);
        setVendorProducts([]);
      }
    };

    loadProducts();
  }, []);

  // ────────────────────────────────────────────────
  // Handlers
  // ────────────────────────────────────────────────
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('favorites');
    localStorage.removeItem('cart');
    navigate('/home');
  };

  const handleRateProduct = (productId, newRating) => {
    setVendorProducts((prev) =>
      prev.map((p) =>
        p.id === productId ? { ...p, rating: newRating, totalRatings: (p.totalRatings || 0) + 1 } : p
      )
    );
  };

  const handleAddToFavorites = (product) => {
    if (favoriteItems.some((f) => f.id === product.id)) {
      alert('Already in your favorites!');
      return;
    }

    const newFav = {
      id: product.id,
      name: product.name,
      vendor: product.vendor,
      price: product.price,
      rating: product.rating,
      image: product.image,
    };

    setFavoriteItems((prev) => {
      const updated = [...prev, newFav];
      // Update stats immediately
      setUserStats((prevStats) => ({
        ...prevStats,
        favoriteItemsCount: updated.length,
      }));
      return updated;
    });

    alert(`"${product.name}" added to favorites!`);
  };

  const handleRemoveFromFavorites = (id) => {
    setFavoriteItems((prev) => {
      const updated = prev.filter((f) => f.id !== id);
      setUserStats((prevStats) => ({
        ...prevStats,
        favoriteItemsCount: updated.length,
      }));
      return updated;
    });
  };

  const handleAddToCart = (product, qty = 1) => {
    setCartItems((prev) => {
      const existing = prev.find((item) => item.id === product.id);
      if (existing) {
        return prev.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + qty } : item
        );
      }
      return [...prev, { ...product, quantity: qty }];
    });
  };

  const handleUpdateCartQuantity = (id, delta) => {
    setCartItems((prev) =>
      prev.map((item) =>
        item.id === id ? { ...item, quantity: Math.max(1, item.quantity + delta) } : item
      )
    );
  };

  const handleRemoveFromCart = (id) => {
    setCartItems((prev) => prev.filter((item) => item.id !== id));
  };

  const handleCheckout = async () => {
    if (cartItems.length === 0) {
      alert('Your cart is empty');
      return;
    }

    const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

    try {
      const res = await fetch('http://localhost:8081/api/orders', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          items: cartItems.map((item) => ({ productId: item.id, quantity: item.quantity })),
          total,
          userId: userData?.id,
        }),
      });

      if (!res.ok) throw new Error('Checkout failed');

      setCartItems([]);
      setUserStats((prev) => {
        const newOrders = prev.totalOrders + 1;
        const newSpent = prev.totalSpent + total;
        return {
          ...prev,
          totalOrders: newOrders,
          totalSpent: newSpent,
          points: prev.points + Math.floor(total),
          lastOrder: new Date().toLocaleDateString(),
          avgOrderValue: newOrders > 0 ? newSpent / newOrders : 0,
        };
      });

      alert(`Checkout successful! Total: NPR ${total.toFixed(2)}`);
    } catch (err) {
      alert('Failed to checkout: ' + err.message);
    }
  };

  // ────────────────────────────────────────────────
  // Computed values
  // ────────────────────────────────────────────────
  const filteredProducts = vendorProducts.filter((p) => {
    const nameMatch = p.name.toLowerCase().includes(searchQuery.toLowerCase());
    const vendorMatch = p.vendor.toLowerCase().includes(searchQuery.toLowerCase());
    const categoryMatch = filterCategory === 'all' || p.category === filterCategory;
    return (nameMatch || vendorMatch) && categoryMatch;
  });

  const categories = ['all', ...new Set(vendorProducts.map((p) => p.category || '').filter(Boolean))];

  const getViewTitle = () => {
    switch (currentView) {
      case 'dashboard': return 'Dashboard';
      case 'meals':     return 'Browse Products';
      case 'favorites': return `Favorites (${favoriteItems.length})`;
      case 'profile':   return 'Profile';
      case 'cart':      return `Cart (${cartItems.length})`;
      default:          return 'Dashboard';
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '80vh' }}>
        <CircularProgress size={64} />
      </Box>
    );
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      <Container maxWidth="lg" sx={{ py: 4 }}>
        {/* Dashboard */}
        {currentView === 'dashboard' && (
          <>
            <Paper sx={{ p: 3, mb: 4, bgcolor: 'primary.50' }}>
              <Grid container spacing={2} alignItems="center">
                <Grid item xs={12} sm={6} md={4}>
                  <Button variant="contained" startIcon={<Restaurant />} onClick={() => setCurrentView('meals')}>
                    Order Now
                  </Button>
                </Grid>
                <Grid item xs={12} sm={6} md={4}>
                  <Button variant="outlined" startIcon={<ShoppingCart />} onClick={() => setCurrentView('cart')}>
                    Cart ({cartItems.length})
                  </Button>
                </Grid>
              </Grid>
            </Paper>

            <Box sx={{ mb: 4, display: 'flex', alignItems: 'center' }}>
              <Avatar sx={{ width: 64, height: 64, bgcolor: 'primary.main', mr: 2 }}>
                <Person fontSize="large" />
              </Avatar>
              <Box>
                <Typography variant="h5">Welcome, {userData?.name || 'User'}!</Typography>
                <Typography color="text.secondary">
                  Member since {userStats.memberSince} • {userStats.points} points
                </Typography>
              </Box>
            </Box>

            <Grid container spacing={3} sx={{ mb: 5 }}>
              <Grid item xs={6} sm={3}>
                <Card sx={{ p: 2, textAlign: 'center' }}>
                  <Typography variant="h4">{userStats.totalOrders}</Typography>
                  <Typography color="text.secondary">Orders</Typography>
                </Card>
              </Grid>
              <Grid item xs={6} sm={3}>
                <Card sx={{ p: 2, textAlign: 'center', bgcolor: 'success.light', color: 'white' }}>
                  <Typography variant="h4">NPR {userStats.totalSpent.toFixed(0)}</Typography>
                  <Typography>Total Spent</Typography>
                </Card>
              </Grid>
              <Grid item xs={6} sm={3}>
                <Card sx={{ p: 2, textAlign: 'center' }}>
                  <Typography variant="h4">{userStats.favoriteItemsCount}</Typography>
                  <Typography color="text.secondary">Favorites</Typography>
                </Card>
              </Grid>
              <Grid item xs={6} sm={3}>
                <Card sx={{ p: 2, textAlign: 'center' }}>
                  <Typography variant="h4">{userStats.points}</Typography>
                  <Typography color="text.secondary">Points</Typography>
                </Card>
              </Grid>
            </Grid>

            <Button
              variant="contained"
              size="large"
              fullWidth
              startIcon={<Restaurant />}
              onClick={() => setCurrentView('meals')}
              sx={{ mb: 4, py: 2 }}
            >
              Browse Products
            </Button>
          </>
        )}

        {/* Products View */}
        {currentView === 'meals' && (
          <>
            <Typography variant="h4" gutterBottom>Available Products</Typography>

            <Box sx={{ display: 'flex', gap: 2, mb: 3, flexWrap: 'wrap' }}>
              <TextField
                fullWidth
                variant="outlined"
                placeholder="Search products..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                InputProps={{
                  startAdornment: <InputAdornment position="start"><SearchIcon /></InputAdornment>,
                }}
              />
              <FormControl sx={{ minWidth: 140 }}>
                <InputLabel>Category</InputLabel>
                <Select
                  value={filterCategory}
                  label="Category"
                  onChange={(e) => setFilterCategory(e.target.value)}
                >
                  {categories.map((cat) => (
                    <MenuItem key={cat} value={cat}>
                      {cat.charAt(0).toUpperCase() + cat.slice(1)}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Box>

            {filteredProducts.length === 0 ? (
              <Paper sx={{ p: 5, textAlign: 'center' }}>
                <Typography variant="h6" color="text.secondary" gutterBottom>
                  No products found
                </Typography>
                <Button variant="outlined" onClick={() => { setSearchQuery(''); setFilterCategory('all'); }}>
                  Reset Filters
                </Button>
              </Paper>
            ) : (
              <Grid container spacing={3}>
                {filteredProducts.map((product) => {
                  const isFavorited = favoriteItems.some((f) => f.id === product.id);

                  return (
                    <Grid item xs={12} sm={6} md={4} key={product.id}>
                      <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                        <Box sx={{ position: 'relative', pt: '75%' }}>
                          <img
                            src={product.image}
                            alt={product.name}
                            style={{ position: 'absolute', inset: 0, width: '100%', height: '100%', objectFit: 'cover' }}
                            onError={(e) => (e.target.src = imageFallback)}
                          />
                          <Chip
                            label={`NPR ${product.price}`}
                            color="success"
                            size="small"
                            sx={{ position: 'absolute', top: 8, right: 8 }}
                          />
                        </Box>

                        <CardContent sx={{ flexGrow: 1 }}>
                          <Typography variant="h6" gutterBottom>{product.name}</Typography>
                          <Typography variant="body2" color="text.secondary" gutterBottom>
                            {product.vendor}
                          </Typography>
                          <Rating
                            value={product.rating || 0}
                            precision={0.5}
                            onChange={(_, v) => v && handleRateProduct(product.id, v)}
                            size="small"
                          />

                          <Box sx={{ mt: 2, display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                            <Button variant="contained" size="small" onClick={() => handleAddToCart(product)}>
                              Add to Cart
                            </Button>

                            <Button
                              variant="outlined"
                              size="small"
                              startIcon={isFavorited ? <Favorite /> : <FavoriteBorder />}
                              color={isFavorited ? 'error' : 'default'}
                              onClick={() => handleAddToFavorites(product)}
                            >
                              {isFavorited ? 'Favorited' : 'Favorite'}
                            </Button>

                            <Button
                              variant="outlined"
                              size="small"
                              onClick={() => {
                                setSelectedProductId(product.id);
                                setShowProductDetail(true);
                              }}
                            >
                              Details
                            </Button>
                          </Box>
                        </CardContent>
                      </Card>
                    </Grid>
                  );
                })}
              </Grid>
            )}
          </>
        )}

        {/* Favorites View */}
        {currentView === 'favorites' && (
          <>
            <Typography variant="h4" gutterBottom>
              My Favorites ({favoriteItems.length})
            </Typography>

            {favoriteItems.length === 0 ? (
              <Paper sx={{ p: 5, textAlign: 'center' }}>
                <Typography variant="h6" color="text.secondary">No favorites yet</Typography>
                <Button variant="contained" sx={{ mt: 2 }} onClick={() => setCurrentView('meals')}>
                  Browse Products
                </Button>
              </Paper>
            ) : (
              <Grid container spacing={3}>
                {favoriteItems.map((item) => (
                  <Grid item xs={12} sm={6} md={4} key={item.id}>
                    <Card>
                      <img
                        src={item.image}
                        alt={item.name}
                        style={{ width: '100%', height: 180, objectFit: 'cover' }}
                        onError={(e) => (e.target.src = imageFallback)}
                      />
                      <CardContent>
                        <Typography variant="h6">{item.name}</Typography>
                        <Typography variant="body2" color="text.secondary">
                          {item.vendor} • NPR {item.price}
                        </Typography>
                        <Box sx={{ mt: 2, display: 'flex', gap: 1 }}>
                          <Button variant="contained" size="small" onClick={() => handleAddToCart(item)}>
                            Add to Cart
                          </Button>
                          <Button
                            variant="outlined"
                            size="small"
                            color="error"
                            onClick={() => handleRemoveFromFavorites(item.id)}
                          >
                            Remove
                          </Button>
                        </Box>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            )}
          </>
        )}

        {/* Cart View */}
        {currentView === 'cart' && (
          <>
            <Typography variant="h4" gutterBottom>
              Shopping Cart ({cartItems.length})
            </Typography>

            {cartItems.length === 0 ? (
              <Paper sx={{ p: 5, textAlign: 'center' }}>
                <Typography variant="h6" color="text.secondary">Your cart is empty</Typography>
                <Button variant="contained" sx={{ mt: 2 }} onClick={() => setCurrentView('meals')}>
                  Shop Now
                </Button>
              </Paper>
            ) : (
              <>
                <TableContainer component={Paper}>
                  <Table>
                    <TableHead>
                      <TableRow>
                        <TableCell>Product</TableCell>
                        <TableCell align="right">Price</TableCell>
                        <TableCell align="center">Quantity</TableCell>
                        <TableCell align="right">Subtotal</TableCell>
                        <TableCell align="right">Actions</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {cartItems.map((item) => (
                        <TableRow key={item.id}>
                          <TableCell>{item.name}</TableCell>
                          <TableCell align="right">NPR {item.price}</TableCell>
                          <TableCell align="center">
                            <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                              <IconButton size="small" onClick={() => handleUpdateCartQuantity(item.id, -1)}>
                                <Remove />
                              </IconButton>
                              <Typography sx={{ mx: 2 }}>{item.quantity}</Typography>
                              <IconButton size="small" onClick={() => handleUpdateCartQuantity(item.id, 1)}>
                                <Add />
                              </IconButton>
                            </Box>
                          </TableCell>
                          <TableCell align="right">NPR {(item.price * item.quantity).toFixed(2)}</TableCell>
                          <TableCell align="right">
                            <IconButton color="error" onClick={() => handleRemoveFromCart(item.id)}>
                              <DeleteIcon />
                            </IconButton>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>

                <Box sx={{ mt: 3, textAlign: 'right' }}>
                  <Typography variant="h6">
                    Total: NPR {cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0).toFixed(2)}
                  </Typography>
                  <Button variant="contained" color="primary" size="large" sx={{ mt: 2 }} onClick={handleCheckout}>
                    Checkout
                  </Button>
                </Box>
              </>
            )}
          </>
        )}

        {/* Profile View */}
        {currentView === 'profile' && (
          <Paper sx={{ p: 4 }}>
            <Typography variant="h5" gutterBottom>Profile</Typography>
            <Typography>Name: {userData?.name || 'Guest'}</Typography>
            <Typography>Email: {userData?.email || 'guest@example.com'}</Typography>
            <Typography>Member since: {userStats.memberSince}</Typography>
            <Button variant="outlined" color="error" sx={{ mt: 3 }} onClick={handleLogout}>
              Logout
            </Button>
          </Paper>
        )}

        {/* Product Detail Dialog */}
        <Dialog open={showProductDetail} onClose={() => setShowProductDetail(false)} maxWidth="md" fullWidth>
          {selectedProductId && (() => {
            const product = vendorProducts.find((p) => p.id === selectedProductId);
            if (!product) return null;

            return (
              <>
                <DialogTitle>{product.name}</DialogTitle>
                <DialogContent>
                  <Box sx={{ display: 'flex', gap: 3, flexDirection: { xs: 'column', sm: 'row' } }}>
                    <img
                      src={product.image}
                      alt={product.name}
                      style={{ width: '100%', maxWidth: 300, height: 300, objectFit: 'cover', borderRadius: 8 }}
                      onError={(e) => (e.target.src = imageFallback)}
                    />
                    <Box sx={{ flex: 1 }}>
                      <Typography variant="h6">Vendor: {product.vendor}</Typography>
                      <Typography variant="h5" color="primary" sx={{ my: 1 }}>
                        NPR {product.price}
                      </Typography>
                      <Rating value={product.rating || 0} readOnly size="large" />
                      <Typography variant="h6" sx={{ mt: 3 }}>Ingredients</Typography>
                      <Typography variant="body1">{product.ingredients}</Typography>
                      <Typography variant="h6" sx={{ mt: 3 }}>Nutrition Info</Typography>
                      <Typography variant="body1">{product.nutrition}</Typography>
                    </Box>
                  </Box>
                </DialogContent>
                <DialogActions>
                  <Button onClick={() => setShowProductDetail(false)}>Close</Button>
                  <Button variant="contained" onClick={() => handleAddToCart(product)}>
                    Add to Cart
                  </Button>
                </DialogActions>
              </>
            );
          })()}
        </Dialog>
      </Container>
    </Box>
  );
};

export default SimpleUserDashboard;
