import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  Button,
  Avatar,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  Alert,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Tabs,
  Tab,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  AppBar,
  Toolbar,
  IconButton,
  Menu,
  MenuItem,
  LinearProgress,
} from '@mui/material';
import {
  Store,
  ShoppingCart,
  Assessment,
  People,
  TrendingUp,
  Restaurant,
  LocalOffer,
  AttachMoney,
  Logout,
  Add,
  Edit,
  Delete,
  Visibility,
  CheckCircle,
  Pending,
  Cancel,
  ArrowBack,
  Home,
  AccountCircle,
} from '@mui/icons-material';

const SimpleVendorDashboard = () => {
  const [vendorData, setVendorData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentView, setCurrentView] = useState('dashboard'); // dashboard, products, orders, analytics, promotions
  const [anchorEl, setAnchorEl] = useState(null);

  // Dialog states
  const [productDialogOpen, setProductDialogOpen] = useState(false);
  const [orderDialogOpen, setOrderDialogOpen] = useState(false);
  const [analyticsDialogOpen, setAnalyticsDialogOpen] = useState(false);
  const [promotionDialogOpen, setPromotionDialogOpen] = useState(false);
  const [activeTab, setActiveTab] = useState(0);

  // Sample data
  const [products, setProducts] = useState([
    { id: 1, name: 'Classic Burger', price: 12.99, category: 'Main Course', status: 'Available', orders: 45 },
    { id: 2, name: 'French Fries', price: 4.99, category: 'Side Dish', status: 'Available', orders: 89 },
    { id: 3, name: 'Caesar Salad', price: 8.99, category: 'Salad', status: 'Available', orders: 23 },
    { id: 4, name: 'Grilled Chicken', price: 15.99, category: 'Main Course', status: 'Unavailable', orders: 34 },
  ]);

  const [orders, setOrders] = useState([
    { id: 1234, customer: 'John Doe', items: 'Burger, Fries, Coke', total: 15.99, status: 'Pending', time: '2 mins ago' },
    { id: 1233, customer: 'Jane Smith', items: 'Pizza, Salad', total: 22.50, status: 'Preparing', time: '5 mins ago' },
    { id: 1232, customer: 'Bob Johnson', items: 'Sandwich, Juice', total: 8.75, status: 'Ready', time: '8 mins ago' },
    { id: 1231, customer: 'Alice Brown', items: 'Pasta, Bread', total: 18.25, status: 'Delivered', time: '12 mins ago' },
  ]);

  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    category: '',
    description: ''
  });

  useEffect(() => {
    // Get vendor data from localStorage
    const user = localStorage.getItem('user');
    const token = localStorage.getItem('token');

    if (!user || !token) {
      setError('Please login first');
      setLoading(false);
      return;
    }

    try {
      const parsedUser = JSON.parse(user);
      setVendorData(parsedUser);
      setLoading(false);
    } catch (err) {
      setError('Error loading user data');
      setLoading(false);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  // Navigation Functions
  const handleBackToDashboard = () => {
    setCurrentView('dashboard');
  };

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const getViewTitle = () => {
    switch(currentView) {
      case 'dashboard': return 'Vendor Dashboard';
      case 'products': return 'Product Management';
      case 'orders': return 'Order Management';
      case 'analytics': return 'Analytics';
      case 'promotions': return 'Promotions';
      default: return 'Vendor Dashboard';
    }
  };

  // Product Management Functions
  const handleAddProduct = () => {
    if (newProduct.name && newProduct.price && newProduct.category) {
      const product = {
        id: products.length + 1,
        name: newProduct.name,
        price: parseFloat(newProduct.price),
        category: newProduct.category,
        description: newProduct.description,
        status: 'Available',
        orders: 0
      };
      setProducts([...products, product]);
      setNewProduct({ name: '', price: '', category: '', description: '' });
      setProductDialogOpen(false);
      alert('Product added successfully!');
    } else {
      alert('Please fill all required fields');
    }
  };

  const handleEditProduct = (product) => {
    setCurrentView('products');
    alert(`Edit product: ${product.name}`);
  };

  const handleDeleteProduct = (productId) => {
    if (confirm('Are you sure you want to delete this product?')) {
      setProducts(products.filter(p => p.id !== productId));
      alert('Product deleted successfully!');
    }
  };

  const handleToggleProductStatus = (productId) => {
    setProducts(products.map(p => 
      p.id === productId 
        ? { ...p, status: p.status === 'Available' ? 'Unavailable' : 'Available' }
        : p
    ));
    alert('Product status updated!');
  };

  const handleViewProducts = () => {
    setCurrentView('products');
  };

  // Order Management Functions
  const handleUpdateOrderStatus = (orderId, newStatus) => {
    setOrders(orders.map(o => 
      o.id === orderId 
        ? { ...o, status: newStatus }
        : o
    ));
    alert(`Order #${orderId} status updated to ${newStatus}!`);
  };

  const handleViewOrderDetails = (order) => {
    alert(`Order Details:\n\nOrder ID: #${order.id}\nCustomer: ${order.customer}\nItems: ${order.items}\nTotal: $${order.total}\nStatus: ${order.status}\nTime: ${order.time}`);
  };

  const handleViewOrders = () => {
    setCurrentView('orders');
  };

  // Analytics Functions
  const handleViewAnalytics = () => {
    setCurrentView('analytics');
  };

  // Promotion Functions
  const handleCreatePromotion = () => {
    alert('ðŸŽ‰ Promotion Created!\n\nPromotion: 20% OFF on all burgers\nValid: Next 7 days\nCode: BURGER20\nStatus: Active');
    setPromotionDialogOpen(false);
  };

  const handleViewPromotions = () => {
    setCurrentView('promotions');
  };

  if (loading) {
    return (
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
          <CircularProgress size={60} />
        </Box>
      </Container>
    );
  }

  if (error) {
    return (
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Alert severity="error">{error}</Alert>
        <Button
          variant="contained"
          onClick={() => window.location.href = '/login'}
          sx={{ mt: 2 }}
        >
          Go to Login
        </Button>
      </Container>
    );
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      {/* Navbar */}
      <AppBar position="static" sx={{ mb: 3 }}>
        <Toolbar>
          {currentView !== 'dashboard' && (
            <IconButton
              edge="start"
              color="inherit"
              onClick={handleBackToDashboard}
              sx={{ mr: 2 }}
            >
              <ArrowBack />
            </IconButton>
          )}

          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            {getViewTitle()}
          </Typography>

          <IconButton
            edge="end"
            color="inherit"
            onClick={handleMenuOpen}
          >
            <AccountCircle />
          </IconButton>

          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={handleMenuClose}
          >
            <MenuItem onClick={handleMenuClose}>
              <ListItemIcon><Store /></ListItemIcon>
              <ListItemText>{vendorData?.name || 'Vendor'}</ListItemText>
            </MenuItem>
            <MenuItem onClick={() => { handleMenuClose(); handleLogout(); }}>
              <ListItemIcon><Logout /></ListItemIcon>
              <ListItemText>Logout</ListItemText>
            </MenuItem>
          </Menu>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ py: 2 }}>
        {/* Dashboard View */}
        {currentView === 'dashboard' && (
          <>
            {/* Header */}
            <Box sx={{ mb: 4, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <Avatar sx={{ bgcolor: 'primary.main', mr: 2 }}>
                  <Store />
                </Avatar>
                <Box>
                  <Typography variant="h6" color="text.secondary">
                    Welcome, {vendorData?.name || 'Vendor'}!
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Manage your restaurant business efficiently
                  </Typography>
                </Box>
              </Box>
            </Box>

            {/* Vendor Stats Cards */}
            <Grid container spacing={3} sx={{ mb: 4 }}>
              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'success.main', mr: 2 }}>
                        <AttachMoney />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          $12,450
                        </Typography>
                        <Typography color="text.secondary">
                          Total Revenue
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'info.main', mr: 2 }}>
                        <ShoppingCart />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          156
                        </Typography>
                        <Typography color="text.secondary">
                          Total Orders
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'warning.main', mr: 2 }}>
                        <Restaurant />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          24
                        </Typography>
                        <Typography color="text.secondary">
                          Active Products
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'primary.main', mr: 2 }}>
                        <TrendingUp />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          4.8
                        </Typography>
                        <Typography color="text.secondary">
                          Avg Rating
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>

            {/* Quick Actions */}
            <Grid container spacing={3} sx={{ mb: 4 }}>
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Quick Actions
                  </Typography>
                  <List>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1, mb: 1 }}
                      onClick={handleViewProducts}
                    >
                      <ListItemIcon>
                        <Restaurant color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="Manage Products" 
                        secondary="Add, edit, or remove food items" 
                      />
                    </ListItem>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1, mb: 1 }}
                      onClick={handleViewOrders}
                    >
                      <ListItemIcon>
                        <ShoppingCart color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="View Orders" 
                        secondary="Manage customer orders and fulfillment" 
                      />
                    </ListItem>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1, mb: 1 }}
                      onClick={handleViewAnalytics}
                    >
                      <ListItemIcon>
                        <Assessment color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="Analytics" 
                        secondary="View sales performance and insights" 
                      />
                    </ListItem>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1 }}
                      onClick={handleViewPromotions}
                    >
                      <ListItemIcon>
                        <LocalOffer color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="Promotions" 
                        secondary="Create and manage special offers" 
                      />
                    </ListItem>
                  </List>
                </Paper>
              </Grid>

              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Recent Orders
                  </Typography>
                  <List>
                    {orders.slice(0, 4).map((order) => (
                      <ListItem key={order.id} sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                        <ListItemText 
                          primary={
                            <Box display="flex" justifyContent="space-between" alignItems="center">
                              <span>Order #{order.id}</span>
                              <Chip 
                                label={order.status} 
                                color={
                                  order.status === 'Delivered' ? 'success' :
                                  order.status === 'Ready' ? 'info' :
                                  order.status === 'Preparing' ? 'warning' : 'default'
                                }
                                size="small"
                              />
                            </Box>
                          }
                          secondary={`${order.customer} â€¢ ${order.items} â€¢ $${order.total}`}
                        />
                      </ListItem>
                    ))}
                  </List>
                  <Button 
                    variant="outlined" 
                    fullWidth 
                    sx={{ mt: 2 }}
                    onClick={handleViewOrders}
                  >
                    View All Orders
                  </Button>
                </Paper>
              </Grid>
            </Grid>

            {/* Top Products and Customer Insights */}
            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Top Products
                  </Typography>
                  <List>
                    {products.slice(0, 5).map((product) => (
                      <ListItem key={product.id} sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                        <ListItemText 
                          primary={product.name}
                          secondary={`${product.category} â€¢ $${product.price} â€¢ ${product.orders} orders`}
                        />
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                          <Typography variant="body2" color="success.main">
                            â­ {product.rating || '4.5'}
                          </Typography>
                        </Box>
                      </ListItem>
                    ))}
                  </List>
                  <Button 
                    variant="outlined" 
                    fullWidth 
                    sx={{ mt: 2 }}
                    onClick={handleViewProducts}
                  >
                    View All Products
                  </Button>
                </Paper>
              </Grid>

              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Customer Insights
                  </Typography>
                  <List>
                    <ListItem sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                      <ListItemText 
                        primary="Repeat Customers"
                        secondary="Customers who ordered more than 3 times"
                      />
                      <Typography variant="h6" color="success.main">
                        68%
                      </Typography>
                    </ListItem>
                    <ListItem sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                      <ListItemText 
                        primary="Avg Order Value"
                        secondary="Average amount spent per order"
                      />
                      <Typography variant="h6" color="primary.main">
                        $24.50
                      </Typography>
                    </ListItem>
                    <ListItem sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                      <ListItemText 
                        primary="Peak Hours"
                        secondary="Busiest time for orders"
                      />
                      <Typography variant="h6" color="warning.main">
                        12-2 PM
                      </Typography>
                    </ListItem>
                    <ListItem sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                      <ListItemText 
                        primary="Customer Rating"
                        secondary="Average customer satisfaction"
                      />
                      <Typography variant="h6" color="success.main">
                        4.8 â­
                      </Typography>
                    </ListItem>
                  </List>
                  <Button 
                    variant="contained" 
                    fullWidth 
                    sx={{ mt: 2 }}
                    onClick={handleViewAnalytics}
                  >
                    View Detailed Analytics
                  </Button>
                </Paper>
              </Grid>
            </Grid>

            {/* Performance Progress */}
            <Paper sx={{ p: 3, mt: 4 }}>
              <Typography variant="h6" fontWeight="bold" sx={{ mb: 2 }}>
                Monthly Performance
              </Typography>
              <Box sx={{ mb: 2 }}>
                <Typography variant="body2" color="text.secondary">
                  Revenue Goal: $12,450 / $15,000
                </Typography>
                <LinearProgress 
                  variant="determinate" 
                  value={(12450 / 15000) * 100} 
                  sx={{ mt: 1 }}
                />
              </Box>
              <Typography variant="body2" color="primary.main">
                ðŸŽ¯ You're $2,550 away from your monthly revenue goal!
              </Typography>
            </Paper>

            {/* Account Info */}
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                Account Information
              </Typography>
              <Grid container spacing={3}>
                <Grid item xs={12} md={6}>
                  <Typography variant="body2" color="text.secondary">
                    Business Name
                  </Typography>
                  <Typography variant="body1" sx={{ mb: 2 }}>
                    {vendorData?.name || 'Vendor Business'}
                  </Typography>

                  <Typography variant="body2" color="text.secondary">
                    Email
                  </Typography>
                  <Typography variant="body1" sx={{ mb: 2 }}>
                    {vendorData?.email || 'vendor@example.com'}
                  </Typography>
                </Grid>

                <Grid item xs={12} md={6}>
                  <Typography variant="body2" color="text.secondary">
                    Role
                  </Typography>
                  <Typography variant="body1" sx={{ mb: 2 }}>
                    {vendorData?.role || 'VENDOR'}
                  </Typography>

                  <Typography variant="body2" color="text.secondary">
                    User ID
                  </Typography>
                  <Typography variant="body1">
                    {vendorData?.id || 'N/A'}
                  </Typography>
                </Grid>
              </Grid>
            </Paper>

            {/* Product Management Dialog */}
            <Dialog open={productDialogOpen} onClose={() => setProductDialogOpen(false)} maxWidth="md" fullWidth>
              <DialogTitle>Manage Products</DialogTitle>
              <DialogContent>
                <Tabs value={activeTab} onChange={(e, newValue) => setActiveTab(newValue)} sx={{ mb: 2 }}>
                  <Tab label="Add Product" />
                  <Tab label="Existing Products" />
                </Tabs>

                {activeTab === 0 && (
                  <Box sx={{ p: 2 }}>
                    <TextField
                      fullWidth
                      label="Product Name"
                      value={newProduct.name}
                      onChange={(e) => setNewProduct({...newProduct, name: e.target.value})}
                      sx={{ mb: 2 }}
                    />
                    <TextField
                      fullWidth
                      label="Price"
                      type="number"
                      value={newProduct.price}
                      onChange={(e) => setNewProduct({...newProduct, price: e.target.value})}
                      sx={{ mb: 2 }}
                    />
                    <TextField
                      fullWidth
                      label="Category"
                      value={newProduct.category}
                      onChange={(e) => setNewProduct({...newProduct, category: e.target.value})}
                      sx={{ mb: 2 }}
                    />
                    <TextField
                      fullWidth
                      label="Description"
                      multiline
                      rows={3}
                      value={newProduct.description}
                      onChange={(e) => setNewProduct({...newProduct, description: e.target.value})}
                      sx={{ mb: 2 }}
                    />
                    <Button variant="contained" onClick={handleAddProduct} startIcon={<Add />}>
                      Add Product
                    </Button>
                  </Box>
                )}

                {activeTab === 1 && (
                  <TableContainer>
                    <Table>
                      <TableHead>
                        <TableRow>
                          <TableCell>Product</TableCell>
                          <TableCell>Price</TableCell>
                          <TableCell>Category</TableCell>
                          <TableCell>Status</TableCell>
                          <TableCell>Orders</TableCell>
                          <TableCell>Actions</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {products.map((product) => (
                          <TableRow key={product.id}>
                            <TableCell>{product.name}</TableCell>
                            <TableCell>${product.price}</TableCell>
                            <TableCell>{product.category}</TableCell>
                            <TableCell>
                              <Chip 
                                label={product.status} 
                                color={product.status === 'Available' ? 'success' : 'error'}
                                size="small"
                              />
                            </TableCell>
                            <TableCell>{product.orders}</TableCell>
                            <TableCell>
                              <Button 
                                size="small" 
                                onClick={() => handleToggleProductStatus(product.id)}
                              >
                                {product.status === 'Available' ? 'Disable' : 'Enable'}
                              </Button>
                              <Button 
                                size="small" 
                                color="error"
                                onClick={() => handleDeleteProduct(product.id)}
                              >
                                <Delete />
                              </Button>
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                )}
              </DialogContent>
              <DialogActions>
                <Button onClick={() => setProductDialogOpen(false)}>Close</Button>
              </DialogActions>
            </Dialog>

            {/* Order Management Dialog */}
            <Dialog open={orderDialogOpen} onClose={() => setOrderDialogOpen(false)} maxWidth="lg" fullWidth>
              <DialogTitle>All Orders</DialogTitle>
              <DialogContent>
                <TableContainer>
                  <Table>
                    <TableHead>
                      <TableRow>
                        <TableCell>Order ID</TableCell>
                        <TableCell>Customer</TableCell>
                        <TableCell>Items</TableCell>
                        <TableCell>Total</TableCell>
                        <TableCell>Status</TableCell>
                        <TableCell>Time</TableCell>
                        <TableCell>Actions</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {orders.map((order) => (
                        <TableRow key={order.id}>
                          <TableCell>#{order.id}</TableCell>
                          <TableCell>{order.customer}</TableCell>
                          <TableCell>{order.items}</TableCell>
                          <TableCell>${order.total}</TableCell>
                          <TableCell>
                            <Chip 
                              label={order.status} 
                              color={
                                order.status === 'Delivered' ? 'success' :
                                order.status === 'Ready' ? 'info' :
                                order.status === 'Preparing' ? 'warning' : 'default'
                              }
                              size="small"
                            />
                          </TableCell>
                          <TableCell>{order.time}</TableCell>
                          <TableCell>
                            <Box sx={{ display: 'flex', gap: 1 }}>
                              <Button 
                                size="small" 
                                variant="outlined"
                                onClick={() => handleViewOrderDetails(order)}
                              >
                                <Visibility fontSize="small" />
                              </Button>
                              {order.status === 'Pending' && (
                                <Button 
                                  size="small" 
                                  variant="contained"
                                  color="success"
                                  onClick={() => handleUpdateOrderStatus(order.id, 'Preparing')}
                                >
                                  Accept
                                </Button>
                              )}
                              {order.status === 'Preparing' && (
                                <Button 
                                  size="small" 
                                  variant="contained"
                                  color="info"
                                  onClick={() => handleUpdateOrderStatus(order.id, 'Ready')}
                                >
                                  Ready
                                </Button>
                              )}
                              {order.status === 'Ready' && (
                                <Button 
                                  size="small" 
                                  variant="contained"
                                  color="success"
                                  onClick={() => handleUpdateOrderStatus(order.id, 'Delivered')}
                                >
                                  Deliver
                                </Button>
                              )}
                            </Box>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>
              </DialogContent>
              <DialogActions>
                <Button onClick={() => setOrderDialogOpen(false)}>Close</Button>
              </DialogActions>
            </Dialog>

            {/* Promotion Dialog */}
            <Dialog open={promotionDialogOpen} onClose={() => setPromotionDialogOpen(false)} maxWidth="sm" fullWidth>
              <DialogTitle>Create Promotion</DialogTitle>
              <DialogContent>
                <TextField
                  fullWidth
                  label="Promotion Name"
                  defaultValue="Weekend Special"
                  sx={{ mb: 2 }}
                />
                <TextField
                  fullWidth
                  label="Discount Percentage"
                  type="number"
                  defaultValue="20"
                  sx={{ mb: 2 }}
                />
                <TextField
                  fullWidth
                  label="Promotion Code"
                  defaultValue="WEEKEND20"
                  sx={{ mb: 2 }}
                />
                <TextField
                  fullWidth
                  label="Duration (days)"
                  type="number"
                  defaultValue="7"
                  sx={{ mb: 2 }}
                />
              </DialogContent>
              <DialogActions>
                <Button onClick={() => setPromotionDialogOpen(false)}>Cancel</Button>
                <Button variant="contained" onClick={handleCreatePromotion}>Create Promotion</Button>
              </DialogActions>
            </Dialog>
          </>
        )}
      </Container>
    </Box>
  );
};

export default SimpleVendorDashboard;
