import React, { useState } from 'react';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Box,
  Alert,
  CircularProgress,
  Divider,
  useTheme,
} from '@mui/material';
import {
  Email,
  Lock,
} from '@mui/icons-material';

const SimpleLogin = () => {
  const theme = useTheme();

  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const [formErrors, setFormErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  // Simple validation
  const validateForm = () => {
    const errors = {};

    if (!formData.email) {
      errors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = 'Email is invalid';
    }

    if (!formData.password) {
      errors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      errors.password = 'Password must be at least 6 characters';
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));

    // Clear errors when user starts typing
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: '',
      }));
    }

    // Clear message when user starts typing
    if (message) {
      setMessage('');
    }
  };

  // Simple manual login - NO AUTO-ANYTHING
  const handleManualLogin = async () => {
    console.log('Manual login button clicked');

    if (!validateForm()) {
      console.log('Validation failed');
      return;
    }

    console.log('Starting manual login with:', formData.email);
    setLoading(true);
    setMessage('');

    try {
      // Direct API call - no context, no auto-redirects
      const response = await fetch('http://localhost:8081/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok && data.success) {
        console.log('Login successful:', data);

        // Show success message
        setMessage(`‚úÖ Login successful! Welcome ${data.data.name || 'User'}!`);

        // Store in localStorage manually
        localStorage.setItem('token', data.data.token);
        localStorage.setItem('user', JSON.stringify({
          id: data.data.id,
          email: data.data.email,
          name: data.data.name,
          role: data.data.role
        }));

        // Reset form
        setFormData({
          email: '',
          password: ''
        });
        setFormErrors({});

        // Simple redirect after 2 seconds
        setTimeout(() => {
          window.location.href = '/meal-planner';
        }, 2000);

      } else {
        console.log('Login failed:', data.message);
        setMessage(`‚ùå Login failed: ${data.message || 'Invalid credentials'}`);
      }
    } catch (error) {
      console.error('Login error:', error);
      setMessage('‚ùå Login failed. Please check if backend is running.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container component="main" maxWidth="sm">
      <Box
        sx={{
          minHeight: '100vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          py: 4,
        }}
      >
        <Paper
          elevation={10}
          sx={{
            p: 4,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            borderRadius: 3,
          }}
        >
          {/* Logo and Title */}
          <Box sx={{ textAlign: 'center', mb: 3 }}>
            <Typography variant="h3" color="primary.main" fontWeight="bold">
              üçΩÔ∏è MealBasket
            </Typography>
            <Typography variant="h5" color="text.secondary" sx={{ mt: 1 }}>
              Sign in to your account
            </Typography>
          </Box>

          {/* Message Display */}
          {message && (
            <Alert severity={message.includes('‚úÖ') ? 'success' : 'error'} sx={{ width: '100%', mb: 2 }}>
              {message}
            </Alert>
          )}

          {/* Error Display */}
          {Object.keys(formErrors).length > 0 && (
            <Alert severity="error" sx={{ width: '100%', mb: 2 }}>
              Please fix the errors below
            </Alert>
          )}

          {/* Simple Form - NO onSubmit, NO form element */}
          <Box sx={{ width: '100%' }}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
              value={formData.email}
              onChange={handleChange}
              error={!!formErrors.email}
              helperText={formErrors.email}
              InputProps={{
                startAdornment: <Email sx={{ mr: 1, color: 'action.active' }} />,
              }}
            />

            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
              value={formData.password}
              onChange={handleChange}
              error={!!formErrors.password}
              helperText={formErrors.password}
              InputProps={{
                startAdornment: <Lock sx={{ mr: 1, color: 'action.active' }} />,
              }}
            />

            {/* MANUAL LOGIN BUTTON */}
            <Button
              type="button"
              fullWidth
              variant="contained"
              size="large"
              disabled={loading}
              onClick={handleManualLogin}
              sx={{ mt: 3, mb: 2, py: 1.5 }}
            >
              {loading ? (
                <CircularProgress size={24} color="inherit" />
              ) : (
                'üîë Sign In'
              )}
            </Button>

            <Box sx={{ textAlign: 'center' }}>
              <Button
                type="button"
                onClick={() => window.location.href = '/register'}
                sx={{ textTransform: 'none' }}
              >
                <Typography variant="body2" color="primary.main">
                  Don't have an account? Sign Up
                </Typography>
              </Button>
            </Box>
          </Box>

          <Divider sx={{ width: '100%', my: 3 }}>
            <Typography variant="body2" color="text.secondary">
              DEMO ACCOUNTS
            </Typography>
          </Divider>

          {/* Demo Account Info */}
          <Alert
            severity="info"
            sx={{ mt: 3, width: '100%' }}
          >
            <Typography variant="body2">
              <strong>User:</strong> user@test.com / password123<br/>
              <strong>Vendor:</strong> vendor@test.com / vendor123<br/>
              <strong>Admin:</strong> admin@test.com / admin123
            </Typography>
          </Alert>
        </Paper>
      </Box>
    </Container>
  );
};

export default SimpleLogin;
