import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  Button,
  Avatar,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  Alert,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Tabs,
  Tab,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  AppBar,
  Toolbar,
  IconButton,
  Menu,
  MenuItem,
  LinearProgress,
} from '@mui/material';
import {
  Person,
  ShoppingCart,
  Restaurant,
  Assessment,
  Favorite,
  History,
  LocalOffer,
  TrendingUp,
  AttachMoney,
  Logout,
  Add,
  Edit,
  Delete,
  Visibility,
  CheckCircle,
  Pending,
  Cancel,
  ArrowBack,
  Home,
  AccountCircle,
  Store,
} from '@mui/icons-material';

const SimpleVendorDashboard = () => {
  const [vendorData, setVendorData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentView, setCurrentView] = useState('dashboard'); // dashboard, products, orders, analytics, promotions
  const [anchorEl, setAnchorEl] = useState(null);

  // Use shared ProductService
  const [products, setProducts] = useState([
    {
      id: 1,
      name: 'Classic Burger',
      vendor: 'My Restaurant',
      vendorId: 1,
      price: 12.99,
      category: 'Main Course',
      status: 'Available',
      orders: 45,
      description: 'Juicy beef patty with fresh vegetables and special sauce',
      rating: 4.5,
      inStock: true,
      image: 'https://via.placeholder.com/300x200/FF6B6B/FFFFFF?text=Classic+Burger',
      ingredients: 'Beef patty, lettuce, tomato, onion, pickles, special sauce, sesame bun',
      calories: 550,
      prepTime: '15 mins'
    },
    {
      id: 2,
      name: 'French Fries',
      vendor: 'My Restaurant',
      vendorId: 1,
      price: 4.99,
      category: 'Side Dish',
      status: 'Available',
      orders: 89,
      description: 'Crispy golden french fries with sea salt',
      rating: 4.2,
      inStock: true,
      image: 'https://via.placeholder.com/300x200/FF9800/FFFFFF?text=French+Fries',
      ingredients: 'Potatoes, sea salt, vegetable oil',
      calories: 320,
      prepTime: '10 mins'
    },
    {
      id: 3,
      name: 'Caesar Salad',
      vendor: 'My Restaurant',
      vendorId: 1,
      price: 8.99,
      category: 'Salad',
      status: 'Available',
      orders: 23,
      description: 'Fresh romaine lettuce, parmesan, croutons, and Caesar dressing',
      rating: 4.0,
      inStock: true,
      image: 'https://via.placeholder.com/300x200/4CAF50/FFFFFF?text=Caesar+Salad',
      ingredients: 'Romaine lettuce, parmesan cheese, croutons, Caesar dressing',
      calories: 180,
      prepTime: '10 mins'
    }
  ]);
  const [vendorId] = useState(1); // This vendor's ID

  // Dialog states
  const [productDialogOpen, setProductDialogOpen] = useState(false);
  const [orderDialogOpen, setOrderDialogOpen] = useState(false);
  const [analyticsDialogOpen, setAnalyticsDialogOpen] = useState(false);
  const [promotionDialogOpen, setPromotionDialogOpen] = useState(false);
  const [activeTab, setActiveTab] = useState(0);

  // Initialize products from service and subscribe to updates
  useEffect(() => {
    console.log('SimpleVendorDashboard - Products loaded:', products.length);
  }, [vendorData]);

  // Sample data
  const [orders, setOrders] = useState([
    { id: 1234, customer: 'John Doe', items: 'Burger, Fries, Coke', total: 15.99, status: 'Pending', time: '2 mins ago' },
    { id: 1233, customer: 'Jane Smith', items: 'Pizza, Salad', total: 22.50, status: 'Preparing', time: '5 mins ago' },
    { id: 1232, customer: 'Bob Johnson', items: 'Sandwich, Juice', total: 8.75, status: 'Ready', time: '8 mins ago' },
    { id: 1231, customer: 'Alice Brown', items: 'Pasta, Bread', total: 18.25, status: 'Delivered', time: '12 mins ago' },
  ]);

  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    category: '',
    description: ''
  });

  useEffect(() => {
    // Get vendor data from localStorage
    const user = localStorage.getItem('user');
    const token = localStorage.getItem('token');

    if (!user || !token) {
      setError('Please login first');
      setLoading(false);
      return;
    }

    try {
      const parsedUser = JSON.parse(user);
      setVendorData(parsedUser);
      setLoading(false);
    } catch (err) {
      setError('Error loading user data');
      setLoading(false);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  // Navigation Functions
