  // Product Management Functions with real-time sync
  const handleAddProduct = () => {
    const productName = prompt('Enter product name:');
    const productPrice = prompt('Enter price (e.g., 12.99):');
    const productCategory = prompt('Enter category (e.g., Main Course):');
    const productDescription = prompt('Enter product description:');

    if (productName && productPrice && productCategory && productDescription) {
      const product = {
        id: Date.now(),
        name: productName,
        vendor: 'My Restaurant',
        vendorId: 1,
        price: parseFloat(productPrice),
        category: productCategory,
        description: productDescription,
        rating: 4.0,
        inStock: true,
        image: `https://via.placeholder.com/300x200/FF6B6B/FFFFFF?text=${encodeURIComponent(productName)}`,
        ingredients: 'Fresh ingredients',
        calories: 300,
        prepTime: '15 mins'
      };

      // Add to local state
      setProducts([...products, product]);

      // Store in localStorage for user dashboard to see
      const existingProducts = JSON.parse(localStorage.getItem('vendorProducts') || '[]');
      existingProducts.push(product);
      localStorage.setItem('vendorProducts', JSON.stringify(existingProducts));

      // Notify user dashboard
      window.postMessage({
        type: 'PRODUCT_ADDED',
        product: product
      }, '*');

      alert(`Product "${productName}" added successfully! It will appear in user dashboard.`);
    } else {
      alert('Please fill in all fields');
    }
  };

  const handleEditProduct = (productId) => {
    const product = products.find(p => p.id === productId);
    if (!product) return;

    const newName = prompt('Update product name:', product.name);
    const newPrice = prompt('Update price:', product.price);
    const newCategory = prompt('Update category:', product.category);
    const newDescription = prompt('Update description:', product.description || '');

    if (newName && newPrice && newCategory) {
      // Update local state
      const updatedProduct = {
        ...product,
        name: newName,
        price: parseFloat(newPrice),
        category: newCategory,
        description: newDescription
      };

      setProducts(products.map(p => 
        p.id === productId 
          ? updatedProduct
          : p
      ));

      // Update localStorage for user dashboard to see
      const existingProducts = JSON.parse(localStorage.getItem('vendorProducts') || '[]');
      const updatedProducts = existingProducts.map(p => 
        p.id === productId ? updatedProduct : p
      );
      localStorage.setItem('vendorProducts', JSON.stringify(updatedProducts));

      // Notify user dashboard
      window.postMessage({
        type: 'PRODUCT_UPDATED',
        product: updatedProduct
