      // Remove from local state
      setProducts(products.filter(p => p.id !== productId));

      // Remove from localStorage for user dashboard to see
      const existingProducts = JSON.parse(localStorage.getItem('vendorProducts') || '[]');
      const filteredProducts = existingProducts.filter(p => p.id !== productId);
      localStorage.setItem('vendorProducts', JSON.stringify(filteredProducts));

      // Notify user dashboard
      window.postMessage({
        type: 'PRODUCT_DELETED',
        product: product
