import React, { useState, useEffect } from 'react';
import vendorService from '../services/vendorService';
import {
  Container,
  Paper,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  Button,
  Avatar,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  Alert,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Tabs,
  Tab,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  AppBar,
  Toolbar,
  IconButton,
  Menu,
  MenuItem,
  LinearProgress,
} from '@mui/material';
import {
  Person,
  ShoppingCart,
  Restaurant,
  Assessment,
  Favorite,
  History,
  LocalOffer,
  TrendingUp,
  AttachMoney,
  Logout,
  Add,
  Edit,
  Delete,
  Visibility,
  CheckCircle,
  Pending,
  Cancel,
  ArrowBack,
  Home,
  AccountCircle,
  Store,
} from '@mui/icons-material';

const SimpleVendorDashboard = () => {
  const [vendorData, setVendorData] = useState({
    id: 1,
    name: 'Vendor One',
    email: 'vendor1@test.com',
    rating: 0.0,
    totalRatings: 0,
    totalOrders: 0,
    revenue: 0.0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentView, setCurrentView] = useState('dashboard'); // dashboard, products, orders, analytics, promotions
  const [anchorEl, setAnchorEl] = useState(null);

  // Start with empty products - each vendor starts fresh
  const [products, setProducts] = useState([]);
  const [vendorId] = useState(vendorData.id); // This vendor's ID

  // Vendor switching function with fresh profiles
  const switchVendor = (vendorId) => {
    const vendors = {
      1: { 
        id: 1, 
        name: 'Vendor One', 
        email: 'vendor1@test.com',
        rating: 0.0,
        totalRatings: 0,
        totalOrders: 0,
        revenue: 0.0
      },
      2: { 
        id: 2, 
        name: 'Vendor Two', 
        email: 'vendor2@test.com',
        rating: 0.0,
        totalRatings: 0,
        totalOrders: 0,
        revenue: 0.0
      },
      3: { 
        id: 3, 
        name: 'Vendor Three', 
        email: 'vendor3@test.com',
        rating: 0.0,
        totalRatings: 0,
        totalOrders: 0,
        revenue: 0.0
      }
    };

    // Load vendor data from localStorage or use fresh data
    const storedVendorData = localStorage.getItem(`vendor_${vendorId}_profile`);
    const vendorProfile = storedVendorData ? JSON.parse(storedVendorData) : vendors[vendorId];

    setVendorData(vendorProfile);

    // Load the new vendor's products (or start empty)
    const allVendorProducts = JSON.parse(localStorage.getItem('allVendorProducts') || '{}');
    if (allVendorProducts[`vendor_${vendorId}`]) {
      setProducts(allVendorProducts[`vendor_${vendorId}`]);
    } else {
      setProducts([]); // Start empty for new vendor
    }
  };

  // Dialog states
  const [productDialogOpen, setProductDialogOpen] = useState(false);
  const [orderDialogOpen, setOrderDialogOpen] = useState(false);
  const [analyticsDialogOpen, setAnalyticsDialogOpen] = useState(false);
  const [promotionDialogOpen, setPromotionDialogOpen] = useState(false);
  const [activeTab, setActiveTab] = useState(0);

  // Initialize products from service and subscribe to updates
  useEffect(() => {
    const loadVendorData = async () => {
      try {
        console.log('Loading vendor data...');

        // Set current vendor from localStorage (logged-in user)
        const user = localStorage.getItem('user');
        if (user) {
          try {
            const parsedUser = JSON.parse(user);
            console.log('Logged in user:', parsedUser);

            // Set the current vendor for the service
            vendorService.setCurrentVendor(parsedUser.id);

            // Load vendor's products using service (strict isolation)
            const vendorProducts = await vendorService.getVendorProducts();
            setProducts(vendorProducts);

            // Load vendor profile and merge with logged-in user data
            const vendorProfile = await vendorService.getVendorProfile();

            // Ensure the logged-in user data takes priority
            const finalVendorData = {
              ...vendorProfile,
              id: parsedUser.id,
              name: parsedUser.name,
              email: parsedUser.email,
              role: parsedUser.role
            };

            setVendorData(finalVendorData);

            console.log('Vendor data loaded successfully:', { finalVendorData, products: vendorProducts });
            setLoading(false);

          } catch (err) {
            console.error('Error parsing user data:', err);
            setError('Error loading vendor data');
            setLoading(false);
          }
        } else {
          setError('No user found in localStorage. Please login again.');
          setLoading(false);
        }

      } catch (error) {
        console.error('Error loading vendor data:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    loadVendorData();

    // Listen for delete messages from user dashboard (only for this vendor)
    const handleMessage = (event) => {
      if (event.data && event.data.type === 'PRODUCT_DELETED' && event.data.vendorId === vendorData.id) {
        console.log('Vendor Dashboard received delete message:', event.data);
        // Update local state - only if it's this vendor's product
        setProducts(prev => prev.filter(p => p.id !== event.data.product.id));

        // Update vendor profile
        const updatedVendorData = {
          ...vendorData,
          totalProducts: Math.max(0, (vendorData.totalProducts || 0) - 1)
        };
        setVendorData(updatedVendorData);
        localStorage.setItem(`vendor_${vendorData.id}_profile`, JSON.stringify(updatedVendorData));
      }
    };

    window.addEventListener('message', handleMessage);

    return () => {
      window.removeEventListener('message', handleMessage);
    };
  }, []);

  // Sample data
  const [orders, setOrders] = useState([
    { id: 1234, customer: 'John Doe', items: 'Burger, Fries, Coke', total: 15.99, status: 'Pending', time: '2 mins ago' },
    { id: 1233, customer: 'Jane Smith', items: 'Pizza, Salad', total: 22.50, status: 'Preparing', time: '5 mins ago' },
    { id: 1232, customer: 'Bob Johnson', items: 'Sandwich, Juice', total: 8.75, status: 'Ready', time: '8 mins ago' },
    { id: 1231, customer: 'Alice Brown', items: 'Pasta, Bread', total: 18.25, status: 'Delivered', time: '12 mins ago' },
  ]);

  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    category: '',
    description: ''
  });

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  // Navigation Functions
  const handleBackToDashboard = () => {
    setCurrentView('dashboard');
  };

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const getViewTitle = () => {
    switch(currentView) {
      case 'dashboard': return 'Vendor Dashboard';
      case 'products': return 'Product Management';
      case 'orders': return 'Order Management';
      case 'analytics': return 'Analytics';
      case 'promotions': return 'Promotions';
      default: return 'Vendor Dashboard';
    }
  };

  // Product Management Functions using vendor service with strict isolation
  const handleAddProduct = async () => {
    const productName = prompt('Enter product name:');
    const productPrice = prompt('Enter price (e.g., 12.99):');
    const productCategory = prompt('Enter category (e.g., Main Course):');
    const productDescription = prompt('Enter product description:');

    if (productName && productPrice && productCategory && productDescription) {
      try {
        const productData = {
          name: productName,
          price: parseFloat(productPrice),
          category: productCategory,
          description: productDescription,
          vendor: vendorData.name,
          image: `https://via.placeholder.com/300x200/FF6B6B/FFFFFF?text=${encodeURIComponent(productName)}`,
          ingredients: 'Fresh ingredients',
          calories: 300,
          prepTime: '15 mins'
        };

        // Add product using vendor service (ensures vendor isolation)
        const newProduct = await vendorService.addProduct(productData);

        // Update local state
        setProducts([...products, newProduct]);

        // Update vendor profile
        const updatedVendorData = {
          ...vendorData,
          totalProducts: (vendorData.totalProducts || 0) + 1
        };
        setVendorData(updatedVendorData);
        await vendorService.updateVendorProfile(updatedVendorData);

        // Notify user dashboard
        window.postMessage({
          type: 'PRODUCT_ADDED',
          product: newProduct,
          vendorId: vendorData.id
        }, '*');

        alert(`Product "${productName}" added successfully! It will appear in user dashboard.`);

      } catch (error) {
        console.error('Error adding product:', error);
        alert(`Failed to add product: ${error.message}`);
      }
    } else {
      alert('Please fill in all fields');
    }
  };

  const handleEditProduct = async (productId) => {
    const product = products.find(p => p.id === productId);
    if (!product) return;

    const newName = prompt('Update product name:', product.name);
    const newPrice = prompt('Update price:', product.price);
    const newCategory = prompt('Update category:', product.category);
    const newDescription = prompt('Update description:', product.description || '');

    if (newName && newPrice && newCategory) {
      try {
        const productData = {
          name: newName,
          price: parseFloat(newPrice),
          category: newCategory,
          description: newDescription
        };

        // Update product using vendor service (ensures vendor ownership)
        const updatedProduct = await vendorService.updateProduct(productId, productData);

        // Update local state
        setProducts(products.map(p => 
          p.id === productId ? updatedProduct : p
        ));

        // Notify user dashboard
        window.postMessage({
          type: 'PRODUCT_UPDATED',
          product: updatedProduct,
          vendorId: vendorData.id
        }, '*');

        alert(`Product "${newName}" updated successfully!`);

      } catch (error) {
        console.error('Error updating product:', error);
        alert(`Failed to update product: ${error.message}`);
      }
    } else {
      alert('Please fill in all required fields');
    }
  };

  const handleDeleteProduct = async (productId) => {
    const product = products.find(p => p.id === productId);
    if (!product) return;

    if (confirm(`Are you sure you want to delete "${product.name}"?`)) {
      try {
        // Delete product using vendor service (ensures vendor ownership)
        const deletedProduct = await vendorService.deleteProduct(productId);

        // Update local state
        setProducts(products.filter(p => p.id !== productId));

        // Update vendor profile
        const updatedVendorData = {
          ...vendorData,
          totalProducts: Math.max(0, (vendorData.totalProducts || 0) - 1)
        };
        setVendorData(updatedVendorData);
        await vendorService.updateVendorProfile(updatedVendorData);

        // Notify user dashboard
        window.postMessage({
          type: 'PRODUCT_DELETED',
          product: deletedProduct,
          vendorId: vendorData.id
        }, '*');

        alert(`Product "${product.name}" deleted successfully!`);

      } catch (error) {
        console.error('Error deleting product:', error);
        alert(`Failed to delete product: ${error.message}`);
      }
    }
  };

  const handleToggleProductStatus = async (productId) => {
    try {
      const product = products.find(p => p.id === productId);
      if (!product) return;

      const updatedProduct = {
        ...product,
        inStock: !product.inStock
      };

      // Update product using vendor service
      const result = await vendorService.updateProduct(productId, { inStock: updatedProduct.inStock });

      // Update local state
      setProducts(products.map(p => 
        p.id === productId ? result : p
      ));

      // Notify user dashboard
      window.postMessage({
        type: 'PRODUCT_UPDATED',
        product: result,
        vendorId: vendorData.id
      }, '*');

      alert(`Product "${result.name}" is now ${result.inStock ? 'Available' : 'Unavailable'}!`);

    } catch (error) {
      console.error('Error toggling product status:', error);
      alert(`Failed to update product status: ${error.message}`);
    }
  };

  const handleClearAllProducts = () => {
    if (confirm('Are you sure you want to delete ALL products? This will clear everything!')) {
      // Clear local state
      setProducts([]);

      // Clear vendor-specific localStorage
      const allVendorProducts = JSON.parse(localStorage.getItem('allVendorProducts') || '{}');
      allVendorProducts[`vendor_${vendorData.id}`] = [];
      localStorage.setItem('allVendorProducts', JSON.stringify(allVendorProducts));

      // Notify user dashboard
      window.postMessage({
        type: 'VENDOR_PRODUCTS_DELETED',
        vendorId: vendorData.id
      }, '*');

      alert('All products deleted! User dashboard updated.');
    }
  };

  const handleProductManagement = () => {
    if (products.length === 0) {
      alert('No products available. Add a product first!');
      return;
    }

    const productOptions = products.map((product, index) => 
      `${index + 1}. ${product.name} - $${product.price} - ${product.inStock ? 'In Stock' : 'Out of Stock'}`
    ).join('\n');

    const choice = prompt(`Select a product to manage:\n\n${productOptions}\n\nEnter product number (1-${products.length}):`);

    if (choice) {
      const index = parseInt(choice) - 1;
      if (index >= 0 && index < products.length) {
        const selectedProduct = products[index];
        const action = prompt(`Manage "${selectedProduct.name}":\n\n1. Update Product\n2. Delete Product\n3. Toggle Status\n\nEnter action number (1-3):`);

        switch (action) {
          case '1':
            handleEditProduct(selectedProduct.id);
            break;
          case '2':
            handleDeleteProduct(selectedProduct.id);
            break;
          case '3':
            handleToggleProductStatus(selectedProduct.id);
            break;
          default:
            alert('Invalid action. Please try again.');
        }
      } else {
        alert('Invalid selection. Please try again.');
      }
    }
  };

  const handleViewProducts = () => {
    setCurrentView('products');
  };

  // Order Management Functions
  const handleUpdateOrderStatus = (orderId, newStatus) => {
    setOrders(orders.map(o => 
      o.id === orderId 
        ? { ...o, status: newStatus }
        : o
    ));
    alert(`Order #${orderId} status updated to ${newStatus}!`);
  };

  const handleViewOrderDetails = (order) => {
    alert(`Order Details:\n\nOrder ID: #${order.id}\nCustomer: ${order.customer}\nItems: ${order.items}\nTotal: $${order.total}\nStatus: ${order.status}\nTime: ${order.time}`);
  };

  const handleViewOrders = () => {
    setCurrentView('orders');
  };

  // Analytics Functions
  const handleViewAnalytics = () => {
    setCurrentView('analytics');
  };

  // Promotion Functions
  const handleCreatePromotion = () => {
    alert('ðŸŽ‰ Promotion Created!\n\nPromotion: 20% OFF on all burgers\nValid: Next 7 days\nCode: BURGER20\nStatus: Active');
    setPromotionDialogOpen(false);
  };

  const handleViewPromotions = () => {
    setCurrentView('promotions');
  };

  if (loading) {
    return (
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
          <CircularProgress size={60} />
        </Box>
      </Container>
    );
  }

  if (error) {
    return (
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Alert severity="error">{error}</Alert>
        <Button
          variant="contained"
          onClick={() => window.location.href = '/login'}
          sx={{ mt: 2 }}
        >
          Go to Login
        </Button>
      </Container>
    );
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      {/* Navbar */}
      <AppBar position="static" sx={{ mb: 3 }}>
        <Toolbar>
          {currentView !== 'dashboard' && (
            <IconButton
              edge="start"
              color="inherit"
              onClick={handleBackToDashboard}
              sx={{ mr: 2 }}
            >
              <ArrowBack />
            </IconButton>
          )}

          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            {getViewTitle()}
          </Typography>

          <IconButton
            edge="end"
            color="inherit"
            onClick={handleMenuOpen}
          >
            <AccountCircle />
          </IconButton>

          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={handleMenuClose}
          >
            <MenuItem onClick={handleMenuClose}>
              <ListItemIcon><Store /></ListItemIcon>
              <ListItemText>{vendorData?.name || 'Vendor'}</ListItemText>
            </MenuItem>
            <MenuItem onClick={() => { handleMenuClose(); handleLogout(); }}>
              <ListItemIcon><Logout /></ListItemIcon>
              <ListItemText>Logout</ListItemText>
            </MenuItem>
          </Menu>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ py: 2 }}>
        {/* Dashboard View */}
        {currentView === 'dashboard' && (
          <>
            {/* Header */}
            <Box sx={{ mb: 4, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <Avatar sx={{ bgcolor: 'primary.main', mr: 2 }}>
                  <Store />
                </Avatar>
                <Box>
                  <Typography variant="h6" color="text.secondary">
                    Welcome, {vendorData?.name || 'Vendor'}!
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Manage your restaurant business efficiently
                  </Typography>
                </Box>
              </Box>
            </Box>

            {/* Vendor Stats Cards */}
            <Grid container spacing={3} sx={{ mb: 4 }}>
              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'success.main', mr: 2 }}>
                        <Restaurant />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          24
                        </Typography>
                        <Typography color="text.secondary">
                          Total Products
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'info.main', mr: 2 }}>
                        <Assessment />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          18
                        </Typography>
                        <Typography color="text.secondary">
                          In Stock
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'warning.main', mr: 2 }}>
                        <TrendingUp />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          6
                        </Typography>
                        <Typography color="text.secondary">
                          Low Stock
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'primary.main', mr: 2 }}>
                        <ShoppingCart />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          156
                        </Typography>
                        <Typography color="text.secondary">
                          Total Orders
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>

            {/* Quick Actions */}
            <Grid container spacing={3} sx={{ mb: 4 }}>
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Vendor Actions
                  </Typography>
                  <List>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1, mb: 1 }}
                      onClick={handleAddProduct}
                    >
                      <ListItemIcon>
                        <Restaurant color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="Create Product" 
                        secondary="Add new food items to your menu" 
                      />
                    </ListItem>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1, mb: 1 }}
                      onClick={handleProductManagement}
                    >
                      <ListItemIcon>
                        <Assessment color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="Manage Products" 
                        secondary={`Update, delete, or toggle status for ${products.length} products`} 
                      />
                    </ListItem>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1, mb: 1 }}
                      onClick={() => setCurrentView('orders')}
                    >
                      <ListItemIcon>
                        <ShoppingCart color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="View Orders" 
                        secondary="Manage customer orders and fulfillment" 
                      />
                    </ListItem>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1 }}
                      onClick={() => setCurrentView('analytics')}
                    >
                      <ListItemIcon>
                        <TrendingUp color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="View Analytics" 
                        secondary="Track sales performance and revenue" 
                      />
                    </ListItem>
                  </List>
                </Paper>
              </Grid>

              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Recent Orders
                  </Typography>
                  <List>
                    {orders.slice(0, 4).map((order) => (
                      <ListItem key={order.id} sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                        <ListItemText 
                          primary={`Order #${order.id}`}
                          secondary={`${order.customer} â€¢ ${order.items} â€¢ $${order.total}`}
                        />
                      </ListItem>
                    ))}
                  </List>
                  <Button variant="outlined" fullWidth sx={{ mt: 2 }} onClick={() => setCurrentView('orders')}>
                    View All Orders
                  </Button>
                </Paper>
              </Grid>
            </Grid>

            {/* Top Products and Customer Insights */}
            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3, mb: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 2 }}>
                    Debug Information
                  </Typography>
                  <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                    <Typography variant="body2" color="primary.main">
                      Logged-in Vendor ID: {vendorData?.id || 'Not loaded'}
                    </Typography>
                    <Typography variant="body2" color="primary.main">
                      Logged-in Vendor Name: {vendorData?.name || 'Not loaded'}
                    </Typography>
                    <Typography variant="body2" color="primary.main">
                      Logged-in Vendor Email: {vendorData?.email || 'Not loaded'}
                    </Typography>
                    <Typography variant="body2" color="success.main">
                      Products Found: {products.length}
                    </Typography>
                    <Typography variant="body2" color="info.main">
                      localStorage user: {localStorage.getItem('user') ? 'Present' : 'Missing'}
                    </Typography>
                  </Box>
                </Paper>

                <Paper sx={{ p: 3, mb: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 2 }}>
                    Switch Vendor Account (For Testing)
                  </Typography>
                  <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                    <Button 
                      variant={vendorData.id === 1 ? "contained" : "outlined"}
                      size="small"
                      onClick={() => switchVendor(1)}
                    >
                      Vendor One
                    </Button>
                    <Button 
                      variant={vendorData.id === 2 ? "contained" : "outlined"}
                      size="small"
                      onClick={() => switchVendor(2)}
                    >
                      Vendor Two
                    </Button>
                    <Button 
                      variant={vendorData.id === 3 ? "contained" : "outlined"}
                      size="small"
                      onClick={() => switchVendor(3)}
                    >
                      Vendor Three
                    </Button>
                  </Box>
                  <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
                    Current: {vendorData.name} ({vendorData.email}) - {products.length} products
                  </Typography>
                  <Box sx={{ mt: 2, display: 'flex', gap: 2, flexWrap: 'wrap' }}>
                    <Chip label={`Rating: ${vendorData.rating || 0.0} â­`} size="small" color="primary" />
                    <Chip label={`Orders: ${vendorData.totalOrders || 0}`} size="small" color="success" />
                    <Chip label={`Revenue: $${(vendorData.revenue || 0).toFixed(2)}`} size="small" color="warning" />
                  </Box>
                </Paper>

                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Top Products
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    Debug: Found {products.length} products for vendor {vendorData?.id || 'N/A'}
                  </Typography>
                  <List>
                    {products.slice(0, 5).map((product) => (
                      <ListItem key={product.id} sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                        <ListItemText 
                          primary={product.name}
                          secondary={`${product.category} â€¢ $${product.price} â€¢ ${product.orders} orders`}
                        />
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                          <Typography variant="body2" color="success.main">
                            â­ {product.rating || '4.5'}
                          </Typography>
                        </Box>
                      </ListItem>
                    ))}
                  </List>
                  {products.length === 0 && (
                    <Box sx={{ textAlign: 'center', py: 4 }}>
                      <Typography variant="body1" color="text.secondary">
                        No products available.
                      </Typography>
                      <Button variant="contained" sx={{ mt: 2 }} onClick={() => {
                        console.log('Vendor Dashboard - Manual refresh - checking products...');
                        console.log('Vendor Dashboard - Manual refresh products:', products);
                        // Products are already loaded in state
                      }}>
                        Refresh Products
                      </Button>
                    </Box>
                  )}
                  <Button 
                    variant="outlined" 
                    fullWidth 
                    sx={{ mt: 2 }}
                    onClick={handleViewProducts}
                  >
                    View All Products ({products.length})
                  </Button>
                  <Button 
                    variant="contained" 
                    color="error"
                    fullWidth 
                    sx={{ mt: 1 }}
                    onClick={handleClearAllProducts}
                  >
                    Clear All Products
                  </Button>
                </Paper>
              </Grid>

              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Stock Insights
                  </Typography>
                  <List>
                    <ListItem sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                      <ListItemText 
                        primary="Stock Health"
                        secondary="Overall inventory status"
                      />
                      <Typography variant="h6" color="success.main">
                        75% Good
                      </Typography>
                    </ListItem>
                    <ListItem sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                      <ListItemText 
                        primary="Stock Alerts"
                        secondary="Items needing attention"
                      />
                      <Typography variant="h6" color="warning.main">
                        6 Items
                      </Typography>
                    </ListItem>
                    <ListItem sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                      <ListItemText 
                        primary="Prediction Accuracy"
                        secondary="AI forecast precision"
                      />
                      <Typography variant="h6" color="info.main">
                        92% â­
                      </Typography>
                    </ListItem>
                    <ListItem sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                      <ListItemText 
                        primary="Restock Needed"
                        secondary="Items to reorder soon"
                      />
                      <Typography variant="h6" color="error.main">
                        3 Items
                      </Typography>
                    </ListItem>
                  </List>
                  <Button 
                    variant="contained" 
                    fullWidth 
                    sx={{ mt: 2 }}
                    onClick={handleViewAnalytics}
                  >
                    View Stock Analytics
                  </Button>
                </Paper>
              </Grid>
            </Grid>

            {/* Performance Progress */}
            <Paper sx={{ p: 3, mt: 4 }}>
              <Typography variant="h6" fontWeight="bold" sx={{ mb: 2 }}>
                Monthly Performance
              </Typography>
              <Box sx={{ mb: 2 }}>
                <Typography variant="body2" color="text.secondary">
                  Revenue Goal: $12,450 / $15,000
                </Typography>
                <LinearProgress 
                  variant="determinate" 
                  value={(12450 / 15000) * 100} 
                  sx={{ mt: 1 }}
                />
              </Box>
              <Typography variant="body2" color="primary.main">
                ðŸŽ¯ You're $2,550 away from your monthly revenue goal!
              </Typography>
            </Paper>

            {/* Account Info */}
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                Account Information
              </Typography>
              <Grid container spacing={3}>
                <Grid item xs={12} md={6}>
                  <Typography variant="body2" color="text.secondary">
                    Business Name
                  </Typography>
                  <Typography variant="body1" sx={{ mb: 2 }}>
                    {vendorData?.name || 'Vendor Business'}
                  </Typography>

                  <Typography variant="body2" color="text.secondary">
                    Email
                  </Typography>
                  <Typography variant="body1" sx={{ mb: 2 }}>
                    {vendorData?.email || 'vendor@example.com'}
                  </Typography>
                </Grid>

                <Grid item xs={12} md={6}>
                  <Typography variant="body2" color="text.secondary">
                    Role
                  </Typography>
                  <Typography variant="body1" sx={{ mb: 2 }}>
                    {vendorData?.role || 'VENDOR'}
                  </Typography>

                  <Typography variant="body2" color="text.secondary">
                    User ID
                  </Typography>
                  <Typography variant="body1">
                    {vendorData?.id || 'N/A'}
                  </Typography>
                </Grid>
              </Grid>
            </Paper>

            {/* Product Management Dialog */}
            <Dialog open={productDialogOpen} onClose={() => setProductDialogOpen(false)} maxWidth="md" fullWidth>
              <DialogTitle>Manage Products</DialogTitle>
              <DialogContent>
                <Tabs value={activeTab} onChange={(e, newValue) => setActiveTab(newValue)} sx={{ mb: 2 }}>
                  <Tab label="Add Product" />
                  <Tab label="Existing Products" />
                </Tabs>

                {activeTab === 0 && (
                  <Box sx={{ p: 2 }}>
                    <TextField
                      fullWidth
                      label="Product Name"
                      value={newProduct.name}
                      onChange={(e) => setNewProduct({...newProduct, name: e.target.value})}
                      sx={{ mb: 2 }}
                    />
                    <TextField
                      fullWidth
                      label="Price"
                      type="number"
                      value={newProduct.price}
                      onChange={(e) => setNewProduct({...newProduct, price: e.target.value})}
                      sx={{ mb: 2 }}
                    />
                    <TextField
                      fullWidth
                      label="Category"
                      value={newProduct.category}
                      onChange={(e) => setNewProduct({...newProduct, category: e.target.value})}
                      sx={{ mb: 2 }}
                    />
                    <TextField
                      fullWidth
                      label="Description"
                      multiline
                      rows={3}
                      value={newProduct.description}
                      onChange={(e) => setNewProduct({...newProduct, description: e.target.value})}
                      sx={{ mb: 2 }}
                    />
                    <Button variant="contained" onClick={handleAddProduct} startIcon={<Add />}>
                      Add Product
                    </Button>
                  </Box>
                )}

                {activeTab === 1 && (
                  <TableContainer>
                    <Table>
                      <TableHead>
                        <TableRow>
                          <TableCell>Product</TableCell>
                          <TableCell>Price</TableCell>
                          <TableCell>Category</TableCell>
                          <TableCell>Status</TableCell>
                          <TableCell>Orders</TableCell>
                          <TableCell>Actions</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {products.map((product) => (
                          <TableRow key={product.id}>
                            <TableCell>{product.name}</TableCell>
                            <TableCell>${product.price}</TableCell>
                            <TableCell>{product.category}</TableCell>
                            <TableCell>
                              <Chip 
                                label={product.status} 
                                color={product.status === 'Available' ? 'success' : 'error'}
                                size="small"
                              />
                            </TableCell>
                            <TableCell>{product.orders}</TableCell>
                            <TableCell>
                              <Button 
                                size="small" 
                                onClick={() => handleToggleProductStatus(product.id)}
                              >
                                {product.status === 'Available' ? 'Disable' : 'Enable'}
                              </Button>
                              <Button 
                                size="small" 
                                color="error"
                                onClick={() => handleDeleteProduct(product.id)}
                              >
                                <Delete />
                              </Button>
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                )}
              </DialogContent>
              <DialogActions>
                <Button onClick={() => setProductDialogOpen(false)}>Close</Button>
              </DialogActions>
            </Dialog>

            {/* Order Management Dialog */}
            <Dialog open={orderDialogOpen} onClose={() => setOrderDialogOpen(false)} maxWidth="lg" fullWidth>
              <DialogTitle>All Orders</DialogTitle>
              <DialogContent>
                <TableContainer>
                  <Table>
                    <TableHead>
                      <TableRow>
                        <TableCell>Order ID</TableCell>
                        <TableCell>Customer</TableCell>
                        <TableCell>Items</TableCell>
                        <TableCell>Total</TableCell>
                        <TableCell>Status</TableCell>
                        <TableCell>Time</TableCell>
                        <TableCell>Actions</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {orders.map((order) => (
                        <TableRow key={order.id}>
                          <TableCell>#{order.id}</TableCell>
                          <TableCell>{order.customer}</TableCell>
                          <TableCell>{order.items}</TableCell>
                          <TableCell>${order.total}</TableCell>
                          <TableCell>
                            <Chip 
                              label={order.status} 
                              color={
                                order.status === 'Delivered' ? 'success' :
                                order.status === 'Ready' ? 'info' :
                                order.status === 'Preparing' ? 'warning' : 'default'
                              }
                              size="small"
                            />
                          </TableCell>
                          <TableCell>{order.time}</TableCell>
                          <TableCell>
                            <Box sx={{ display: 'flex', gap: 1 }}>
                              <Button 
                                size="small" 
                                variant="outlined"
                                onClick={() => handleViewOrderDetails(order)}
                              >
                                <Visibility fontSize="small" />
                              </Button>
                              {order.status === 'Pending' && (
                                <Button 
                                  size="small" 
                                  variant="contained"
                                  color="success"
                                  onClick={() => handleUpdateOrderStatus(order.id, 'Preparing')}
                                >
                                  Accept
                                </Button>
                              )}
                              {order.status === 'Preparing' && (
                                <Button 
                                  size="small" 
                                  variant="contained"
                                  color="info"
                                  onClick={() => handleUpdateOrderStatus(order.id, 'Ready')}
                                >
                                  Ready
                                </Button>
                              )}
                              {order.status === 'Ready' && (
                                <Button 
                                  size="small" 
                                  variant="contained"
                                  color="success"
                                  onClick={() => handleUpdateOrderStatus(order.id, 'Delivered')}
                                >
                                  Deliver
                                </Button>
                              )}
                            </Box>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>
              </DialogContent>
              <DialogActions>
                <Button onClick={() => setOrderDialogOpen(false)}>Close</Button>
              </DialogActions>
            </Dialog>

            {/* Promotion Dialog */}
            <Dialog open={promotionDialogOpen} onClose={() => setPromotionDialogOpen(false)} maxWidth="sm" fullWidth>
              <DialogTitle>Create Promotion</DialogTitle>
              <DialogContent>
                <TextField
                  fullWidth
                  label="Promotion Name"
                  defaultValue="Weekend Special"
                  sx={{ mb: 2 }}
                />
                <TextField
                  fullWidth
                  label="Discount Percentage"
                  type="number"
                  defaultValue="20"
                  sx={{ mb: 2 }}
                />
                <TextField
                  fullWidth
                  label="Promotion Code"
                  defaultValue="WEEKEND20"
                  sx={{ mb: 2 }}
                />
                <TextField
                  fullWidth
                  label="Duration (days)"
                  type="number"
                  defaultValue="7"
                  sx={{ mb: 2 }}
                />
              </DialogContent>
              <DialogActions>
                <Button onClick={() => setPromotionDialogOpen(false)}>Cancel</Button>
                <Button variant="contained" onClick={handleCreatePromotion}>Create Promotion</Button>
              </DialogActions>
            </Dialog>
          </>
        )}
      </Container>
    </Box>
  );
};

export default SimpleVendorDashboard;
