import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Box,
  Alert,
  CircularProgress,
  Divider,
  useTheme,
} from '@mui/material';
import {
  Restaurant,
  Email,
  Lock,
  PersonAdd,
} from '@mui/icons-material';
import { useAuth } from '../contexts/AuthContext';

const Login = () => {
  const navigate = useNavigate();
  const theme = useTheme();
  const { login, loading, error, isAuthenticated, role } = useAuth();

  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const [formErrors, setFormErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  React.useEffect(() => {
    if (isAuthenticated) {
      // Redirect based on role if already authenticated
      switch (role) {
        case 'VENDOR':
          navigate('/vendor');
          break;
        case 'ADMIN':
          navigate('/admin/dashboard');
          break;
        case 'USER':
        default:
          navigate('/');
          break;
      }
    }
  }, [isAuthenticated, navigate, role]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));

    // Clear errors when user starts typing
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: '',
      }));
    }
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.email.trim()) {
      errors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = 'Email is invalid';
    }

    if (!formData.password.trim()) {
      errors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      errors.password = 'Password must be at least 6 characters';
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm() || isSubmitting) {
      return;
    }

    setIsSubmitting(true);
    setFormErrors({}); // Clear previous errors

    try {
      const result = await login(formData);

      if (result.success) {
        // Use role directly from response (more reliable than context)
        const userRole = result.data?.role || 'USER';
        const userData = result.data?.user || {};

        console.log('Login successful:', {
          role: userRole,
          userData: userData,
          email: formData.email
        });

        // Store additional user info in localStorage for dashboard display
        localStorage.setItem('userEmail', formData.email);
        localStorage.setItem('userName', userData.name || 'User');
        localStorage.setItem('userRole', userRole);

        setTimeout(() => {
          switch (userRole) {
            case 'VENDOR':
              console.log('Navigating to vendor dashboard');
              navigate('/vendor');
              break;
            case 'ADMIN':
              console.log('Navigating to admin dashboard');
              navigate('/admin/dashboard');
              break;
            case 'USER':
            default:
              console.log('Navigating to user dashboard');
              navigate('/');
              break;
          }
        }, 200); // Slightly longer delay to ensure auth context is updated
      } else {
        setFormErrors({ 
          submit: result.error || 'Invalid credentials' 
        });
      }
    } catch (error) {
      console.error('Login error:', error);
      setFormErrors({ 
        submit: error.response?.data?.message || error.response?.data?.error || 'Login failed' 
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Container component="main" maxWidth="sm">
      <Box
        sx={{
          minHeight: '100vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          py: 4,
        }}
      >
        <Paper
          elevation={10}
          sx={{
            p: 4,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            borderRadius: 3,
          }}
        >
          {/* Logo and Title */}
          <Box sx={{ mb: 3, textAlign: 'center' }}>
            <Restaurant sx={{ fontSize: 48, color: 'primary.main', mb: 1 }} />
            <Typography component="h1" variant="h4" gutterBottom>
              Welcome Back
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Sign in to your MealBasket account
            </Typography>
          </Box>

          {/* Error Alert */}
          {error && (
            <Alert severity="error" sx={{ width: '100%', mb: 2 }}>
              {error}
            </Alert>
          )}

          {/* Login Form */}
          <Box
            component="form"
            onSubmit={handleSubmit}
            sx={{ width: '100%' }}
          >
            <TextField
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
              value={formData.email}
              onChange={handleChange}
              error={!!formErrors.email}
              helperText={formErrors.email}
              InputProps={{
                startAdornment: <Email sx={{ mr: 1, color: 'action.active' }} />,
              }}
            />

            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
              value={formData.password}
              onChange={handleChange}
              error={!!formErrors.password}
              helperText={formErrors.password}
              InputProps={{
                startAdornment: <Lock sx={{ mr: 1, color: 'action.active' }} />,
              }}
            />

            <Button
              type="submit"
              fullWidth
              variant="contained"
              size="large"
              disabled={loading}
              sx={{ mt: 3, mb: 2, py: 1.5 }}
            >
              {loading ? (
                <CircularProgress size={24} color="inherit" />
              ) : (
                'Sign In'
              )}
            </Button>

            <Box sx={{ textAlign: 'center' }}>
              <Link to="/forgot-password" style={{ textDecoration: 'none' }}>
                <Typography variant="body2" color="primary.main">
                  Forgot your password?
                </Typography>
              </Link>
            </Box>
          </Box>

          <Divider sx={{ width: '100%', my: 3 }}>
            <Typography variant="body2" color="text.secondary">
              OR
            </Typography>
          </Divider>

          {/* Register Link */}
          <Box sx={{ textAlign: 'center', width: '100%' }}>
            <Typography variant="body2" color="text.secondary">
              Don't have an account?{' '}
              <Link to="/register" style={{ textDecoration: 'none' }}>
                <Typography
                  component="span"
                  variant="body2"
                  color="primary.main"
                  sx={{ fontWeight: 'bold', '&:hover': { textDecoration: 'underline' } }}
                >
                  Sign Up
                </Typography>
              </Link>
            </Typography>
          </Box>

          {/* Demo Account Info */}
          <Alert
            severity="info"
            sx={{ mt: 3, width: '100%' }}
            title="Demo Account"
          >
            <Typography variant="body2">
              <strong>Demo User:</strong> user@example.com / password123<br />
              <strong>Demo Admin:</strong> admin@example.com / admin123
            </Typography>
          </Alert>
        </Paper>
      </Box>
    </Container>
  );
};

export default Login;
