import axios from 'axios';

const API_BASE_URL = 'http://localhost:8081/api';

// ── Axios instance ────────────────────────────────────────────────────────────
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: { 'Content-Type': 'application/json' },
  timeout: 10000,
});

// ── Request interceptor — attach Bearer token ─────────────────────────────────
api.interceptors.request.use(
  (config) => {
    console.log(`[API] ${config.method?.toUpperCase()} ${config.url}`);

    const isPublicAsset =
      config.url?.includes('favicon.ico') ||
      config.url?.includes('manifest.json') ||
      config.url?.includes('logo') ||
      config.url?.endsWith('.png') ||
      config.url?.endsWith('.ico') ||
      config.url?.endsWith('.json');

    if (!isPublicAsset) {
      const token = localStorage.getItem('token');
      if (token) {
        config.headers.Authorization = `Bearer ${token}`;
      } else {
        console.warn(`[Auth] No token for: ${config.url}`);
      }
    }
    return config;
  },
  (error) => {
    console.error('[Request Error]', error);
    return Promise.reject(error);
  }
);

// ── Response interceptor — handle 401 and network errors ──────────────────────
api.interceptors.response.use(
  (response) => {
    console.log(`[API] ${response.status} ${response.config.url}`);
    return response;
  },
  (error) => {
    console.error('[API Error]', error.response?.status, error.config?.url, error.message);

    const skipRedirect =
      error.config?.url?.includes('favicon.ico') ||
      error.config?.url?.includes('manifest.json') ||
      !error.config?.url?.startsWith('/');

    if (error.response?.status === 401 && !skipRedirect) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }

    if (error.code === 'ECONNREFUSED' || error.code === 'ERR_NETWORK') {
      console.error('[Network] Backend not reachable at', API_BASE_URL);
    }

    return Promise.reject(error);
  }
);

// ─────────────────────────────────────────────────────────────────────────────
// Auth APIs
// ─────────────────────────────────────────────────────────────────────────────
export const authAPI = {
  login:           (credentials) => api.post('/auth/login', credentials),
  register:        (userData)    => api.post('/auth/register', userData),
  registerVendor:  (vendorData)  => api.post('/auth/register/vendor', vendorData),
  logout: () => {
    ['token', 'user', 'userEmail', 'userName', 'userRole', 'cart', 'favorites'].forEach(k =>
      localStorage.removeItem(k)
    );
    return Promise.resolve();
  },
  getCurrentUser: () => ({
    user:  JSON.parse(localStorage.getItem('user') || '{}'),
    token: localStorage.getItem('token'),
  }),
  updateProfile:   (userData)    => api.put('/auth/profile', userData),
  changePassword:  (data)        => api.put('/auth/change-password', data),
  forgotPassword:  (email)       => api.post('/auth/forgot-password', { email }),
  resetPassword:   (data)        => api.post('/auth/reset-password', data),
};

// ─────────────────────────────────────────────────────────────────────────────
// Product APIs
// ─────────────────────────────────────────────────────────────────────────────
export const productAPI = {
  getAll:       ()            => api.get('/products'),
  getById:      (id)          => api.get(`/products/${id}`),
  search:       (name)        => api.get(`/products/search?name=${encodeURIComponent(name)}`),
  getByVendor:  (vendorName)  => api.get(`/products/vendor/${vendorName}`),
  getByVendorId:(vendorId)    => api.get(`/products/vendor/${vendorId}`),
  create:       (product)     => api.post('/products', product),
  update:       (id, product) => api.put(`/products/${id}`, product),
  delete:       (id)          => api.delete(`/products/${id}`),
};

// ─────────────────────────────────────────────────────────────────────────────
// Order APIs
// ─────────────────────────────────────────────────────────────────────────────
export const orderAPI = {
  /** All orders (admin) */
  getAll:         ()          => api.get('/orders'),

  /** Single order */
  getById:        (id)        => api.get(`/orders/${id}`),

  /** Orders belonging to the logged-in USER */
  getUserOrders:  ()          => api.get('/orders/user'),

  /** Orders belonging to the logged-in VENDOR */
  getVendorOrders: ()         => api.get('/orders/vendor'),

  /** Create a new order */
  create:         (order)     => api.post('/orders', order),

  /** Delete an order */
  delete:         (id)        => api.delete(`/orders/${id}`),

  /**
   * Update order status (vendor action)
   * PATCH /api/orders/{id}/status  body: { status: "Preparing" }
   */
  updateStatus:   (id, status) => api.patch(`/orders/${id}/status`, { status }),

  /**
   * Cancel an order (user action)
   * PATCH /api/orders/{id}/cancel
   */
  cancel:         (id)        => api.patch(`/orders/${id}/cancel`),
};

// ─────────────────────────────────────────────────────────────────────────────
// User APIs
// ─────────────────────────────────────────────────────────────────────────────
export const userAPI = {
  getProfile:           ()              => api.get('/auth/profile'),
  updateProfile:        (userData)      => api.put('/auth/profile', userData),
  deleteAccount:        ()              => api.delete('/auth/account'),
  getUserOrders:        ()              => api.get('/orders/user'),
  updateUserPreferences:(preferences)   => api.put('/auth/preferences', preferences),
  getUserActivity:      ()              => api.get('/auth/activity'),
};

// ─────────────────────────────────────────────────────────────────────────────
// Vendor APIs  (scoped to the logged-in vendor via token)
// ─────────────────────────────────────────────────────────────────────────────
export const vendorAPI = {
  /** GET /api/vendor/profile  — profile of the currently logged-in vendor */
  getProfile:    ()                      => api.get('/vendor/profile'),

  /** GET /api/vendor/products */
  getProducts:   ()                      => api.get('/vendor/products'),

  /** POST /api/vendor/products */
  createProduct: (product)               => api.post('/vendor/products', product),

  /** PUT /api/vendor/products/{productId} */
  updateProduct: (productId, product)    => api.put(`/vendor/products/${productId}`, product),

  /** DELETE /api/vendor/products/{productId} */
  deleteProduct: (productId)             => api.delete(`/vendor/products/${productId}`),

  /** GET /api/vendor/orders — orders for this vendor */
  getOrders:     ()                      => api.get('/vendor/orders'),

  /** PATCH /api/vendor/orders/{id}/status */
  updateOrderStatus: (id, status)        => api.patch(`/vendor/orders/${id}/status`, { status }),

  /** GET /api/vendor/analytics */
  getAnalytics:  ()                      => api.get('/vendor/analytics'),
};

// ─────────────────────────────────────────────────────────────────────────────
// Promotion APIs
// ─────────────────────────────────────────────────────────────────────────────
export const promotionAPI = {
  /** GET /api/promotions/vendor — active vendor's promotions */
  getVendorPromotions: ()                => api.get('/promotions/vendor'),

  /** POST /api/promotions */
  create:              (promo)           => api.post('/promotions', promo),

  /**
   * PATCH /api/promotions/{id}
   * body: { active: false } or any partial update
   */
  update:              (id, data)        => api.patch(`/promotions/${id}`, data),

  /** DELETE /api/promotions/{id} */
  delete:              (id)              => api.delete(`/promotions/${id}`),
};

// ─────────────────────────────────────────────────────────────────────────────
// Meal Plan APIs
// ─────────────────────────────────────────────────────────────────────────────
export const mealPlanAPI = {
  getAll:              ()                => api.get('/meal-plans'),
  getUserPlans:        ()                => api.get('/meal-plans/user'),
  getPlansByDate:      (date)            => api.get(`/meal-plans/user/date/${date}`),
  getPlansByType:      (mealType)        => api.get(`/meal-plans/user/meal-type/${mealType}`),
  create:              (plan)            => api.post('/meal-plans', plan),
  update:              (id, plan)        => api.put(`/meal-plans/${id}`, plan),
  delete:              (id)              => api.delete(`/meal-plans/${id}`),
  getAIRecommendation: (mealType, date)  =>
    api.post(`/meal-plans/ai-recommendation?mealType=${mealType}&date=${date}`),
  getRecommended:      ()                => api.get('/meal-plans/recommended'),
};

// ─────────────────────────────────────────────────────────────────────────────
// Stock Alert APIs
// ─────────────────────────────────────────────────────────────────────────────
export const stockAlertAPI = {
  getAll:        ()                               => api.get('/stock-alerts'),
  getByVendor:   (vendorId)                       => api.get(`/stock-alerts/vendor/${vendorId}`),
  getByProduct:  (productId)                      => api.get(`/stock-alerts/product/${productId}`),
  getActive:     ()                               => api.get('/stock-alerts/active'),
  getCritical:   ()                               => api.get('/stock-alerts/critical'),
  create:        (alert)                          => api.post('/stock-alerts', alert),
  monitorStock:  (vendorId, productId, stock)     =>
    api.post(`/stock-alerts/monitor/${vendorId}/${productId}/${stock}`),
  predictStock:  (vendorId, productId, stock)     =>
    api.post(`/stock-alerts/predict/${vendorId}/${productId}/${stock}`),
  deactivate:    (alertId)                        => api.put(`/stock-alerts/deactivate/${alertId}`),
  delete:        (alertId)                        => api.delete(`/stock-alerts/${alertId}`),
};

// ─────────────────────────────────────────────────────────────────────────────
// System Performance APIs
// ─────────────────────────────────────────────────────────────────────────────
export const performanceAPI = {
  getAll:               ()               => api.get('/system-performance'),
  getByType:            (metricType)     => api.get(`/system-performance/type/${metricType}`),
  getCritical:          ()               => api.get('/system-performance/critical'),
  getRecent:            (hours)          => api.get(`/system-performance/recent/${hours}`),
  monitorResponseTime:  (ms)             =>
    api.post(`/system-performance/monitor/response-time?responseTimeMs=${ms}`),
  monitorErrorRate:     (rate)           =>
    api.post(`/system-performance/monitor/error-rate?errorPercentage=${rate}`),
  monitorOrderVolume:   (count)          =>
    api.post(`/system-performance/monitor/order-volume?orderCount=${count}`),
  monitorSystemLoad:    (cpu)            =>
    api.post(`/system-performance/monitor/system-load?cpuUsage=${cpu}`),
  healthCheck:          ()               => api.post('/system-performance/health-check'),
  generateDailyReport:  ()               => api.post('/system-performance/generate-daily-report'),
  getAnalytics:         (start, end)     => api.get(`/system-performance/analytics/${start}/${end}`),
};

// ─────────────────────────────────────────────────────────────────────────────
// Miscellaneous APIs
// ─────────────────────────────────────────────────────────────────────────────
export const contactAPI = {
  sendMessage:   (message) => api.post('/contact/message', message),
  getAllMessages: ()        => api.get('/contact/messages'),
};

export const dbTestAPI = {
  testConnectivity: () => api.get('/db-test/connectivity'),
  testTables:       () => api.get('/db-test/tables'),
  createTestData:   () => api.post('/db-test/create-test-data'),
};

export const testAPI = {
  healthCheck:    () => api.get('/test/health'),
  publicEndpoint: () => api.get('/test/public'),
};

export const setupAPI = {
  createTestUsers:    () => api.post('/setup/create-test-users'),
  getTestCredentials: () => api.get('/setup/test-credentials'),
};

export default api;
