import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container, Paper, Typography, Box, Grid, Card, CardContent, Button,
  Avatar, CircularProgress, Chip, Rating, IconButton, TextField,
  InputAdornment, Select, MenuItem, FormControl, InputLabel,
  Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
  Dialog, DialogTitle, DialogContent, DialogActions, Drawer, List,
  ListItem, ListItemButton, ListItemIcon, ListItemText, Divider, Badge,
  useTheme, useMediaQuery
} from '@mui/material';
import {
  Person, ShoppingCart, Restaurant, Search as SearchIcon,
  Add, Remove, Delete as DeleteIcon, Favorite, Dashboard,
  Menu as MenuIcon, Close as CloseIcon, Logout as LogoutIcon,
  Star, TrendingUp
} from '@mui/icons-material';

const API_BASE = "http://localhost:8081/api";

const SimpleUserDashboard = () => {
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentView, setCurrentView] = useState('dashboard');
  const [mobileDrawerOpen, setMobileDrawerOpen] = useState(false);

  const [vendorProducts, setVendorProducts] = useState([]);
  const [selectedProductId, setSelectedProductId] = useState(null);
  const [showProductDetail, setShowProductDetail] = useState(false);

  const [cartItems, setCartItems] = useState([]);
  const [favoriteItems, setFavoriteItems] = useState([]);

  const [searchQuery, setSearchQuery] = useState('');
  const [filterCategory, setFilterCategory] = useState('all');

  const imageFallback = 'https://via.placeholder.com/300x200?text=No+Image';

  // ================= AUTH =================
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) navigate('/login');
  }, [navigate]);

  // ================= LOAD USER =================
  useEffect(() => {
    const stored = localStorage.getItem('user');
    setUserData(stored ? JSON.parse(stored) : null);
    setLoading(false);
  }, []);

  // ================= LOAD PRODUCTS =================
  useEffect(() => {
    const loadProducts = async () => {
      try {
        const res = await fetch(`${API_BASE}/products`);
        const data = await res.json();
        const products = data.data || data || [];
        setVendorProducts(products);
      } catch (err) {
        console.error(err);
      }
    };
    loadProducts();
  }, []);

  // ================= LOAD CART =================
  const loadCart = async () => {
  try {
    const token = localStorage.getItem("token");

    const res = await fetch(`${API_BASE}/cart`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    const result = await res.json();

    console.log("Cart API response:", result);

    // ✅ IMPORTANT FIX
    const cartArray = Array.isArray(result)
      ? result
      : Array.isArray(result.data)
      ? result.data
      : [];

    setCartItems(cartArray);

  } catch (err) {
    console.error("Cart load error:", err);
    setCartItems([]); // prevent crash
  }
};

  // ================= LOAD FAVORITES =================
  const loadFavorites = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`${API_BASE}/favorites`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      setFavoriteItems(data || []);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    loadCart();
    loadFavorites();
  }, []);

  // ================= CART FUNCTIONS =================
  const handleAddToCart = async (product) => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`${API_BASE}/cart`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ productId: product.id, quantity: 1 })
      });
      loadCart();
    } catch (err) {
      console.error(err);
    }
  };

  const handleRemoveFromCart = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`${API_BASE}/cart/${id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` }
      });
      loadCart();
    } catch (err) {
      console.error(err);
    }
  };

  const handleUpdateCartQuantity = async (id, delta) => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`${API_BASE}/cart/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ delta })
      });
      loadCart();
    } catch (err) {
      console.error(err);
    }
  };

  const handleCheckout = async () => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`${API_BASE}/orders`, {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}` }
      });
      loadCart();
      alert("Order placed successfully!");
    } catch (err) {
      console.error(err);
    }
  };

  // ================= FAVORITES =================
  const handleAddToFavorites = async (product) => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`${API_BASE}/favorites`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ productId: product.id })
      });
      loadFavorites();
    } catch (err) {
      console.error(err);
    }
  };

  const handleRemoveFromFavorites = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`${API_BASE}/favorites/${id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` }
      });
      loadFavorites();
    } catch (err) {
      console.error(err);
    }
  };

  // ================= RATE PRODUCT =================
  const handleRateProduct = async (productId, rating) => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`${API_BASE}/products/${productId}/rate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ rating })
      });
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
        <CircularProgress size={64} />
      </Box>
    );
  }

  return (
    <Container>
      <Typography variant="h4" sx={{ mb: 3 }}>
        Welcome {userData?.name}
      </Typography>

      {/* PRODUCTS */}
      <Grid container spacing={3}>
        {vendorProducts.map(product => (
          <Grid item xs={12} md={4} key={product.id}>
            <Card>
              <CardContent>
                <Typography variant="h6">{product.name}</Typography>
                <Typography>NPR {product.price}</Typography>
                <Rating
                  value={product.rating || 0}
                  onChange={(_, v) => v && handleRateProduct(product.id, v)}
                />
                <Box sx={{ mt: 2 }}>
                  <Button onClick={() => handleAddToCart(product)}>Add to Cart</Button>
                  <Button onClick={() => handleAddToFavorites(product)}>Favorite</Button>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* CART */}
      <Box sx={{ mt: 5 }}>
        <Typography variant="h5">Cart</Typography>
        {Array.isArray(cartItems).map(item => (
          <Box key={item.id} sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
            <Typography>{item.name}</Typography>
            <IconButton onClick={() => handleUpdateCartQuantity(item.id, -1)}><Remove /></IconButton>
            <Typography>{item.quantity}</Typography>
            <IconButton onClick={() => handleUpdateCartQuantity(item.id, 1)}><Add /></IconButton>
            <IconButton onClick={() => handleRemoveFromCart(item.id)}><DeleteIcon /></IconButton>
          </Box>
        ))}
        <Button variant="contained" sx={{ mt: 2 }} onClick={handleCheckout}>
          Checkout
        </Button>
      </Box>
    </Container>
  );
};

export default SimpleUserDashboard;
