                            <Typography variant="body2" color="text.secondary">
                              <strong>Prep Time:</strong> {product.prepTime}
                            </Typography>
                          </Box>
                        </Box>
                      </Box>

                      {/* Ingredients Section */}
                      <Paper sx={{ p: 2, bgcolor: 'grey.50' }}>
                        <Typography variant="h6" fontWeight="bold" sx={{ mb: 2 }}>
                          Ingredients
                        </Typography>
                        <Typography variant="body2">
                          {product.ingredients}
                        </Typography>
                      </Paper>

                      {/* Action Buttons */}
                      <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', mt: 2 }}>
                        <Button 
                          variant="contained" 
                          size="large"
                          onClick={handleAddToCartFromDetail}
                          startIcon={<ShoppingCart />}
                        >
                          Add to Cart
                        </Button>
                        <Button 
                          variant="outlined" 
                          size="large"
                          onClick={handleCloseProductDetail}
                        >
                          Close
                        </Button>
                      </Box>
                    </Box>
                  );
                })()}
              </DialogContent>
              <DialogActions>
                <Button onClick={handleCloseProductDetail}>Close</Button>
                <Button onClick={handleAddToCartFromDetail} variant="contained" color="primary">
                  Add to Cart - NPR {product.price}
                </Button>
              </DialogActions>
            </>
          )}
        </Dialog>
      </Container>
    </Box>
  );
};

export default SimpleUserDashboard;
