import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container, Paper, Typography, Box, Grid, Card, CardContent, Button, Avatar, List, ListItem,
  ListItemText, ListItemIcon, Alert, CircularProgress, LinearProgress, Chip, Rating,
  AppBar, Toolbar, IconButton, Menu, MenuItem, Dialog, DialogTitle, DialogContent
} from '@mui/material';
import { Person, ShoppingCart, Restaurant, Assessment, Favorite, ArrowBack, Home, AccountCircle, Star } from '@mui/icons-material';

const SimpleUserDashboard = () => {
  const navigate = useNavigate();

  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentView, setCurrentView] = useState('dashboard'); // dashboard, meals, orders, favorites, profile
  const [anchorEl, setAnchorEl] = useState(null);

  const [vendorProducts, setVendorProducts] = useState([]);
  const [selectedProductId, setSelectedProductId] = useState(null);
  const [showProductDetail, setShowProductDetail] = useState(false);

  // **Fixed state issues**
  const [userStats, setUserStats] = useState({
    totalOrders: 0,
    totalSpent: 0.00,
    favoriteItems: 0,
    points: 0,
    memberSince: new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long' }),
    lastOrder: 'No orders yet',
    avgOrderValue: 0.00
  });

  const [recentOrders, setRecentOrders] = useState([]);
  const [favoriteItems, setFavoriteItems] = useState([]);
  const [recommendations, setRecommendations] = useState([]);

  // Load user from localStorage
  useEffect(() => {
    try {
      const user = localStorage.getItem('user');
      if (user) {
        setUserData(JSON.parse(user));
      } else {
        setUserData({ id: 1, name: 'Test User', email: 'user@test.com', role: 'USER' });
      }
    } catch (err) {
      console.error('Error loading user data:', err);
      setUserData({ id: 1, name: 'Test User', email: 'user@test.com', role: 'USER' });
    }
    setLoading(false);
  }, []);

  // Load products
  useEffect(() => {
    const loadProducts = async () => {
      try {
        const res = await fetch('http://localhost:8081/api/products');
        const data = await res.json();
        const safeProducts = Array.isArray(data?.data || data) ? data.data || data : [];
        const normalized = safeProducts.map(p => {
          const vendorName = p?.vendor?.shopName || p?.vendor?.name || p?.vendor || p?.vendorName;
          const vendorId = p?.vendor?.id || p?.vendorId;
          return { ...p, vendor: vendorName, vendorId };
        });
        setVendorProducts(normalized);
      } catch (err) {
        console.error('Error loading products:', err);
        setVendorProducts([]);
      }
    };
    loadProducts();
  }, []);

  // **Handlers**
  const handleRateProduct = (productId, rating) => {
    setVendorProducts(prev =>
      prev.map(p => p.id === productId ? { ...p, rating, totalRatings: (p.totalRatings || 0) + 1 } : p)
    );
    const product = vendorProducts.find(p => p.id === productId);
    alert(`You rated "${product.name}" ${rating} stars!`);
  };

  const handlePlaceOrder = (product) => {
    const quantity = prompt('Enter quantity:', '1');
    if (!quantity || isNaN(quantity) || parseInt(quantity) <= 0) return alert('Please enter a valid quantity');

    const orderQuantity = parseInt(quantity);
    const totalCost = product.price * orderQuantity;

    // Update userStats
    setUserStats(prev => ({
      ...prev,
      totalOrders: prev.totalOrders + 1,
      totalSpent: prev.totalSpent + totalCost,
      points: prev.points + Math.floor(totalCost),
      lastOrder: new Date().toLocaleDateString(),
      avgOrderValue: (prev.totalSpent + totalCost) / (prev.totalOrders + 1)
    }));

    // Update recentOrders
    setRecentOrders(prev => [
      { id: Date.now(), vendor: product.vendor, items: orderQuantity, total: totalCost, status: 'Pending' },
      ...prev
    ]);

    alert(`Order placed: ${orderQuantity} x "${product.name}" = NPR ${totalCost.toFixed(2)}`);
  };

  const handleAddToFavorites = (product) => {
    if (favoriteItems.some(item => item.id === product.id)) return alert('This item is already in your favorites!');
    setFavoriteItems(prev => [...prev, product]);
    setUserStats(prev => ({ ...prev, favoriteItems: prev.favoriteItems + 1 }));
    alert(`"${product.name}" added to favorites!`);
  };

  const handleRemoveFromFavorites = (itemId) => {
    const item = favoriteItems.find(i => i.id === itemId);
    if (!item) return;
    if (confirm(`Remove "${item.name}" from your favorites?`)) {
      setFavoriteItems(prev => prev.filter(i => i.id !== itemId));
      setUserStats(prev => ({ ...prev, favoriteItems: prev.favoriteItems - 1 }));
      alert(`"${item.name}" removed from favorites!`);
    }
  };

  const handleAddToCartFromDetail = () => {
    if (!userData) return navigate('/login');
    const product = vendorProducts.find(p => p.id === selectedProductId);
    if (product) alert(`Added "${product.name}" to cart! Total: NPR ${product.price}`);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/home';
  };

  const handleBackToDashboard = () => setCurrentView('dashboard');
  const handleMenuOpen = (e) => setAnchorEl(e.currentTarget);
  const handleMenuClose = () => setAnchorEl(null);
  const handleNavigateToMealPlanner = () => setCurrentView('meals');
  const handleViewOrderHistory = () => setCurrentView('orders');
  const handleViewFavorites = () => setCurrentView('favorites');
  const handleViewProfile = () => setCurrentView('profile');

  if (loading) {
    return (
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
          <CircularProgress size={60} />
        </Box>
      </Container>
    );
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      {/* AppBar */}
      <AppBar position="static" sx={{ mb: 3 }}>
        <Toolbar>
          {currentView !== 'dashboard' && (
            <IconButton edge="start" color="inherit" onClick={handleBackToDashboard} sx={{ mr: 2 }}>
              <ArrowBack />
            </IconButton>
          )}
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            {currentView === 'dashboard' ? 'User Dashboard' : currentView === 'meals' ? 'Browse Meals' : currentView === 'orders' ? 'Order History' : currentView === 'favorites' ? 'My Favorites' : 'Profile Settings'}
          </Typography>
          <IconButton edge="end" color="inherit" onClick={handleMenuOpen}>
            <AccountCircle />
          </IconButton>
          <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleMenuClose}>
            <MenuItem onClick={handleMenuClose}><ListItemIcon><Person /></ListItemIcon>{userData?.name}</MenuItem>
            <MenuItem onClick={() => { handleMenuClose(); handleLogout(); }}><ListItemIcon><Favorite /></ListItemIcon>Logout</MenuItem>
          </Menu>
        </Toolbar>
      </AppBar>

      {/* Rest of your component stays mostly the same, just use the fixed state variables */}
      {/* Example: favoriteItems.map(...) now works because it's state */}
    </Box>
  );
};

export default SimpleUserDashboard;
