import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Grid,
  Paper,
  Box,
  Avatar,
  Button,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Chip,
  CircularProgress,
  Alert,
  Tabs,
  Tab,
  Card,
  CardContent,
  LinearProgress,
  IconButton,
  ListItemSecondaryAction,
  Fade
} from '@mui/material';
import {
  People,
  Store,
  ShoppingCart,
  AttachMoney,
  TrendingUp,
  Person,
  Business,
  Email,
  Phone,
  LocationOn,
  AccessTime,
  Delete,
  Refresh,
  Dashboard,
  Assessment,
  Group,
  LocalMall,
  MonetizationOn,
  ShowChart
} from '@mui/icons-material';
import adminService from '../services/adminService';

const SimpleAdminDashboard = () => {
  const [adminData, setAdminData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentView, setCurrentView] = useState('dashboard'); // dashboard, users, vendors, analytics, settings
  const [anchorEl, setAnchorEl] = useState(null);

  // Admin data states
  const [users, setUsers] = useState([]);
  const [vendors, setVendors] = useState([]);
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalVendors: 0,
    totalProducts: 0,
    totalOrders: 0,
    totalRevenue: 0
  });

  // Real-time activity tracking states
  const [recentActivities, setRecentActivities] = useState([]);
  const [systemStatus, setSystemStatus] = useState({
    activeUsers: 0,
    onlineUsers: 0,
    totalSessions: 0,
    serverLoad: 45,
    uptime: '99.8%',
    lastBackup: new Date().toLocaleString(),
    systemVersion: 'v2.1.0'
  });
  const [userSessions, setUserSessions] = useState([]);

  useEffect(() => {
    // Get admin data from localStorage
    const user = localStorage.getItem('user');
    const token = localStorage.getItem('token');

    if (!user || !token) {
      setError('Please login first');
      setLoading(false);
      return;
    }

    try {
      const parsedUser = JSON.parse(user);
      if (parsedUser.role !== 'ADMIN') {
        setError('Access denied. Admin role required.');
        setLoading(false);
        return;
      }
      setAdminData(parsedUser);

      // Load admin data
      loadAdminData();
    } catch (err) {
      setError('Error loading user data');
      setLoading(false);
    }
  }, []);

  // Load admin dashboard data
  const loadAdminData = async () => {
    try {
      console.log('AdminDashboard: Loading admin data from backend...');
      setLoading(true);
      setError('');

      // Try to fetch data from backend API first
      let allUsers = [];
      let allVendorProducts = {};
      let stats = {};

      try {
        // Fetch users from backend
        const usersData = await adminService.getAllUsers();
        allUsers = usersData.data || usersData || [];
        console.log('AdminDashboard: Successfully fetched users from backend:', allUsers);

        // Fetch dashboard stats from backend
        const statsData = await adminService.getDashboardStats();
        stats = statsData.data || statsData || {};
        console.log('AdminDashboard: Successfully fetched stats from backend:', stats);

        // Fetch products from backend
        const productsData = await adminService.getAllProducts();
        allVendorProducts = productsData.data || productsData || {};
        console.log('AdminDashboard: Successfully fetched products from backend:', allVendorProducts);

      } catch (apiError) {
        console.log('AdminDashboard: Backend API not available, using localStorage fallback:', apiError);

        // Fallback to localStorage if backend is not available
