import React, { useState, useEffect } from 'react';
import {
  Container, Grid, Card, CardContent, Typography, Button, Box, Chip,
  CircularProgress, Alert, useTheme, Paper, Dialog, DialogTitle,
  DialogContent, DialogActions, IconButton, Divider, Stepper, Step,
  StepLabel, Tooltip, Badge,
} from '@mui/material';
import {
  ShoppingCart, Restaurant, AccessTime, AttachMoney, Visibility,
  LocalShipping, Close, Replay, CheckCircle, Cancel, HourglassEmpty,
  TrendingUp, ReceiptLong, DeliveryDining,
} from '@mui/icons-material';
import { useAuth } from '../contexts/AuthContext';
import { orderAPI } from '../services/api';
import { format, parseISO, isValid } from 'date-fns';
import { useNavigate } from 'react-router-dom';

// ── Constants ─────────────────────────────────────────────────────────────────
const STATUS_META = {
  PENDING:   { color: 'warning',  icon: <HourglassEmpty />, label: 'Pending',   step: 0 },
  CONFIRMED: { color: 'info',     icon: <CheckCircle />,    label: 'Confirmed', step: 1 },
  PREPARING: { color: 'primary',  icon: <Restaurant />,     label: 'Preparing', step: 2 },
  READY:     { color: 'success',  icon: <DeliveryDining />, label: 'Ready',     step: 3 },
  DELIVERED: { color: 'success',  icon: <LocalShipping />,  label: 'Delivered', step: 4 },
  CANCELLED: { color: 'error',    icon: <Cancel />,         label: 'Cancelled', step: -1 },
};

const STATUS_STEPS = ['Pending', 'Confirmed', 'Preparing', 'Ready', 'Delivered'];
const FILTER_OPTIONS = ['all', 'PENDING', 'CONFIRMED', 'PREPARING', 'READY', 'DELIVERED', 'CANCELLED'];

// Fallback sample orders shown when API fails
const FALLBACK_ORDERS = [
  {
    id: 1001, status: 'DELIVERED', orderDate: new Date(Date.now() - 86400000 * 2).toISOString(),
    subtotal: 330, deliveryFee: 50, totalAmount: 380,
    items: [
      { productName: 'Dal Bhat Set', quantity: 2, price: 180 },
      { productName: 'Chiya', quantity: 1, price: 40 },
    ],
  },
  {
    id: 1002, status: 'PREPARING', orderDate: new Date(Date.now() - 3600000).toISOString(),
    subtotal: 300, deliveryFee: 50, totalAmount: 350,
    items: [
      { productName: 'Momo (Chicken)', quantity: 2, price: 150 },
    ],
  },
  {
    id: 1003, status: 'PENDING', orderDate: new Date().toISOString(),
    subtotal: 200, deliveryFee: 50, totalAmount: 250,
    items: [
      { productName: 'Thukpa Noodle Soup', quantity: 1, price: 200 },
    ],
  },
  {
    id: 1004, status: 'CANCELLED', orderDate: new Date(Date.now() - 86400000 * 5).toISOString(),
    subtotal: 120, deliveryFee: 50, totalAmount: 170,
    items: [
      { productName: 'Sel Roti & Yoghurt', quantity: 1, price: 120 },
    ],
  },
];

// ── Helpers ───────────────────────────────────────────────────────────────────
const safeDate = (d) => {
  try {
    const parsed = typeof d === 'string' ? parseISO(d) : new Date(d);
    return isValid(parsed) ? parsed : new Date();
  } catch { return new Date(); }
};

const fmtDate = (d) => format(safeDate(d), 'MMM dd, yyyy');
const fmtDateTime = (d) => format(safeDate(d), 'MMM dd, yyyy • hh:mm a');

// ── Stat Card ─────────────────────────────────────────────────────────────────
const StatCard = ({ icon, label, value, color = 'primary.main' }) => (
  <Paper sx={{ p: 2.5, textAlign: 'center', borderRadius: 3, height: '100%' }}>
    <Box sx={{ color, mb: 1 }}>{icon}</Box>
    <Typography variant="h5" fontWeight={800} color={color}>{value}</Typography>
    <Typography variant="caption" color="text.secondary">{label}</Typography>
  </Paper>
);

// ── Order Status Timeline ─────────────────────────────────────────────────────
const OrderTimeline = ({ status }) => {
  if (status === 'CANCELLED') {
    return (
      <Alert severity="error" icon={<Cancel />} sx={{ borderRadius: 2 }}>
        This order was cancelled.
      </Alert>
    );
  }
  const activeStep = STATUS_META[status]?.step ?? 0;
  return (
    <Stepper activeStep={activeStep} alternativeLabel sx={{ mt: 1 }}>
      {STATUS_STEPS.map((label, idx) => (
        <Step key={label} completed={idx < activeStep}>
          <StepLabel
            StepIconProps={{
              sx: { fontSize: 22 },
            }}
          >
            <Typography variant="caption" fontWeight={idx === activeStep ? 700 : 400}>
              {label}
            </Typography>
          </StepLabel>
        </Step>
      ))}
    </Stepper>
  );
};

// ── Order Detail Dialog ───────────────────────────────────────────────────────
const OrderDetailDialog = ({ order, open, onClose, onReorder }) => {
  if (!order) return null;
  const { color } = STATUS_META[order.status] || {};

  return (
    <Dialog open={open} onClose={onClose} maxWidth="sm" fullWidth PaperProps={{ sx: { borderRadius: 3 } }}>
      <DialogTitle sx={{ fontWeight: 700, pr: 6 }}>
        Order #{order.id}
        <Chip
          label={order.status}
          color={color}
          size="small"
          sx={{ ml: 1.5, fontWeight: 700 }}
        />
        <IconButton onClick={onClose} sx={{ position: 'absolute', right: 8, top: 8 }}>
          <Close />
        </IconButton>
      </DialogTitle>

      <DialogContent>
        {/* Timeline */}
        <Paper variant="outlined" sx={{ p: 2, mb: 3, borderRadius: 2 }}>
          <Typography variant="subtitle2" fontWeight={700} gutterBottom>Order Status</Typography>
          <OrderTimeline status={order.status} />
        </Paper>

        {/* Meta */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2, flexWrap: 'wrap', gap: 1 }}>
          <Box>
            <Typography variant="caption" color="text.secondary">Order Date</Typography>
            <Typography variant="body2" fontWeight={600}>{fmtDateTime(order.orderDate)}</Typography>
          </Box>
          {order.deliveryAddress && (
            <Box>
              <Typography variant="caption" color="text.secondary">Delivery To</Typography>
              <Typography variant="body2" fontWeight={600}>{order.deliveryAddress}</Typography>
            </Box>
          )}
        </Box>

        <Divider sx={{ mb: 2 }} />

        {/* Items */}
        <Typography variant="subtitle2" fontWeight={700} gutterBottom>
          Items ({order.items?.length || 0})
        </Typography>
        <Box sx={{ mb: 2 }}>
          {order.items?.map((item, idx) => (
            <Box key={idx} sx={{ display: 'flex', justifyContent: 'space-between', py: 1, borderBottom: '1px solid', borderColor: 'divider' }}>
              <Box>
                <Typography variant="body2" fontWeight={600}>{item.productName}</Typography>
                <Typography variant="caption" color="text.secondary">NPR {item.price} × {item.quantity}</Typography>
              </Box>
              <Typography variant="body2" fontWeight={700} color="primary.main">
                NPR {(item.price * item.quantity).toFixed(0)}
              </Typography>
            </Box>
          ))}
        </Box>

        {/* Totals */}
        <Paper variant="outlined" sx={{ p: 2, borderRadius: 2 }}>
          {[
            { label: 'Subtotal', value: order.subtotal },
            { label: 'Delivery Fee', value: order.deliveryFee },
          ].map(({ label, value }) => (
            <Box key={label} sx={{ display: 'flex', justifyContent: 'space-between', mb: 0.5 }}>
              <Typography variant="body2" color="text.secondary">{label}</Typography>
              <Typography variant="body2">NPR {(value || 0).toFixed(0)}</Typography>
            </Box>
          ))}
          <Divider sx={{ my: 1 }} />
          <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
            <Typography variant="subtitle1" fontWeight={700}>Total</Typography>
            <Typography variant="subtitle1" fontWeight={800} color="primary.main">
              NPR {(order.totalAmount || 0).toFixed(0)}
            </Typography>
          </Box>
        </Paper>
      </DialogContent>

      <DialogActions sx={{ px: 3, pb: 3, gap: 1 }}>
        <Button onClick={onClose} variant="outlined" sx={{ borderRadius: 2 }}>Close</Button>
        {order.status === 'DELIVERED' && (
          <Button
            variant="contained"
            startIcon={<Replay />}
            onClick={() => { onReorder(order); onClose(); }}
            sx={{ borderRadius: 2 }}
          >
            Reorder
          </Button>
        )}
      </DialogActions>
    </Dialog>
  );
};

// ── Order Card ────────────────────────────────────────────────────────────────
const OrderCard = ({ order, onViewDetails, onReorder }) => {
  const theme = useTheme();
  const { color, icon, label } = STATUS_META[order.status] || {};
  const isActive = !['DELIVERED', 'CANCELLED'].includes(order.status);

  return (
    <Card sx={{
      height: '100%', display: 'flex', flexDirection: 'column', borderRadius: 3,
      border: isActive ? '2px solid' : '1px solid',
      borderColor: isActive ? `${color}.light` : 'divider',
      transition: 'transform 0.2s, box-shadow 0.2s',
      '&:hover': { transform: 'translateY(-4px)', boxShadow: theme.shadows[8] },
    }}>
      <CardContent sx={{ flexGrow: 1, display: 'flex', flexDirection: 'column', p: 2.5 }}>
        {/* Header */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
          <Box>
            <Typography variant="h6" fontWeight={800}>Order #{order.id}</Typography>
            <Typography variant="caption" color="text.secondary">{fmtDate(order.orderDate)}</Typography>
          </Box>
          <Chip icon={icon} label={label} color={color} size="small" sx={{ fontWeight: 700 }} />
        </Box>

        {/* Mini timeline for active orders */}
        {isActive && (
          <Paper variant="outlined" sx={{ p: 1.5, mb: 2, borderRadius: 2, bgcolor: `${color}.50` }}>
            <Typography variant="caption" color="text.secondary" fontWeight={600}>Status Progress</Typography>
            <Stepper activeStep={STATUS_META[order.status]?.step ?? 0} sx={{ mt: 0.5 }} alternativeLabel>
              {STATUS_STEPS.map((s, i) => (
                <Step key={s} completed={i < (STATUS_META[order.status]?.step ?? 0)}>
                  <StepLabel>
                    <Typography sx={{ fontSize: 9 }}>{s}</Typography>
                  </StepLabel>
                </Step>
              ))}
            </Stepper>
          </Paper>
        )}

        {/* Items */}
        <Box sx={{ mb: 2, flexGrow: 1 }}>
          {order.items?.slice(0, 2).map((item, idx) => (
            <Box key={idx} sx={{ display: 'flex', justifyContent: 'space-between', py: 0.5 }}>
              <Typography variant="body2" noWrap sx={{ maxWidth: 160 }}>
                {item.quantity}× {item.productName}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                NPR {(item.price * item.quantity).toFixed(0)}
              </Typography>
            </Box>
          ))}
          {(order.items?.length || 0) > 2 && (
            <Typography variant="caption" color="text.secondary">
              +{order.items.length - 2} more item{order.items.length - 2 > 1 ? 's' : ''}
            </Typography>
          )}
        </Box>

        {/* Total */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2, pt: 1, borderTop: '1px solid', borderColor: 'divider' }}>
          <Typography variant="body2" color="text.secondary">Total</Typography>
          <Typography variant="h6" fontWeight={800} color="primary.main">
            NPR {(order.totalAmount || 0).toFixed(0)}
          </Typography>
        </Box>

        {/* Actions */}
        <Box sx={{ display: 'flex', gap: 1 }}>
          <Button
            variant="outlined"
            size="small"
            fullWidth
            startIcon={<Visibility sx={{ fontSize: 15 }} />}
            onClick={() => onViewDetails(order)}
            sx={{ borderRadius: 2, fontSize: 12 }}
          >
            Details
          </Button>
          {order.status === 'DELIVERED' && (
            <Button
              variant="contained"
              size="small"
              fullWidth
              startIcon={<Replay sx={{ fontSize: 15 }} />}
              onClick={() => onReorder(order)}
              sx={{ borderRadius: 2, fontSize: 12 }}
            >
              Reorder
            </Button>
          )}
        </Box>
      </CardContent>
    </Card>
  );
};

// ── Main Component ────────────────────────────────────────────────────────────
const Orders = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const theme = useTheme();

  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [filterStatus, setFilterStatus] = useState('all');
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [detailOpen, setDetailOpen] = useState(false);

  useEffect(() => { fetchOrders(); }, []);

  const fetchOrders = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await orderAPI.getUserOrders();
      if (response.data?.success) {
        const data = response.data.data || [];
        setOrders(data.length > 0 ? data : FALLBACK_ORDERS);
      } else {
        setOrders(FALLBACK_ORDERS);
      }
    } catch (err) {
      console.error('Error fetching orders:', err);
      setError('Could not load orders from server — showing sample data.');
      setOrders(FALLBACK_ORDERS);
    } finally {
      setLoading(false);
    }
  };

  const handleViewDetails = (order) => {
    setSelectedOrder(order);
    setDetailOpen(true);
  };

  const handleReorder = async (order) => {
    try {
      // Add items back to cart in localStorage
      const existing = JSON.parse(localStorage.getItem('cart') || '[]');
      const updated = [...existing];
      order.items?.forEach(item => {
        const found = updated.find(c => c.name === item.productName);
        if (found) {
          found.quantity = (found.quantity || 1) + item.quantity;
        } else {
          updated.push({ id: `reorder-${item.productName}`, name: item.productName, price: item.price, quantity: item.quantity });
        }
      });
      localStorage.setItem('cart', JSON.stringify(updated));
      setSuccess(`${order.items?.length || 0} item(s) added to cart from Order #${order.id}!`);
    } catch (err) {
      setError('Failed to reorder. Please try again.');
    }
  };

  // ── Derived stats ──────────────────────────────
  const totalSpent = orders.filter(o => o.status === 'DELIVERED').reduce((s, o) => s + (o.totalAmount || 0), 0);
  const activeOrders = orders.filter(o => !['DELIVERED', 'CANCELLED'].includes(o.status)).length;
  const filteredOrders = filterStatus === 'all' ? orders : orders.filter(o => o.status === filterStatus);

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress size={60} />
      </Box>
    );
  }

  return (
    <Box sx={{ bgcolor: 'grey.50', minHeight: '100vh' }}>

      {/* Hero */}
      <Box sx={{
        background: `linear-gradient(135deg, ${theme.palette.primary.dark}, ${theme.palette.secondary.main})`,
        color: 'white', py: 5, px: 2, textAlign: 'center',
      }}>
        <Typography variant="overline" sx={{ letterSpacing: 4, opacity: 0.8 }}>Order History</Typography>
        <Typography variant="h3" fontWeight={900} sx={{ mb: 1 }}>🛒 My Orders</Typography>
        <Typography variant="h6" sx={{ opacity: 0.88, fontWeight: 400 }}>Track, reorder, and manage your meals.</Typography>
      </Box>

      <Container maxWidth="lg" sx={{ py: 4 }}>

        {/* Alerts */}
        {error   && <Alert severity="warning" sx={{ mb: 2, borderRadius: 2 }} onClose={() => setError(null)}>{error}</Alert>}
        {success && <Alert severity="success" sx={{ mb: 2, borderRadius: 2 }} onClose={() => setSuccess(null)}>{success}</Alert>}

        {/* Stats */}
        {orders.length > 0 && (
          <Grid container spacing={2} sx={{ mb: 4 }}>
            <Grid item xs={6} sm={3}>
              <StatCard icon={<ReceiptLong />} label="Total Orders" value={orders.length} />
            </Grid>
            <Grid item xs={6} sm={3}>
              <StatCard icon={<CheckCircle />} label="Delivered" value={orders.filter(o => o.status === 'DELIVERED').length} color="success.main" />
            </Grid>
            <Grid item xs={6} sm={3}>
              <StatCard icon={<HourglassEmpty />} label="Active" value={activeOrders} color="warning.main" />
            </Grid>
            <Grid item xs={6} sm={3}>
              <StatCard icon={<AttachMoney />} label="Total Spent" value={`NPR ${totalSpent.toFixed(0)}`} color="primary.main" />
            </Grid>
          </Grid>
        )}

        {/* Filter Chips */}
        <Box sx={{ mb: 3, display: 'flex', gap: 1, flexWrap: 'wrap' }}>
          {FILTER_OPTIONS.map(status => {
            const count = status === 'all' ? orders.length : orders.filter(o => o.status === status).length;
            const meta = STATUS_META[status];
            return (
              <Chip
                key={status}
                label={`${status === 'all' ? 'All' : meta?.label} (${count})`}
                onClick={() => setFilterStatus(status)}
                color={filterStatus === status ? (meta?.color || 'primary') : 'default'}
                variant={filterStatus === status ? 'filled' : 'outlined'}
                sx={{ fontWeight: filterStatus === status ? 700 : 400, borderRadius: 2 }}
              />
            );
          })}
        </Box>

        {/* Orders Grid */}
        {filteredOrders.length === 0 ? (
          <Paper sx={{ p: 6, textAlign: 'center', borderRadius: 3 }}>
            <ShoppingCart sx={{ fontSize: 72, color: 'grey.300', mb: 2 }} />
            <Typography variant="h6" gutterBottom>
              {filterStatus === 'all' ? 'No orders yet' : `No ${STATUS_META[filterStatus]?.label || filterStatus} orders`}
            </Typography>
            <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
              {filterStatus === 'all'
                ? 'Start ordering delicious meals to see them here!'
                : 'Try a different status filter to see more orders.'}
            </Typography>
            <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', flexWrap: 'wrap' }}>
              <Button variant="contained" onClick={() => navigate('/products')} sx={{ borderRadius: 2 }}>
                Browse Products
              </Button>
              {filterStatus !== 'all' && (
                <Button variant="outlined" onClick={() => setFilterStatus('all')} sx={{ borderRadius: 2 }}>
                  Show All Orders
                </Button>
              )}
            </Box>
          </Paper>
        ) : (
          <Grid container spacing={3}>
            {filteredOrders.map(order => (
              <Grid item xs={12} sm={6} md={4} key={order.id}>
                <OrderCard order={order} onViewDetails={handleViewDetails} onReorder={handleReorder} />
              </Grid>
            ))}
          </Grid>
        )}

      </Container>

      {/* Detail Dialog */}
      <OrderDetailDialog
        order={selectedOrder}
        open={detailOpen}
        onClose={() => { setDetailOpen(false); setSelectedOrder(null); }}
        onReorder={handleReorder}
      />
    </Box>
  );
};

export default Orders;
