import React, { useState, useEffect } from 'react';
import {
  Container, Grid, Card, CardContent, CardMedia, Typography, Button,
  Box, Chip, CircularProgress, Alert, TextField, InputAdornment,
  useTheme, Snackbar, Badge, IconButton, Divider, FormControl,
  InputLabel, Select, MenuItem, Paper,
} from '@mui/material';
import {
  Search, ShoppingCart, Restaurant, Star, Favorite, FavoriteBorder,
  LocalDining, TrendingUp, NewReleases, FilterList,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { productAPI } from '../services/api';

// ── Fallback products shown when API fails ────────────────────────────────────
const FALLBACK_PRODUCTS = [
  { id: 'f1', name: 'Dal Bhat Set', description: 'Traditional Nepali lentil soup with rice, served with pickles and vegetable curry.', price: 180, category: 'Foods', image: 'https://via.placeholder.com/400x300/FF6B35/FFFFFF?text=Dal+Bhat', rating: 4.8 },
  { id: 'f2', name: 'Momo (Chicken)', description: 'Steamed dumplings stuffed with seasoned chicken, served with tomato achar.', price: 150, category: 'Foods', image: 'https://via.placeholder.com/400x300/4ECDC4/FFFFFF?text=Momo', rating: 4.9 },
  { id: 'f3', name: 'Thukpa Noodle Soup', description: 'Hearty Tibetan-style noodle soup with vegetables and tender meat.', price: 200, category: 'Foods', image: 'https://via.placeholder.com/400x300/45B7D1/FFFFFF?text=Thukpa', rating: 4.6 },
  { id: 'f4', name: 'Sel Roti & Yoghurt', description: 'Crispy traditional Nepali ring-shaped rice bread with homemade yoghurt.', price: 120, category: 'Snacks', image: 'https://via.placeholder.com/400x300/96CEB4/FFFFFF?text=Sel+Roti', rating: 4.7 },
  { id: 'f5', name: 'Newari Khaja Set', description: 'Authentic Newari platter with beaten rice, eggs, soybeans, and meat.', price: 250, category: 'Foods', image: 'https://via.placeholder.com/400x300/FFEAA7/555?text=Khaja+Set', rating: 4.5 },
  { id: 'f6', name: 'Chiya (Milk Tea)', description: 'Strong Nepali milk tea brewed with cardamom and ginger spices.', price: 40, category: 'Drinks', image: 'https://via.placeholder.com/400x300/DDA0DD/FFFFFF?text=Chiya', rating: 4.9 },
  { id: 'f7', name: 'Choila Platter', description: 'Spiced flame-roasted buffalo meat with sesame oil and herbs.', price: 300, category: 'Foods', image: 'https://via.placeholder.com/400x300/E17055/FFFFFF?text=Choila', rating: 4.4 },
  { id: 'f8', name: 'Lassi (Sweet)', description: 'Thick and creamy blended yoghurt drink, sweetened and chilled.', price: 80, category: 'Drinks', image: 'https://via.placeholder.com/400x300/74B9FF/FFFFFF?text=Lassi', rating: 4.6 },
];

const CATEGORIES = ['All', 'Foods', 'Snacks', 'Drinks', 'Clothing', 'Electronic', 'Books'];

// ── Product Card ──────────────────────────────────────────────────────────────
const ProductCard = ({ product, onAddToCart, onToggleFavorite, isFavorite, isLoggedIn }) => {
  const navigate = useNavigate();
  const [addedFeedback, setAddedFeedback] = useState(false);

  const handleAddToCart = (e) => {
    e.stopPropagation();
    if (!isLoggedIn) { navigate('/login'); return; }
    onAddToCart(product);
    setAddedFeedback(true);
    setTimeout(() => setAddedFeedback(false), 1200);
  };

  const handleFavorite = (e) => {
    e.stopPropagation();
    if (!isLoggedIn) { navigate('/login'); return; }
    onToggleFavorite(product.id);
  };

  return (
    <Card
      onClick={() => navigate(`/products/${product.id}`)}
      sx={{
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        cursor: 'pointer',
        borderRadius: 3,
        overflow: 'hidden',
        transition: 'transform 0.25s ease, box-shadow 0.25s ease',
        '&:hover': { transform: 'translateY(-6px)', boxShadow: '0 16px 40px rgba(0,0,0,0.14)' },
      }}
    >
      {/* Image */}
      <Box sx={{ position: 'relative', height: 200, overflow: 'hidden' }}>
        <img
          src={product.image}
          alt={product.name}
          style={{ width: '100%', height: '100%', objectFit: 'cover', transition: 'transform 0.35s ease' }}
          onError={e => { e.target.src = `https://via.placeholder.com/400x300/ccc/555?text=${encodeURIComponent(product.name)}`; }}
        />
        {/* Favorite btn */}
        <IconButton
          size="small"
          onClick={handleFavorite}
          sx={{
            position: 'absolute', top: 8, right: 8,
            bgcolor: 'rgba(255,255,255,0.92)', backdropFilter: 'blur(4px)',
            '&:hover': { bgcolor: 'white', transform: 'scale(1.1)' },
            transition: 'all 0.2s',
          }}
        >
          {isFavorite
            ? <Favorite sx={{ color: '#e91e63', fontSize: 20 }} />
            : <FavoriteBorder sx={{ color: '#888', fontSize: 20 }} />}
        </IconButton>
        {/* Category badge */}
        {product.category && (
          <Chip
            label={product.category}
            size="small"
            sx={{
              position: 'absolute', top: 8, left: 8,
              bgcolor: 'rgba(0,0,0,0.65)', color: 'white',
              fontSize: 11, fontWeight: 600, backdropFilter: 'blur(4px)',
            }}
          />
        )}
      </Box>

      <CardContent sx={{ flexGrow: 1, display: 'flex', flexDirection: 'column', p: 2 }}>
        <Typography variant="h6" fontWeight={700} noWrap sx={{ fontSize: 15, mb: 0.5 }}>
          {product.name}
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mb: 1.5, height: 38, overflow: 'hidden', fontSize: 12, lineHeight: 1.6 }}>
          {product.description || 'Freshly prepared with quality ingredients.'}
        </Typography>

        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 1.5 }}>
          <Typography variant="h6" color="primary.main" fontWeight={800} sx={{ fontSize: 18 }}>
            Rs. {product.price?.toFixed(0) || '0'}
          </Typography>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
            <Star sx={{ fontSize: 15, color: '#f59e0b' }} />
            <Typography variant="caption" fontWeight={600} color="text.secondary">
              {product.rating?.toFixed(1) || '4.5'}
            </Typography>
          </Box>
        </Box>

        <Button
          variant={addedFeedback ? 'outlined' : 'contained'}
          fullWidth
          size="small"
          startIcon={<ShoppingCart sx={{ fontSize: 16 }} />}
          onClick={handleAddToCart}
          sx={{
            mt: 'auto', borderRadius: 2, py: 0.8, fontSize: 13, fontWeight: 600,
            transition: 'all 0.2s',
            ...(addedFeedback && { borderColor: 'success.main', color: 'success.main' }),
          }}
        >
          {addedFeedback ? '✓ Added!' : 'Add to Cart'}
        </Button>
      </CardContent>
    </Card>
  );
};

// ── Feature Badge ─────────────────────────────────────────────────────────────
const FeatureBadge = ({ icon, label, sub }) => (
  <Box sx={{ textAlign: 'center', px: 2 }}>
    <Box sx={{ fontSize: 32, mb: 1 }}>{icon}</Box>
    <Typography variant="subtitle2" fontWeight={700}>{label}</Typography>
    <Typography variant="caption" color="text.secondary">{sub}</Typography>
  </Box>
);

// ── Main Component ────────────────────────────────────────────────────────────
const Home = () => {
  const navigate = useNavigate();
  const theme = useTheme();

  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [sortBy, setSortBy] = useState('default');

  const [cart, setCart] = useState(() => {
    try { return JSON.parse(localStorage.getItem('cart') || '[]'); } catch { return []; }
  });
  const [favorites, setFavorites] = useState(() => {
    try { return JSON.parse(localStorage.getItem('favorites') || '[]'); } catch { return []; }
  });
  const [snackbar, setSnackbar] = useState({ open: false, message: '' });

  const isLoggedIn = !!localStorage.getItem('token');
  const cartCount = cart.reduce((sum, item) => sum + (item.quantity || 1), 0);

  // ── Persist cart & favorites ────────────────────
  useEffect(() => { localStorage.setItem('cart', JSON.stringify(cart)); }, [cart]);
  useEffect(() => { localStorage.setItem('favorites', JSON.stringify(favorites)); }, [favorites]);

  // ── Fetch products ──────────────────────────────
  useEffect(() => { fetchProducts(); }, []);

  // ── Filter & sort whenever deps change ─────────
  useEffect(() => {
    let result = [...products];

    // Search
    if (searchTerm.trim()) {
      const q = searchTerm.toLowerCase();
      result = result.filter(p =>
        p.name?.toLowerCase().includes(q) ||
        p.description?.toLowerCase().includes(q) ||
        p.category?.toLowerCase().includes(q)
      );
    }

    // Category
    if (selectedCategory !== 'All') {
      result = result.filter(p => p.category === selectedCategory);
    }

    // Sort
    if (sortBy === 'price_asc') result.sort((a, b) => (a.price || 0) - (b.price || 0));
    else if (sortBy === 'price_desc') result.sort((a, b) => (b.price || 0) - (a.price || 0));
    else if (sortBy === 'rating') result.sort((a, b) => (b.rating || 0) - (a.rating || 0));
    else if (sortBy === 'name') result.sort((a, b) => a.name?.localeCompare(b.name));

    setFilteredProducts(result);
  }, [searchTerm, products, selectedCategory, sortBy]);

  const setFallbackProducts = () => {
    setProducts(FALLBACK_PRODUCTS);
    setFilteredProducts(FALLBACK_PRODUCTS);
  };

  const fetchProducts = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await productAPI.getAll();
      if (response.data.success) {
        const data = response.data.data || [];
        setProducts(data.length > 0 ? data : FALLBACK_PRODUCTS);
      } else {
        setFallbackProducts();
      }
    } catch (err) {
      console.error('Error fetching products:', err);
      setError('Could not connect to server — showing sample products.');
      setFallbackProducts();
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    if (!searchTerm.trim()) return;
    try {
      setLoading(true);
      const response = await productAPI.search(searchTerm);
      if (response.data.success) {
        setFilteredProducts(response.data.data || []);
      }
    } catch {
      // Filter locally if API search fails
      const q = searchTerm.toLowerCase();
      setFilteredProducts(products.filter(p =>
        p.name?.toLowerCase().includes(q) || p.description?.toLowerCase().includes(q)
      ));
    } finally {
      setLoading(false);
    }
  };

  // ── Cart ────────────────────────────────────────
  const handleAddToCart = (product) => {
    setCart(prev => {
      const existing = prev.find(i => i.id === product.id);
      if (existing) return prev.map(i => i.id === product.id ? { ...i, quantity: (i.quantity || 1) + 1 } : i);
      return [...prev, { ...product, quantity: 1 }];
    });
    setSnackbar({ open: true, message: `"${product.name}" added to cart!` });
  };

  // ── Favorites ───────────────────────────────────
  const handleToggleFavorite = (productId) => {
    setFavorites(prev =>
      prev.includes(productId) ? prev.filter(id => id !== productId) : [...prev, productId]
    );
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress size={60} />
      </Box>
    );
  }

  return (
    <Box sx={{ bgcolor: 'grey.50', minHeight: '100vh' }}>

      {/* ── Hero ── */}
      <Box sx={{
        background: `linear-gradient(135deg, ${theme.palette.primary.dark} 0%, ${theme.palette.primary.main} 50%, ${theme.palette.secondary.main} 100%)`,
        color: 'white', py: { xs: 6, md: 9 }, px: 2, position: 'relative', overflow: 'hidden',
      }}>
        {/* Decorative circles */}
        <Box sx={{ position: 'absolute', top: -60, right: -60, width: 260, height: 260, borderRadius: '50%', bgcolor: 'rgba(255,255,255,0.07)' }} />
        <Box sx={{ position: 'absolute', bottom: -40, left: -40, width: 180, height: 180, borderRadius: '50%', bgcolor: 'rgba(255,255,255,0.05)' }} />

        <Container maxWidth="lg" sx={{ textAlign: 'center', position: 'relative' }}>
          <Typography variant="overline" sx={{ letterSpacing: 4, opacity: 0.8, fontSize: 12 }}>
            Smart Meal Planning
          </Typography>
          <Typography variant="h2" component="h1" fontWeight={900} sx={{ mb: 1.5, fontSize: { xs: '2rem', md: '3.2rem' }, lineHeight: 1.15 }}>
            Welcome to MealBasket 🍽️
          </Typography>
          <Typography variant="h6" sx={{ mb: 4, opacity: 0.88, fontWeight: 400, maxWidth: 540, mx: 'auto', fontSize: { xs: 15, md: 18 } }}>
            Discover fresh local food, plan your meals, and get them delivered fast.
          </Typography>

          <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', flexWrap: 'wrap', mb: 5 }}>
            <Button
              variant="contained"
              size="large"
              startIcon={<Restaurant />}
              onClick={() => navigate('/meal-planner')}
              sx={{ bgcolor: 'white', color: 'primary.main', fontWeight: 700, borderRadius: 3, px: 3, '&:hover': { bgcolor: 'grey.100', transform: 'translateY(-2px)' }, transition: 'all 0.2s' }}
            >
              Plan Meals
            </Button>
            <Button
              variant="outlined"
              size="large"
              startIcon={<ShoppingCart />}
              onClick={() => navigate('/products')}
              sx={{ borderColor: 'rgba(255,255,255,0.7)', color: 'white', fontWeight: 700, borderRadius: 3, px: 3, '&:hover': { borderColor: 'white', bgcolor: 'rgba(255,255,255,0.12)', transform: 'translateY(-2px)' }, transition: 'all 0.2s' }}
            >
              Browse All
            </Button>
            {isLoggedIn && (
              <Badge badgeContent={cartCount} color="error" overlap="rectangular">
                <Button
                  variant="outlined"
                  size="large"
                  startIcon={<ShoppingCart />}
                  onClick={() => navigate('/dashboard')}
                  sx={{ borderColor: 'rgba(255,255,255,0.7)', color: 'white', fontWeight: 700, borderRadius: 3, px: 3, '&:hover': { borderColor: 'white', bgcolor: 'rgba(255,255,255,0.12)' } }}
                >
                  My Cart ({cartCount})
                </Button>
              </Badge>
            )}
          </Box>

          {/* Feature strips */}
          <Box sx={{ display: 'flex', justifyContent: 'center', gap: { xs: 3, md: 6 }, flexWrap: 'wrap' }}>
            <FeatureBadge icon="🚀" label="Fast Delivery" sub="30–45 min avg." />
            <FeatureBadge icon="🥗" label="Fresh Daily" sub="Local sourced" />
            <FeatureBadge icon="💳" label="Easy Payment" sub="Secure checkout" />
            <FeatureBadge icon="⭐" label="Top Rated" sub="4.7 avg. rating" />
          </Box>
        </Container>
      </Box>

      <Container maxWidth="lg" sx={{ py: 4 }}>

        {/* ── Error ── */}
        {error && (
          <Alert severity="warning" sx={{ mb: 3, borderRadius: 2 }} onClose={() => setError(null)}>
            {error}
          </Alert>
        )}

        {/* ── Search + Filters ── */}
        <Paper sx={{ p: 2.5, mb: 4, borderRadius: 3, boxShadow: '0 2px 16px rgba(0,0,0,0.07)' }}>
          <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap', alignItems: 'center' }}>
            <TextField
              sx={{ flexGrow: 1, minWidth: 220 }}
              variant="outlined"
              size="small"
              placeholder="Search meals, snacks, drinks…"
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
              onKeyPress={e => e.key === 'Enter' && handleSearch()}
              InputProps={{
                startAdornment: <InputAdornment position="start"><Search color="action" /></InputAdornment>,
                sx: { borderRadius: 2 },
              }}
            />
            <FormControl size="small" sx={{ minWidth: 130 }}>
              <InputLabel>Category</InputLabel>
              <Select value={selectedCategory} label="Category" onChange={e => setSelectedCategory(e.target.value)} sx={{ borderRadius: 2 }}>
                {CATEGORIES.map(c => <MenuItem key={c} value={c}>{c}</MenuItem>)}
              </Select>
            </FormControl>
            <FormControl size="small" sx={{ minWidth: 140 }}>
              <InputLabel>Sort By</InputLabel>
              <Select value={sortBy} label="Sort By" onChange={e => setSortBy(e.target.value)} sx={{ borderRadius: 2 }}>
                <MenuItem value="default">Default</MenuItem>
                <MenuItem value="price_asc">Price: Low → High</MenuItem>
                <MenuItem value="price_desc">Price: High → Low</MenuItem>
                <MenuItem value="rating">Top Rated</MenuItem>
                <MenuItem value="name">A → Z</MenuItem>
              </Select>
            </FormControl>
            <Button variant="contained" onClick={handleSearch} sx={{ borderRadius: 2, px: 3, height: 40 }}>
              Search
            </Button>
            {(searchTerm || selectedCategory !== 'All' || sortBy !== 'default') && (
              <Button variant="text" onClick={() => { setSearchTerm(''); setSelectedCategory('All'); setSortBy('default'); }} sx={{ color: 'text.secondary', fontSize: 13 }}>
                Reset
              </Button>
            )}
          </Box>
        </Paper>

        {/* ── Section Header ── */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Box>
            <Typography variant="h4" fontWeight={800} sx={{ lineHeight: 1.2 }}>
              {searchTerm ? `Results for "${searchTerm}"` : selectedCategory !== 'All' ? selectedCategory : 'Featured Products'}
            </Typography>
            <Typography variant="body2" color="text.secondary" sx={{ mt: 0.5 }}>
              {filteredProducts.length} item{filteredProducts.length !== 1 ? 's' : ''} available
            </Typography>
          </Box>
          <Box sx={{ display: 'flex', gap: 1 }}>
            <Chip icon={<TrendingUp sx={{ fontSize: 15 }} />} label="Trending" color="primary" size="small" variant="outlined" />
            <Chip icon={<NewReleases sx={{ fontSize: 15 }} />} label="New" color="secondary" size="small" variant="outlined" />
          </Box>
        </Box>

        {/* ── Product Grid ── */}
        {filteredProducts.length > 0 ? (
          <Grid container spacing={3}>
            {filteredProducts.map(product => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={product.id}>
                <ProductCard
                  product={product}
                  onAddToCart={handleAddToCart}
                  onToggleFavorite={handleToggleFavorite}
                  isFavorite={favorites.includes(product.id)}
                  isLoggedIn={isLoggedIn}
                />
              </Grid>
            ))}
          </Grid>
        ) : (
          <Box sx={{ textAlign: 'center', py: 10 }}>
            <Typography variant="h1" sx={{ fontSize: 64, mb: 2 }}>🔍</Typography>
            <Typography variant="h6" color="text.secondary" gutterBottom>
              {searchTerm ? `No results for "${searchTerm}"` : 'No products available right now.'}
            </Typography>
            <Button variant="contained" sx={{ mt: 2, borderRadius: 2 }} onClick={() => { setSearchTerm(''); setSelectedCategory('All'); setSortBy('default'); }}>
              Clear Filters
            </Button>
          </Box>
        )}

        {/* ── Why MealBasket strip ── */}
        <Divider sx={{ my: 6 }} />
        <Box sx={{ textAlign: 'center', mb: 4 }}>
          <Typography variant="h5" fontWeight={800} gutterBottom>Why MealBasket?</Typography>
          {/* <Grid container spacing={3} justifyContent="center" sx={{ mt: 1 }}>
            {[
              { emoji: '🌿', title: 'Fresh & Local', desc: 'Sourced from nearby vendors daily' },
              { emoji: '⏱️', title: 'Quick Delivery', desc: 'Door-to-door in under 45 minutes' },
              { emoji: '💬', title: 'Rate & Review', desc: 'Real feedback from real customers' },
              { emoji: '🎯', title: 'Meal Planning', desc: 'AI-powered weekly meal planner' },
            ].map(({ emoji, title, desc }) => (
              <Grid item xs={6} sm={3} key={title}>
                <Box sx={{ p: 2, borderRadius: 3, bgcolor: 'white', boxShadow: '0 2px 12px rgba(0,0,0,0.06)', height: '100%' }}>
                  <Typography variant="h4" sx={{ mb: 1 }}>{emoji}</Typography>
                  <Typography variant="subtitle2" fontWeight={700}>{title}</Typography>
                  <Typography variant="caption" color="text.secondary">{desc}</Typography>
                </Box>
              </Grid>
            ))}
          </Grid> */}
        </Box>

      </Container>

      {/* ── Cart Snackbar ── */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={2500}
        onClose={() => setSnackbar(s => ({ ...s, open: false }))}
        message={snackbar.message}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      />
    </Box>
  );
};

export default Home;
