import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  Button,
  Avatar,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  Alert,
  CircularProgress,
  AppBar,
  Toolbar,
  IconButton,
  Menu,
  MenuItem,
} from '@mui/material';
import {
  AdminPanelSettings,
  People,
  Store,
  ShoppingCart,
  Assessment,
  TrendingUp,
  AttachMoney,
  Logout,
  Restaurant,
  Person,
  ArrowBack,
  Home,
  AccountCircle,
} from '@mui/icons-material';

const SimpleAdminDashboard = () => {
  const [adminData, setAdminData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentView, setCurrentView] = useState('dashboard'); // dashboard, users, vendors, analytics, settings
  const [anchorEl, setAnchorEl] = useState(null);

  // Admin data states
  const [users, setUsers] = useState([]);
  const [vendors, setVendors] = useState([]);
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalVendors: 0,
    totalProducts: 0,
    totalOrders: 0,
    totalRevenue: 0
  });

  // Real-time activity tracking states
  const [recentActivities, setRecentActivities] = useState([]);
  const [systemStatus, setSystemStatus] = useState({
    activeUsers: 0,
    onlineUsers: 0,
    totalSessions: 0,
    serverLoad: 45,
    uptime: '99.8%',
    lastBackup: new Date().toLocaleString(),
    systemVersion: 'v2.1.0'
  });
  const [userSessions, setUserSessions] = useState([]);

  useEffect(() => {
    // Get admin data from localStorage
    const user = localStorage.getItem('user');
    const token = localStorage.getItem('token');

    if (!user || !token) {
      setError('Please login first');
      setLoading(false);
      return;
    }

    try {
      const parsedUser = JSON.parse(user);
      if (parsedUser.role !== 'ADMIN') {
        setError('Access denied. Admin role required.');
        setLoading(false);
        return;
      }
      setAdminData(parsedUser);

      // Load admin data
      loadAdminData();
    } catch (err) {
      setError('Error loading user data');
      setLoading(false);
    }
  }, []);

  // Load admin dashboard data
  const loadAdminData = async () => {
    try {
      // Load data from localStorage (since backend might not be available)
      const allVendorProducts = JSON.parse(localStorage.getItem('allVendorProducts') || '{}');
      const allUsers = JSON.parse(localStorage.getItem('allUsers') || '[]');

      // Extract products
      let allProducts = [];
      Object.keys(allVendorProducts).forEach(vendorKey => {
        if (allVendorProducts[vendorKey]) {
          allProducts = [...allProducts, ...allVendorProducts[vendorKey]];
        }
      });

      // Extract unique vendors
      const uniqueVendors = [...new Set(allProducts.map(p => p.vendor))];
      const vendorList = uniqueVendors.map(vendorName => ({
        name: vendorName,
        productCount: allProducts.filter(p => p.vendor === vendorName).length
      }));

      // Calculate stats
      const calculatedStats = {
        totalUsers: allUsers.length || 0,
        totalVendors: vendorList.length,
        totalProducts: allProducts.length,
        totalOrders: 0, // Will be implemented when orders are stored
        totalRevenue: allProducts.reduce((sum, p) => sum + (p.price || 0), 0)
      };

      // Load recent activities
      loadRecentActivities(allProducts, allUsers, vendorList);

      // Load user sessions and system status
      loadUserSessions(allUsers);
      updateSystemStatus();

      setUsers(allUsers);
      setVendors(vendorList);
      setProducts(allProducts);
      setOrders([]); // Will be implemented
      setStats(calculatedStats);
      setLoading(false);

    } catch (error) {
      console.error('Error loading admin data:', error);
      setError('Failed to load admin data');
      setLoading(false);
    }
  };

  // Load recent activities
  const loadRecentActivities = (products, users, vendors) => {
    const activities = [];
    const now = new Date();

    // Simulate recent vendor registrations
    vendors.forEach((vendor, index) => {
      activities.push({
        type: 'vendor_registration',
        title: `New Vendor Registration`,
        description: `${vendor.name} applied for vendor account`,
        time: `${index * 5 + 1} mins ago`,
        icon: 'Store',
        color: 'success'
      });
    });

    // Simulate recent product additions
    products.slice(-3).forEach((product, index) => {
      activities.push({
        type: 'product_added',
        title: `New Product Added`,
        description: `${product.name} added by ${product.vendor}`,
        time: `${index * 10 + 2} mins ago`,
        icon: 'ShoppingCart',
        color: 'info'
      });
    });

    // Simulate user registrations
    users.slice(-2).forEach((user, index) => {
      activities.push({
        type: 'user_registration',
        title: `New User Registration`,
        description: `${user.name || 'User'} joined the platform`,
        time: `${index * 15 + 3} mins ago`,
        icon: 'Person',
        color: 'primary'
      });
    });

    // Add system activities
    activities.push({
      type: 'system_update',
      title: `System Update`,
      description: `Database backup completed successfully`,
      time: '1 hour ago',
      icon: 'SystemUpdate',
      color: 'warning'
    });

    setRecentActivities(activities.slice(0, 8)); // Show latest 8 activities
  };

  // Load user sessions
  const loadUserSessions = (users) => {
    const sessions = users.map((user, index) => ({
      id: user.id || index,
      name: user.name || `User ${index + 1}`,
      email: user.email || `user${index + 1}@test.com`,
      status: Math.random() > 0.3 ? 'online' : 'offline', // Simulate online/offline
      lastActivity: Math.random() > 0.5 ? 'Active now' : `${Math.floor(Math.random() * 60)} mins ago`,
      sessionDuration: `${Math.floor(Math.random() * 120) + 10} mins`,
      role: user.role || 'USER'
    }));

    setUserSessions(sessions);

    // Update system status
    const onlineCount = sessions.filter(s => s.status === 'online').length;
    const activeCount = sessions.filter(s => s.lastActivity === 'Active now').length;

    setSystemStatus(prev => ({
      ...prev,
      onlineUsers: onlineCount,
      activeUsers: activeCount,
      totalSessions: sessions.length
    }));
  };

  // Update system status
  const updateSystemStatus = () => {
    setSystemStatus(prev => ({
      ...prev,
      serverLoad: Math.floor(Math.random() * 30) + 30, // Simulate 30-60% load
      lastBackup: new Date(Date.now() - Math.random() * 7200000).toLocaleString() // Random time within last 2 hours
    }));
  };

  // Add new activity (to be called from other components)
  const addActivity = (type, title, description) => {
    const newActivity = {
      type,
      title,
      description,
      time: 'Just now',
      icon: type === 'user_registration' ? 'Person' : 
            type === 'vendor_registration' ? 'Store' : 
            type === 'product_added' ? 'ShoppingCart' : 'Notification',
      color: type === 'user_registration' ? 'primary' : 
             type === 'vendor_registration' ? 'success' : 
             type === 'product_added' ? 'info' : 'warning'
    };

    setRecentActivities(prev => [newActivity, ...prev].slice(0, 8));
  };

  // Listen for real-time updates from other dashboards
  useEffect(() => {
    const handleMessage = (event) => {
      if (event.data && event.data.type) {
        console.log('Admin Dashboard received message:', event.data);

        switch (event.data.type) {
          case 'PRODUCT_ADDED':
            addActivity('product_added', 'New Product Added', 
              `${event.data.product.name} added by ${event.data.product.vendor}`);
            loadAdminData(); // Refresh data
            break;
          case 'USER_LOGIN':
            addActivity('user_login', 'User Login', 
              `${event.data.userName} logged into the system`);
            loadAdminData(); // Refresh data
            break;
          case 'VENDOR_REGISTERED':
            addActivity('vendor_registration', 'New Vendor Registration', 
              `${event.data.vendorName} applied for vendor account`);
            loadAdminData(); // Refresh data
            break;
        }
      }
    };

    window.addEventListener('message', handleMessage);
    return () => window.removeEventListener('message', handleMessage);
  }, []);

  // Auto-refresh system status every 30 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      updateSystemStatus();
      loadUserSessions(users);
    }, 30000);

    return () => clearInterval(interval);
  }, [users]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  // Navigation Functions
  const handleBackToDashboard = () => {
    setCurrentView('dashboard');
  };

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const getViewTitle = () => {
    switch(currentView) {
      case 'dashboard': return 'Admin Dashboard';
      case 'users': return 'User Management';
      case 'vendors': return 'Vendor Management';
      case 'analytics': return 'Platform Analytics';
      case 'settings': return 'System Settings';
      default: return 'Admin Dashboard';
    }
  };

  const handleViewUsers = () => {
    setCurrentView('users');
  };

  const handleViewVendors = () => {
    setCurrentView('vendors');
  };

  const handleViewAnalytics = () => {
    setCurrentView('analytics');
  };

  const handleViewSettings = () => {
    setCurrentView('settings');
  };

  // Admin Management Functions
  const handleDeleteUser = (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      const updatedUsers = users.filter(user => user.id !== userId);
      setUsers(updatedUsers);
      localStorage.setItem('allUsers', JSON.stringify(updatedUsers));
      alert('User deleted successfully');
    }
  };

  const handleDeleteVendor = (vendorName) => {
    if (window.confirm(`Are you sure you want to delete ${vendorName} and all their products?`)) {
      // Remove vendor products from localStorage
      const allVendorProducts = JSON.parse(localStorage.getItem('allVendorProducts') || '{}');
      Object.keys(allVendorProducts).forEach(vendorKey => {
        if (allVendorProducts[vendorKey] && allVendorProducts[vendorKey][0]?.vendor === vendorName) {
          delete allVendorProducts[vendorKey];
        }
      });
      localStorage.setItem('allVendorProducts', JSON.stringify(allVendorProducts));

      // Reload data
      loadAdminData();
      alert(`Vendor ${vendorName} and their products deleted successfully`);
    }
  };

  const handleDeleteProduct = (productId) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      // Remove product from localStorage
      const allVendorProducts = JSON.parse(localStorage.getItem('allVendorProducts') || '{}');
      Object.keys(allVendorProducts).forEach(vendorKey => {
        if (allVendorProducts[vendorKey]) {
          allVendorProducts[vendorKey] = allVendorProducts[vendorKey].filter(p => p.id !== productId);
        }
