import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  Button,
  Avatar,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Alert,
  CircularProgress,
  LinearProgress,
  Chip,
  Rating,
  AppBar,
  Toolbar,
  IconButton,
  Menu,
  MenuItem,
} from '@mui/material';
import {
  Person,
  ShoppingCart,
  Restaurant,
  Assessment,
  Favorite,
  History,
  LocalOffer,
  TrendingUp,
  Star,
  Logout,
  Settings,
  ArrowBack,
  Home,
  AccountCircle,
  Menu as MenuIcon,
} from '@mui/icons-material';

const SimpleUserDashboard = () => {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentView, setCurrentView] = useState('dashboard'); // dashboard, meals, orders, favorites, profile
  const [anchorEl, setAnchorEl] = useState(null);

  // Sample user data
  const userStats = {
    totalOrders: 28,
    totalSpent: 456.78,
    favoriteItems: 12,
    points: 1250,
    memberSince: 'January 2024',
    lastOrder: '2 days ago',
    avgOrderValue: 16.31
  };

  const recentOrders = [
    { id: 1234, vendor: 'Burger Palace', items: 'Classic Burger, Fries', total: 18.99, status: 'Delivered', date: '2024-02-10' },
    { id: 1233, vendor: 'Pizza Express', items: 'Pepperoni Pizza', total: 15.99, status: 'Delivered', date: '2024-02-08' },
    { id: 1232, vendor: 'Green Garden', items: 'Caesar Salad', total: 8.99, status: 'Delivered', date: '2024-02-05' },
    { id: 1231, vendor: 'Chicken House', items: 'Grilled Chicken', total: 14.99, status: 'Delivered', date: '2024-02-03' },
  ];

  const favoriteItems = [
    { id: 1, name: 'Classic Burger', vendor: 'Burger Palace', price: 12.99, rating: 4.5 },
    { id: 2, name: 'Caesar Salad', vendor: 'Green Garden', price: 8.99, rating: 4.2 },
    { id: 3, name: 'Pepperoni Pizza', vendor: 'Pizza Express', price: 15.99, rating: 4.7 },
  ];

  const recommendations = [
    { id: 1, name: 'BBQ Bacon Burger', vendor: 'Burger Palace', price: 14.99, rating: 4.6, reason: 'Based on your favorites' },
    { id: 2, name: 'Greek Salad', vendor: 'Green Garden', price: 9.99, rating: 4.3, reason: 'Popular in your area' },
    { id: 3, name: 'Margherita Pizza', vendor: 'Pizza Express', price: 13.99, rating: 4.5, reason: 'Trending now' },
  ];

  useEffect(() => {
    // Try to get user data from localStorage, but don't fail if it's not there
    try {
      const user = localStorage.getItem('user');
      if (user) {
        const parsedUser = JSON.parse(user);
        setUserData(parsedUser);
      } else {
        // Set default user data if localStorage is empty
        setUserData({
          id: 1,
          name: 'Test User',
          email: 'user@test.com',
          role: 'USER'
        });
      }
    } catch (err) {
      // Set default user data on error
      setUserData({
        id: 1,
        name: 'Test User',
        email: 'user@test.com',
        role: 'USER'
      });
    }
    setLoading(false);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  const handleNavigateToMealPlanner = () => {
    setCurrentView('meals');
  };

  const handleViewOrderHistory = () => {
    setCurrentView('orders');
  };

  const handleViewFavorites = () => {
    setCurrentView('favorites');
  };

  const handleViewProfile = () => {
    setCurrentView('profile');
  };

  const handleBackToDashboard = () => {
    setCurrentView('dashboard');
  };

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const getViewTitle = () => {
    switch(currentView) {
      case 'dashboard': return 'User Dashboard';
      case 'meals': return 'Browse Meals';
      case 'orders': return 'Order History';
      case 'favorites': return 'My Favorites';
      case 'profile': return 'Profile Settings';
      default: return 'User Dashboard';
    }
  };

  if (loading) {
    return (
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
          <CircularProgress size={60} />
        </Box>
      </Container>
    );
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      {/* Navbar */}
      <AppBar position="static" sx={{ mb: 3 }}>
        <Toolbar>
          {currentView !== 'dashboard' && (
            <IconButton
              edge="start"
              color="inherit"
              onClick={handleBackToDashboard}
              sx={{ mr: 2 }}
            >
              <ArrowBack />
            </IconButton>
          )}

          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            {getViewTitle()}
          </Typography>

          <IconButton
            edge="end"
            color="inherit"
            onClick={handleMenuOpen}
          >
            <AccountCircle />
          </IconButton>

          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={handleMenuClose}
          >
            <MenuItem onClick={handleMenuClose}>
              <ListItemIcon><Person /></ListItemIcon>
              <ListItemText>{userData?.name || 'User'}</ListItemText>
            </MenuItem>
            <MenuItem onClick={() => { handleMenuClose(); handleLogout(); }}>
              <ListItemIcon><Logout /></ListItemIcon>
              <ListItemText>Logout</ListItemText>
            </MenuItem>
          </Menu>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ py: 2 }}>
        {/* Dashboard View */}
        {currentView === 'dashboard' && (
          <>
            {/* Header */}
            <Box sx={{ mb: 4, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <Avatar sx={{ bgcolor: 'primary.main', mr: 2, width: 56, height: 56 }}>
                  <Person sx={{ fontSize: 32 }} />
                </Avatar>
                <Box>
                  <Typography variant="h6" color="text.secondary">
                    Welcome back, {userData?.name || 'User'}!
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Member since {userStats.memberSince} • {userStats.points} points
                  </Typography>
                </Box>
              </Box>
            </Box>

            {/* User Stats Cards */}
            <Grid container spacing={3} sx={{ mb: 4 }}>
              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'success.main', mr: 2 }}>
                        <ShoppingCart />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          {userStats.totalOrders}
                        </Typography>
                        <Typography color="text.secondary">
                          Total Orders
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'info.main', mr: 2 }}>
                        <TrendingUp />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          ${userStats.totalSpent}
                        </Typography>
                        <Typography color="text.secondary">
                          Total Spent
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'warning.main', mr: 2 }}>
                        <Favorite />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          {userStats.favoriteItems}
                        </Typography>
                        <Typography color="text.secondary">
                          Favorites
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'primary.main', mr: 2 }}>
                        <Star />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          {userStats.points}
                        </Typography>
                        <Typography color="text.secondary">
                          Points
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>

            {/* Quick Actions */}
            <Grid container spacing={3} sx={{ mb: 4 }}>
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Quick Actions
                  </Typography>
                  <List>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1, mb: 1 }}
                      onClick={handleNavigateToMealPlanner}
                    >
                      <ListItemIcon>
                        <Restaurant color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="Browse Meals" 
                        secondary="Order from your favorite restaurants" 
                      />
                    </ListItem>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1, mb: 1 }}
                      onClick={handleViewOrderHistory}
                    >
                      <ListItemIcon>
                        <History color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="Order History" 
                        secondary="View your past orders and receipts" 
                      />
                    </ListItem>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1, mb: 1 }}
                      onClick={handleViewFavorites}
                    >
                      <ListItemIcon>
                        <Favorite color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="My Favorites" 
                        secondary="Manage your favorite food items" 
                      />
                    </ListItem>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1 }}
                      onClick={handleViewProfile}
                    >
                      <ListItemIcon>
                        <Settings color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="Account Settings" 
                        secondary="Update your profile and preferences" 
                      />
                    </ListItem>
                  </List>
                </Paper>
              </Grid>

              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Recent Orders
                  </Typography>
                  <List>
                    {recentOrders.map((order) => (
                      <ListItem key={order.id} sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                        <ListItemText 
                          primary={
                            <Box display="flex" justifyContent="space-between" alignItems="center">
                              <span>Order #{order.id}</span>
                              <Chip label={order.status} color="success" size="small" />
                            </Box>
                          }
                          secondary={`${order.vendor} • ${order.items} • $${order.total} • ${order.date}`}
                        />
                      </ListItem>
                    ))}
                  </List>
                  <Button 
                    variant="outlined" 
                    fullWidth 
                    sx={{ mt: 2 }}
                    onClick={handleViewOrderHistory}
                  >
                    View All Orders
                  </Button>
                </Paper>
              </Grid>
            </Grid>

            {/* Favorites and Recommendations */}
            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Your Favorites
                  </Typography>
                  <List>
                    {favoriteItems.map((item) => (
                      <ListItem key={item.id} sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                        <ListItemText 
                          primary={item.name}
                          secondary={`${item.vendor} • $${item.price}`}
                        />
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                          <Rating value={item.rating} precision={0.1} size="small" readOnly />
                          <Typography variant="body2">({item.rating})</Typography>
                        </Box>
                      </ListItem>
                    ))}
                  </List>
                  <Button 
                    variant="outlined" 
                    fullWidth 
                    sx={{ mt: 2 }}
                    onClick={handleViewFavorites}
                  >
                    View All Favorites
                  </Button>
                </Paper>
              </Grid>

              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Recommended for You
                  </Typography>
                  <List>
                    {recommendations.map((item) => (
                      <ListItem key={item.id} sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                        <ListItemText 
                          primary={item.name}
                          secondary={`${item.vendor} • $${item.price} • ${item.reason}`}
                        />
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                          <Rating value={item.rating} precision={0.1} size="small" readOnly />
                          <Typography variant="body2">({item.rating})</Typography>
                        </Box>
                      </ListItem>
                    ))}
                  </List>
                  <Button 
                    variant="contained" 
                    fullWidth 
                    sx={{ mt: 2 }}
                    onClick={handleNavigateToMealPlanner}
                  >
                    Browse More
                  </Button>
                </Paper>
              </Grid>
            </Grid>

            {/* Progress Bar for Points */}
            <Paper sx={{ p: 3, mt: 4 }}>
              <Typography variant="h6" fontWeight="bold" sx={{ mb: 2 }}>
                Rewards Progress
              </Typography>
              <Box sx={{ mb: 2 }}>
                <Typography variant="body2" color="text.secondary">
                  {userStats.points} / 2000 points to next reward
                </Typography>
                <LinearProgress 
                  variant="determinate" 
                  value={(userStats.points / 2000) * 100} 
                  sx={{ mt: 1 }}
                />
              </Box>
              <Typography variant="body2" color="primary.main">
                🎉 You're {2000 - userStats.points} points away from a free meal!
              </Typography>
            </Paper>
          </>
        )}

        {/* Meals View */}
        {currentView === 'meals' && (
          <Paper sx={{ p: 3 }}>
            <Typography variant="h5" fontWeight="bold" sx={{ mb: 3 }}>
              Browse Meals
            </Typography>
            <Typography variant="body1" sx={{ mb: 3 }}>
              Browse delicious meals from your favorite restaurants. Add items to your cart and place orders easily.
            </Typography>
            <Button 
              variant="contained" 
              onClick={() => window.location.href = '/meal-planner'}
            >
              Go to Meal Planner
            </Button>
          </Paper>
        )}

        {/* Orders View */}
        {currentView === 'orders' && (
          <Paper sx={{ p: 3 }}>
            <Typography variant="h5" fontWeight="bold" sx={{ mb: 3 }}>
              Order History
            </Typography>
            <List>
              {recentOrders.map((order) => (
                <ListItem key={order.id} sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                  <ListItemText 
                    primary={`Order #${order.id} - ${order.vendor}`}
                    secondary={`${order.items} • $${order.total} • ${order.date} • ${order.status}`}
                  />
                  <Chip label={order.status} color="success" size="small" />
                </ListItem>
              ))}
            </List>
          </Paper>
        )}

        {/* Favorites View */}
        {currentView === 'favorites' && (
          <Paper sx={{ p: 3 }}>
            <Typography variant="h5" fontWeight="bold" sx={{ mb: 3 }}>
              My Favorites
            </Typography>
            <List>
              {favoriteItems.map((item) => (
                <ListItem key={item.id} sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                  <ListItemText 
                    primary={item.name}
                    secondary={`${item.vendor} • $${item.price}`}
                  />
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <Rating value={item.rating} precision={0.1} size="small" readOnly />
                    <Typography variant="body2">({item.rating})</Typography>
                  </Box>
                </ListItem>
              ))}
            </List>
          </Paper>
        )}

        {/* Profile View */}
        {currentView === 'profile' && (
          <Paper sx={{ p: 3 }}>
            <Typography variant="h5" fontWeight="bold" sx={{ mb: 3 }}>
              Profile Settings
            </Typography>
            <Typography variant="body1" sx={{ mb: 2 }}>
              Name: {userData?.name || 'Test User'}
            </Typography>
            <Typography variant="body1" sx={{ mb: 2 }}>
              Email: {userData?.email || 'user@test.com'}
            </Typography>
            <Typography variant="body1" sx={{ mb: 2 }}>
              Role: {userData?.role || 'USER'}
            </Typography>
            <Typography variant="body1" sx={{ mb: 3 }}>
              Member Since: {userStats.memberSince}
            </Typography>
            <Button variant="outlined">
              Edit Profile
            </Button>
          </Paper>
        )}
      </Container>
    </Box>
  );
};

export default SimpleUserDashboard;
