import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  Button,
  Avatar,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Alert,
  CircularProgress,
  LinearProgress,
  Chip,
  Rating,
  AppBar,
  Toolbar,
  IconButton,
  Menu,
  MenuItem,
  TextField,
} from '@mui/material';
import {
  Person,
  ShoppingCart,
  Restaurant,
  Assessment,
  Favorite,
  History,
  LocalOffer,
  TrendingUp,
  Star,
  Logout,
  Settings,
  ArrowBack,
  Home,
  AccountCircle,
  Menu as MenuIcon,
} from '@mui/icons-material';

const SimpleUserDashboard = () => {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentView, setCurrentView] = useState('dashboard'); // dashboard, meals, orders, favorites, profile
  const [anchorEl, setAnchorEl] = useState(null);
  const [vendorProducts, setVendorProducts] = useState([
    {
      id: 1,
      name: 'Classic Burger',
      vendor: 'Burger Palace',
      price: 12.99,
      description: 'Juicy beef patty with fresh vegetables and special sauce',
      rating: 4.5,
      inStock: true
    },
    {
      id: 2,
      name: 'Pepperoni Pizza',
      vendor: 'Pizza Express',
      price: 15.99,
      description: 'Fresh mozzarella, pepperoni, and tomato sauce on thin crust',
      rating: 4.7,
      inStock: true
    },
    {
      id: 3,
      name: 'Caesar Salad',
      vendor: 'Green Garden',
      price: 8.99,
      description: 'Fresh romaine lettuce, parmesan, croutons, and Caesar dressing',
      rating: 4.2,
      inStock: true
    },
    {
      id: 4,
      name: 'Grilled Chicken',
      vendor: 'Chicken House',
      price: 14.99,
      description: 'Tender grilled chicken breast with herbs and spices',
      rating: 4.3,
      inStock: true
    }
  ]);
  const [showAddProductForm, setShowAddProductForm] = useState(false);
  const [newProduct, setNewProduct] = useState({
    name: '',
    vendor: 'Your Restaurant',
    price: '',
    description: '',
    rating: 4.0
  });

  // Sample user data
  const userStats = {
    totalOrders: 28,
    totalSpent: 456.78,
    favoriteItems: 12,
    points: 1250,
    memberSince: 'January 2024',
    lastOrder: '2 days ago',
    avgOrderValue: 16.31
  };

  const recentOrders = [
    { id: 1234, vendor: 'Burger Palace', items: 'Classic Burger, Fries', total: 18.99, status: 'Delivered', date: '2024-02-10' },
    { id: 1233, vendor: 'Pizza Express', items: 'Pepperoni Pizza', total: 15.99, status: 'Delivered', date: '2024-02-08' },
    { id: 1232, vendor: 'Green Garden', items: 'Caesar Salad', total: 8.99, status: 'Delivered', date: '2024-02-05' },
    { id: 1231, vendor: 'Chicken House', items: 'Grilled Chicken', total: 14.99, status: 'Delivered', date: '2024-02-03' },
  ];

  const favoriteItems = [
    { id: 1, name: 'Classic Burger', vendor: 'Burger Palace', price: 12.99, rating: 4.5 },
    { id: 2, name: 'Caesar Salad', vendor: 'Green Garden', price: 8.99, rating: 4.2 },
    { id: 3, name: 'Pepperoni Pizza', vendor: 'Pizza Express', price: 15.99, rating: 4.7 },
  ];

  const recommendations = [
    { id: 1, name: 'BBQ Bacon Burger', vendor: 'Burger Palace', price: 14.99, rating: 4.6, reason: 'Based on your favorites' },
    { id: 2, name: 'Greek Salad', vendor: 'Green Garden', price: 9.99, rating: 4.3, reason: 'Popular in your area' },
    { id: 3, name: 'Margherita Pizza', vendor: 'Pizza Express', price: 13.99, rating: 4.5, reason: 'Trending now' },
  ];

  useEffect(() => {
    // Try to get user data from localStorage, but don't fail if it's not there
    try {
      const user = localStorage.getItem('user');
      if (user) {
        const parsedUser = JSON.parse(user);
        setUserData(parsedUser);
      } else {
        // Set default user data if localStorage is empty
        setUserData({
          id: 1,
          name: 'Test User',
          email: 'user@test.com',
          role: 'USER'
        });
      }
    } catch (err) {
      // Set default user data on error
      setUserData({
        id: 1,
        name: 'Test User',
        email: 'user@test.com',
        role: 'USER'
      });
    }
    setLoading(false);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  const handleNavigateToMealPlanner = () => {
    setCurrentView('meals');
  };

  const handleViewOrderHistory = () => {
    setCurrentView('orders');
  };

  const handleViewFavorites = () => {
    setCurrentView('favorites');
  };

  const handleViewProfile = () => {
    setCurrentView('profile');
  };

  const handleQuickOrder = () => {
    // Navigate to meal planner for quick ordering
    setCurrentView('meals');
  };

  const handleGetRecommendations = () => {
    // Navigate to meals view with AI recommendations
    setCurrentView('meals');
  };

  const handleViewCart = () => {
    // Navigate to cart view (could be implemented later)
    alert('Cart feature coming soon!');
  };

  const handleAddProduct = () => {
    setShowAddProductForm(true);
  };

  const handleSaveProduct = () => {
    if (newProduct.name && newProduct.price && newProduct.description) {
      const product = {
        id: vendorProducts.length + 1,
        name: newProduct.name,
        vendor: newProduct.vendor,
        price: parseFloat(newProduct.price),
        description: newProduct.description,
        rating: parseFloat(newProduct.rating),
        inStock: true
      };
      setVendorProducts([...vendorProducts, product]);
      setNewProduct({
        name: '',
        vendor: 'Your Restaurant',
        price: '',
        description: '',
        rating: 4.0
      });
      setShowAddProductForm(false);
      alert('Product added successfully!');
    } else {
      alert('Please fill in all required fields');
    }
  };

  const handleCancelAddProduct = () => {
    setShowAddProductForm(false);
    setNewProduct({
      name: '',
      vendor: 'Your Restaurant',
      price: '',
      description: '',
      rating: 4.0
    });
  };

  const handleBackToDashboard = () => {
    setCurrentView('dashboard');
  };

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const getViewTitle = () => {
    switch(currentView) {
      case 'dashboard': return 'User Dashboard';
      case 'meals': return 'Browse Meals';
      case 'orders': return 'Order History';
      case 'favorites': return 'My Favorites';
      case 'profile': return 'Profile Settings';
      default: return 'User Dashboard';
    }
  };

  if (loading) {
    return (
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
          <CircularProgress size={60} />
        </Box>
      </Container>
    );
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      {/* Navbar */}
      <AppBar position="static" sx={{ mb: 3 }}>
        <Toolbar>
          {currentView !== 'dashboard' && (
            <IconButton
              edge="start"
              color="inherit"
              onClick={handleBackToDashboard}
              sx={{ mr: 2 }}
            >
              <ArrowBack />
            </IconButton>
          )}

          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            {getViewTitle()}
          </Typography>

          <IconButton
            edge="end"
            color="inherit"
            onClick={handleMenuOpen}
          >
            <AccountCircle />
          </IconButton>

          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={handleMenuClose}
          >
            <MenuItem onClick={handleMenuClose}>
              <ListItemIcon><Person /></ListItemIcon>
              <ListItemText>{userData?.name || 'User'}</ListItemText>
            </MenuItem>
            <MenuItem onClick={() => { handleMenuClose(); handleLogout(); }}>
              <ListItemIcon><Logout /></ListItemIcon>
              <ListItemText>Logout</ListItemText>
            </MenuItem>
          </Menu>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ py: 2 }}>
        {/* Dashboard View */}
        {currentView === 'dashboard' && (
          <>
            {/* Top Action Bar */}
            <Paper sx={{ p: 2, mb: 3, bgcolor: 'primary.50' }}>
              <Grid container spacing={2} alignItems="center">
                <Grid item xs={12} sm={6} md={4}>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <Typography variant="h6" color="primary.main" sx={{ mr: 1 }}>
                      🍽️ Quick Order
                    </Typography>
                    <Button 
                      variant="contained" 
                      size="small" 
                      startIcon={<Restaurant />}
                      sx={{ mr: 1 }}
                      onClick={handleQuickOrder}
                    >
                      Order Now
                    </Button>
                    <Button 
                      variant="outlined" 
                      size="small"
                      startIcon={<ShoppingCart />}
                      onClick={handleViewCart}
                    >
                      Cart (0)
                    </Button>
                  </Box>
                </Grid>

                <Grid item xs={12} sm={6} md={4}>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <Typography variant="h6" color="primary.main" sx={{ mr: 1 }}>
                      🤖 AI Assistant
                    </Typography>
                    <Button 
                      variant="contained" 
                      size="small" 
                      startIcon={<Assessment />}
                      color="secondary"
                      onClick={handleGetRecommendations}
                    >
                      Get Recommendations
                    </Button>
                  </Box>
                </Grid>

                <Grid item xs={12} sm={12} md={4}>
                  <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: { xs: 'flex-start', md: 'flex-end' } }}>
                    <Typography variant="h6" color="primary.main" sx={{ mr: 1 }}>
                      ⭐ Special Offers
                    </Typography>
                    <Chip 
                      label="20% OFF" 
                      color="warning" 
                      variant="filled" 
                      size="small"
                      sx={{ mr: 1 }}
                    />
                    <Chip 
                      label="Free Delivery" 
                      color="success" 
                      variant="filled" 
                      size="small"
                    />
                  </Box>
                </Grid>
              </Grid>
            </Paper>

            {/* Header */}
            <Box sx={{ mb: 4, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <Avatar sx={{ bgcolor: 'primary.main', mr: 2, width: 56, height: 56 }}>
                  <Person sx={{ fontSize: 32 }} />
                </Avatar>
                <Box>
                  <Typography variant="h6" color="text.secondary">
                    Welcome back, {userData?.name || 'User'}!
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Member since {userStats.memberSince} • {userStats.points} points
                  </Typography>
                </Box>
              </Box>
            </Box>

            {/* User Stats Cards */}
            <Grid container spacing={3} sx={{ mb: 4 }}>
              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'success.main', mr: 2 }}>
                        <ShoppingCart />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          {userStats.totalOrders}
                        </Typography>
                        <Typography color="text.secondary">
                          Total Orders
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'info.main', mr: 2 }}>
                        <TrendingUp />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          ${userStats.totalSpent}
                        </Typography>
                        <Typography color="text.secondary">
                          Total Spent
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'warning.main', mr: 2 }}>
                        <Favorite />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          {userStats.favoriteItems}
                        </Typography>
                        <Typography color="text.secondary">
                          Favorite Items
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent>
                    <Box display="flex" alignItems="center">
                      <Avatar sx={{ bgcolor: 'primary.main', mr: 2 }}>
                        <Star />
                      </Avatar>
                      <Box>
                        <Typography variant="h4" fontWeight="bold">
                          {userStats.points}
                        </Typography>
                        <Typography color="text.secondary">
                          Points
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>

            {/* Quick Actions */}
            <Grid container spacing={3} sx={{ mb: 4 }}>
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Quick Actions
                  </Typography>
                  <List>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1, mb: 1 }}
                      onClick={handleNavigateToMealPlanner}
                    >
                      <ListItemIcon>
                        <Restaurant color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="Browse Products" 
                        secondary="Browse products and categories" 
                      />
                    </ListItem>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1, mb: 1 }}
                      onClick={handleNavigateToMealPlanner}
                    >
                      <ListItemIcon>
                        <TrendingUp color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="AI Recommendations" 
                        secondary="Receive AI-powered meal suggestions" 
                      />
                    </ListItem>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1, mb: 1 }}
                      onClick={handleNavigateToMealPlanner}
                    >
                      <ListItemIcon>
                        <Assessment color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="Smart Meal Planner" 
                        secondary="Plan your meals intelligently" 
                      />
                    </ListItem>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1 }}
                      onClick={handleViewOrderHistory}
                    >
                      <ListItemIcon>
                        <ShoppingCart color="primary" />
                      </ListItemIcon>
                      <ListItemText 
                        primary="Place Orders" 
                        secondary="View and manage your orders" 
                      />
                    </ListItem>
                  </List>
                </Paper>
              </Grid>

              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Recent Orders
                  </Typography>
                  <List>
                    {recentOrders.map((order) => (
                      <ListItem key={order.id} sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                        <ListItemText 
                          primary={order.vendor}
                          secondary={`${order.items} • $${order.total} • ${order.status}`}
                        />
                      </ListItem>
                    ))}
                  </List>
                  <Button variant="outlined" fullWidth sx={{ mt: 2 }}>
                    View All Orders
                  </Button>
                </Paper>
              </Grid>
            </Grid>

            {/* Favorites and Recommendations */}
            <Grid container spacing={3} sx={{ mb: 4 }}>
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    My Favorites
                  </Typography>
                  <List>
                    {favoriteItems.map((item) => (
                      <ListItem key={item.id} sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                        <ListItemText 
                          primary={item.name}
                          secondary={`${item.vendor} • $${item.price}`}
                        />
                        <Rating value={item.rating} precision={0.1} readOnly size="small" />
                      </ListItem>
                    ))}
                  </List>
                  <Button variant="outlined" fullWidth sx={{ mt: 2 }}>
                    View All Favorites
                  </Button>
                </Paper>
              </Grid>

              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Recommended for You
                  </Typography>
                  <List>
                    {recommendations.map((item) => (
                      <ListItem key={item.id} sx={{ borderRadius: 1, mb: 1, bgcolor: 'grey.50' }}>
                        <ListItemText 
                          primary={item.name}
                          secondary={`${item.vendor} • $${item.price} • ${item.reason}`}
                        />
                        <Rating value={item.rating} precision={0.1} readOnly size="small" />
                      </ListItem>
                    ))}
                  </List>
                  <Button variant="contained" fullWidth sx={{ mt: 2 }}>
                    Browse More
                  </Button>
                </Paper>
              </Grid>
            </Grid>

            {/* Progress Section */}
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                Rewards Progress
              </Typography>
              <Box sx={{ mb: 2 }}>
                <Box display="flex" justifyContent="space-between" alignItems="center" sx={{ mb: 1 }}>
                  <Typography variant="body2">Points to Next Reward</Typography>
                  <Typography variant="body2" color="primary.main">
                    {2000 - userStats.points} points
                  </Typography>
                </Box>
                <LinearProgress 
                  variant="determinate" 
                  value={(userStats.points / 2000) * 100} 
                  sx={{ height: 8, borderRadius: 4 }}
                />
              </Box>
              <Typography variant="body2" color="text.secondary">
                {userStats.points} / 2000 points to next reward
              </Typography>
            </Paper>
          </>
        )}

        {/* Orders View */}
        {currentView === 'orders' && (
          <Paper sx={{ p: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
              <Typography variant="h5" fontWeight="bold">
                Order History
              </Typography>
              <Button
                variant="outlined"
                startIcon={<Home />}
                onClick={handleBackToDashboard}
              >
                Return to Dashboard
              </Button>
            </Box>
            <Typography variant="body1" sx={{ mb: 3 }}>
              View your complete order history and track current orders.
            </Typography>
            <Button variant="contained">
              View All Orders
            </Button>
          </Paper>
        )}

        {/* Meals View */}
        {currentView === 'meals' && (
          <>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
              <Typography variant="h5" fontWeight="bold">
                Browse Vendor Products ({vendorProducts.length} items)
              </Typography>
              <Button
                variant="outlined"
                startIcon={<Home />}
                onClick={handleBackToDashboard}
              >
                Return to Dashboard
              </Button>
            </Box>

            {/* Add Product Form */}
            {showAddProductForm && (
              <Paper sx={{ p: 3, mb: 3, bgcolor: 'info.50' }}>
                <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                  Add New Product
                </Typography>
                <Grid container spacing={2}>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      label="Product Name"
                      value={newProduct.name}
                      onChange={(e) => setNewProduct({...newProduct, name: e.target.value})}
                      size="small"
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      label="Vendor Name"
                      value={newProduct.vendor}
                      onChange={(e) => setNewProduct({...newProduct, vendor: e.target.value})}
                      size="small"
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      label="Price ($)"
                      type="number"
                      value={newProduct.price}
                      onChange={(e) => setNewProduct({...newProduct, price: e.target.value})}
                      size="small"
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      fullWidth
                      label="Rating (1-5)"
                      type="number"
                      inputProps={{ min: 1, max: 5, step: 0.1 }}
                      value={newProduct.rating}
                      onChange={(e) => setNewProduct({...newProduct, rating: e.target.value})}
                      size="small"
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Description"
                      multiline
                      rows={2}
                      value={newProduct.description}
                      onChange={(e) => setNewProduct({...newProduct, description: e.target.value})}
                      size="small"
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <Box sx={{ display: 'flex', gap: 2 }}>
                      <Button variant="contained" onClick={handleSaveProduct}>
                        Save Product
                      </Button>
                      <Button variant="outlined" onClick={handleCancelAddProduct}>
                        Cancel
                      </Button>
                    </Box>
                  </Grid>
                </Grid>
              </Paper>
            )}

            {/* Vendor Products Grid */}
            <Grid container spacing={3} sx={{ mb: 4 }}>
              <Grid item xs={12} md={8}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Available Products from Vendors
                  </Typography>
                  {vendorProducts.length === 0 ? (
                    <Box sx={{ textAlign: 'center', py: 4 }}>
                      <Typography variant="body1" color="text.secondary">
                        No products available. Add your first product!
                      </Typography>
                    </Box>
                  ) : (
                    <Grid container spacing={2}>
                      {vendorProducts.map((product) => (
                        <Grid item xs={12} sm={6} md={4} key={product.id}>
                          <Card sx={{ height: '100%' }}>
                            <CardContent>
                              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
                                <Typography variant="h6" color="primary.main">
                                  {product.name}
                                </Typography>
                                <Chip label={`$${product.price}`} color="success" size="small" />
                              </Box>
                              <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                                {product.vendor}
                              </Typography>
                              <Typography variant="body2" sx={{ mb: 2 }}>
                                {product.description}
                              </Typography>
                              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                <Rating value={product.rating} precision={0.1} readOnly size="small" />
                                <Button variant="contained" size="small">
                                  Add to Cart
                                </Button>
                              </Box>
                            </CardContent>
                          </Card>
                        </Grid>
                      ))}
                    </Grid>
                  )}
                </Paper>
              </Grid>

              <Grid item xs={12} md={4}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                    Vendor Management
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
                    Manage your vendor products and inventory
                  </Typography>
                  <List>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1, mb: 1 }}
                      onClick={handleAddProduct}
                    >
                      <ListItemIcon>
                        <Restaurant color="primary" />
                      </ListItemIcon>
                      <ListItemText primary="Add New Product" secondary="Add items to your menu" />
                    </ListItem>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1, mb: 1 }}
                      onClick={() => alert(`Total Products: ${vendorProducts.length}`)}
                    >
                      <ListItemIcon>
                        <Assessment color="primary" />
                      </ListItemIcon>
                      <ListItemText primary="Manage Inventory" secondary={`Track ${vendorProducts.length} products`} />
                    </ListItem>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1, mb: 1 }}
                      onClick={() => alert('Sales analytics coming soon!')}
                    >
                      <ListItemIcon>
                        <TrendingUp color="primary" />
                      </ListItemIcon>
                      <ListItemText primary="View Analytics" secondary="Track sales performance" />
                    </ListItem>
                    <ListItem 
                      button 
                      sx={{ borderRadius: 1 }}
                      onClick={() => alert('Promotions feature coming soon!')}
                    >
                      <ListItemIcon>
                        <LocalOffer color="primary" />
                      </ListItemIcon>
                      <ListItemText primary="Create Promotions" secondary="Set up special offers" />
                    </ListItem>
                  </List>
                  <Button variant="contained" fullWidth sx={{ mt: 2 }}>
                    View All Products ({vendorProducts.length})
                  </Button>
                </Paper>
              </Grid>
            </Grid>
          </>
        )}

        {/* Favorites View */}
        {currentView === 'favorites' && (
          <Paper sx={{ p: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
              <Typography variant="h5" fontWeight="bold">
                My Favorites
              </Typography>
              <Button
                variant="outlined"
                startIcon={<Home />}
                onClick={handleBackToDashboard}
              >
                Return to Dashboard
              </Button>
            </Box>
            <Typography variant="body1" sx={{ mb: 3 }}>
              Manage your favorite food items and quick reorder.
            </Typography>
            <Button variant="contained">
              Manage Favorites
            </Button>
          </Paper>
        )}

        {/* Profile View */}
        {currentView === 'profile' && (
          <Paper sx={{ p: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
              <Typography variant="h5" fontWeight="bold">
                Profile Settings
              </Typography>
              <Button
                variant="outlined"
                startIcon={<Home />}
                onClick={handleBackToDashboard}
              >
                Return to Dashboard
              </Button>
            </Box>
            <Typography variant="body1" sx={{ mb: 2 }}>
              Name: {userData?.name || 'Test User'}
            </Typography>
            <Typography variant="body1" sx={{ mb: 2 }}>
              Email: {userData?.email || 'user@test.com'}
            </Typography>
            <Typography variant="body1" sx={{ mb: 2 }}>
              Role: {userData?.role || 'USER'}
            </Typography>
            <Typography variant="body1" sx={{ mb: 3 }}>
              Member Since: {userStats.memberSince}
            </Typography>
            <Button variant="outlined">
              Edit Profile
            </Button>
          </Paper>
        )}
      </Container>
    </Box>
  );
};

export default SimpleUserDashboard;
