import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  Button,
  Avatar,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  Alert,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Tabs,
  Tab,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  AppBar,
  Toolbar,
  IconButton,
  Menu,
  MenuItem,
  LinearProgress,
} from '@mui/material';
import {
  Person,
  ShoppingCart,
  Restaurant,
  Assessment,
  Favorite,
  History,
  LocalOffer,
  TrendingUp,
  AttachMoney,
  Logout,
  Add,
  Edit,
  Delete,
  Visibility,
  CheckCircle,
  Pending,
  Cancel,
  ArrowBack,
  Home,
  AccountCircle,
} from '@mui/icons-material';
import productService from '../services/ProductService';

const SimpleVendorDashboard = () => {
  const [vendorData, setVendorData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [currentView, setCurrentView] = useState('dashboard'); // dashboard, products, orders, analytics, promotions
  const [anchorEl, setAnchorEl] = useState(null);

  // Dialog states
  const [productDialogOpen, setProductDialogOpen] = useState(false);
  const [orderDialogOpen, setOrderDialogOpen] = useState(false);
  const [analyticsDialogOpen, setAnalyticsDialogOpen] = useState(false);
  const [promotionDialogOpen, setPromotionDialogOpen] = useState(false);
  const [activeTab, setActiveTab] = useState(0);

  // Sample data
  const [products, setProducts] = useState([
    { id: 1, name: 'Classic Burger', price: 12.99, category: 'Main Course', status: 'Available', orders: 45 },
    { id: 2, name: 'French Fries', price: 4.99, category: 'Side Dish', status: 'Available', orders: 89 },
    { id: 3, name: 'Caesar Salad', price: 8.99, category: 'Salad', status: 'Available', orders: 23 },
    { id: 4, name: 'Grilled Chicken', price: 15.99, category: 'Main Course', status: 'Unavailable', orders: 34 },
  ]);

  const [orders, setOrders] = useState([
    { id: 1234, customer: 'John Doe', items: 'Burger, Fries, Coke', total: 15.99, status: 'Pending', time: '2 mins ago' },
    { id: 1233, customer: 'Jane Smith', items: 'Pizza, Salad', total: 22.50, status: 'Preparing', time: '5 mins ago' },
    { id: 1232, customer: 'Bob Johnson', items: 'Sandwich, Juice', total: 8.75, status: 'Ready', time: '8 mins ago' },
    { id: 1231, customer: 'Alice Brown', items: 'Pasta, Bread', total: 18.25, status: 'Delivered', time: '12 mins ago' },
  ]);

  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    category: '',
    description: ''
  });

  useEffect(() => {
    // Get vendor data from localStorage
    const user = localStorage.getItem('user');
    const token = localStorage.getItem('token');

    if (!user || !token) {
      setError('Please login first');
      setLoading(false);
      return;
    }

    try {
      const parsedUser = JSON.parse(user);
      setVendorData(parsedUser);
      setLoading(false);
    } catch (err) {
      setError('Error loading user data');
      setLoading(false);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  // Navigation Functions
  const handleBackToDashboard = () => {
    setCurrentView('dashboard');
  };

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const getViewTitle = () => {
    switch(currentView) {
      case 'dashboard': return 'Vendor Dashboard';
      case 'products': return 'Product Management';
      case 'orders': return 'Order Management';
      case 'analytics': return 'Analytics';
      case 'promotions': return 'Promotions';
      default: return 'Vendor Dashboard';
    }
  };

  // Product Management Functions
  const handleAddProduct = () => {
    const productName = prompt('Enter product name:');
    const productPrice = prompt('Enter price (e.g., 12.99):');
    const productCategory = prompt('Enter category (e.g., Main Course):');
    const productDescription = prompt('Enter product description:');

    if (productName && productPrice && productCategory && productDescription) {
      const product = {
        id: Date.now(),
        name: productName,
        price: parseFloat(productPrice),
        category: productCategory,
        description: productDescription,
        status: 'Available',
        orders: 0
      };

      setProducts([...products, product]);
      alert(`Product "${productName}" added successfully!`);
    } else {
      alert('Please fill in all fields');
    }
  };

  const handleEditProduct = (productId) => {
    const product = products.find(p => p.id === productId);
    if (!product) return;

    const newName = prompt('Update product name:', product.name);
    const newPrice = prompt('Update price:', product.price);
    const newCategory = prompt('Update category:', product.category);
    const newDescription = prompt('Update description:', product.description || '');

    if (newName && newPrice && newCategory) {
      setProducts(products.map(p => 
        p.id === productId 
          ? { ...p, name: newName, price: parseFloat(newPrice), category: newCategory, description: newDescription }
          : p
      ));
      alert(`Product "${newName}" updated successfully!`);
    } else {
      alert('Please fill in all required fields');
    }
  };

  const handleDeleteProduct = (productId) => {
    const product = products.find(p => p.id === productId);
    if (!product) return;

    if (confirm(`Are you sure you want to delete "${product.name}"?`)) {
      setProducts(products.filter(p => p.id !== productId));
      alert(`Product "${product.name}" deleted successfully!`);
    }
  };

  const handleToggleProductStatus = (productId) => {
    setProducts(products.map(p => 
      p.id === productId 
        ? { ...p, status: p.status === 'Available' ? 'Unavailable' : 'Available' }
        : p
    ));

    const product = products.find(p => p.id === productId);
    alert(`Product "${product.name}" is now ${product.status}!`);
  };

  const handleProductManagement = () => {
    if (products.length === 0) {
      alert('No products available. Add a product first!');
      return;
    }

    const productOptions = products.map((product, index) => 
      `${index + 1}. ${product.name} - $${product.price} - ${product.status}`
    ).join('\n');

    const choice = prompt(`Select a product to manage:\n\n${productOptions}\n\nEnter product number (1-${products.length}):`);

    if (choice) {
      const index = parseInt(choice) - 1;
      if (index >= 0 && index < products.length) {
        const selectedProduct = products[index];
        const action = prompt(`Manage "${selectedProduct.name}":\n\n1. Update Product\n2. Delete Product\n3. Toggle Status\n\nEnter action number (1-3):`);

        if (action === '1') {
          handleEditProduct(selectedProduct.id);
        } else if (action === '2') {
          handleDeleteProduct(selectedProduct.id);
        } else if (action === '3') {
          handleToggleProductStatus(selectedProduct.id);
        } else {
          alert('Invalid action. Please try again.');
        }
      } else {
        alert('Invalid selection. Please try again.');
      }
    }
  };

  const handleViewProducts = () => {
    setCurrentView('products');
  };

  // Order Management Functions
  const handleUpdateOrderStatus = (orderId, newStatus) => {
    setOrders(orders.map(o => 
      o.id === orderId 
        ? { ...o, status: newStatus }
        : o
    ));
    alert(`Order #${orderId} status updated to ${newStatus}!`);
  };

  const handleViewOrderDetails = (order) => {
    alert(`Order Details:\n\nOrder ID: #${order.id}\nCustomer: ${order.customer}\nItems: ${order.items}\nTotal: $${order.total}\nStatus: ${order.status}\nTime: ${order.time}`);
  };

  const handleViewOrders = () => {
    setCurrentView('orders');
  };

  // Analytics Functions
  const handleViewAnalytics = () => {
    setCurrentView('analytics');
  };

  // Promotion Functions
  const handleCreatePromotion = () => {
    alert('ðŸŽ‰ Promotion Created!\n\nPromotion: 20% OFF on all burgers\nValid: Next 7 days\nCode: BURGER20\nStatus: Active');
    setPromotionDialogOpen(false);
  };

  const handleViewPromotions = () => {
    setCurrentView('promotions');
  };

  if (loading) {
    return (
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
          <CircularProgress size={60} />
        </Box>
      </Container>
    );
  }

  if (error) {
    return (
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Alert severity="error">{error}</Alert>
        <Button
          variant="contained"
          onClick={() => window.location.href = '/login'}
          sx={{ mt: 2 }}
        >
          Go to Login
        </Button>
      </Container>
    );
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      {/* Navbar */}
      <AppBar position="static" sx={{ mb: 3 }}>
        <Toolbar>
          {currentView !== 'dashboard' && (
            <IconButton
              edge="start"
              color="inherit"
              onClick={handleBackToDashboard}
              sx={{ mr: 2 }}
            >
              <ArrowBack />
            </IconButton>
          )}

          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            {getViewTitle()}
          </Typography>

          <IconButton
            edge="end"
            color="inherit"
            onClick={handleMenuOpen}
          >
            <AccountCircle />
          </IconButton>

          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={handleMenuClose}
          >
            <MenuItem onClick={handleMenuClose}>
              <ListItemIcon><Store /></ListItemIcon>
              <ListItemText>{vendorData?.name || 'Vendor'}</ListItemText>
