const Login = () => {
  const navigate = useNavigate();
  const theme = useTheme();
  const { login, loading, error, clearError } = useAuth(); // ‚Üê context error is fine

  const [formData, setFormData] = useState({ email: '', password: '' });
  const [formErrors, setFormErrors] = useState({});

  // ... keep validateForm, handleChange unchanged ...

  const handleManualLogin = async () => {
    if (!validateForm()) return;

    clearError(); // clear previous context error

    try {
      const result = await login(formData);

      if (result.success) {
        alert(`Welcome ${result.user?.name || 'User'}!`);
        setFormData({ email: '', password: '' });
        setFormErrors({});

        if (result.user?.role === 'ADMIN') {
          navigate('/admin/dashboard');
        } else if (result.user?.role === 'VENDOR') {
          navigate('/vendor/dashboard');
        } else {
          navigate('/meal-planner');
        }
      }
      // no else needed ‚Äî context should set error if login fails
    } catch (err) {
      console.error('Login error:', err);
      // Optional: if login() doesn't set error in context, you can force one
      // But ideally your login() function in AuthContext should do:
      // setError(err.response?.data?.message || 'Failed to login');
    }
  };

  return (
    <Container component="main" maxWidth="sm">
      {/* ... rest of your JSX unchanged ... */}
      {error && (
        <Alert severity="error" sx={{ width: '100%', mb: 2 }}>
          {error}
        </Alert>
      )}
      {/* ... form ... */}
    </Container>
  );
};
export default Login;
