import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  Button,
  Avatar,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Chip,
  Rating,
  AppBar,
  Toolbar,
  IconButton,
  Menu,
  MenuItem,
  Dialog,
  DialogTitle,
  DialogContent,
  LinearProgress,
  CircularProgress, // <-- ADD THIS
} from '@mui/material';
import {
  Person,
  ShoppingCart,
  Restaurant,
  Assessment,
  Favorite,
  TrendingUp,
  Logout,
  ArrowBack,
  Home,
  AccountCircle,
  Star,
} from '@mui/icons-material';

const SimpleUserDashboard = () => {
  // User Data
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentView, setCurrentView] = useState('dashboard');
  const [anchorEl, setAnchorEl] = useState(null);

  // Products and selections
  const [vendorProducts, setVendorProducts] = useState([]);
  const [selectedProductId, setSelectedProductId] = useState(null);
  const [showProductDetail, setShowProductDetail] = useState(false);

  // Dynamic state for stats, orders, favorites
  const [userStats, setUserStats] = useState({
    totalOrders: 0,
    totalSpent: 0,
    favoriteItems: 0,
    points: 0,
    memberSince: new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long' }),
    lastOrder: 'No orders yet',
    avgOrderValue: 0,
  });

  const [recentOrders, setRecentOrders] = useState([]);
  const [favoriteItems, setFavoriteItems] = useState([]);
  const [recommendations, setRecommendations] = useState([]);

  // =======================
  // Fetch products from backend
  // =======================
  useEffect(() => {
    const loadProducts = async () => {
      try {
        const response = await fetch('http://localhost:8081/api/products', {
          method: 'GET',
          headers: { 'Content-Type': 'application/json' },
        });
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);

        const data = await response.json();
        const products = Array.isArray(data?.data || data) ? data?.data || data : [];

        const normalized = products.map((p) => {
          const vendorName = p?.vendor?.shopName || p?.vendor?.name || p?.vendor || p?.vendorName;
          const vendorId = p?.vendor?.id || p?.vendorId;
          return { ...p, vendor: vendorName, vendorId };
        });

        setVendorProducts(normalized);
      } catch (error) {
        console.error('Error loading products:', error);
        setVendorProducts([]);
      }
    };

    loadProducts();

    const handleMessage = (event) => {
      if (event.data?.type) loadProducts();
    };

    window.addEventListener('message', handleMessage);
    return () => window.removeEventListener('message', handleMessage);
  }, []);

  // =======================
  // Load user data
  // =======================
  useEffect(() => {
    try {
      const user = localStorage.getItem('user');
      if (user) setUserData(JSON.parse(user));
      else
        setUserData({
          id: 1,
          name: 'Test User',
          email: 'user@test.com',
          role: 'USER',
        });
    } catch {
      setUserData({
        id: 1,
        name: 'Test User',
        email: 'user@test.com',
        role: 'USER',
      });
    }
    setLoading(false);
  }, []);

  // =======================
  // Handlers
  // =======================
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/home';
  };

  const handleBackToDashboard = () => setCurrentView('dashboard');
  const handleMenuOpen = (e) => setAnchorEl(e.currentTarget);
  const handleMenuClose = () => setAnchorEl(null);
  const handleNavigateToMealPlanner = () => setCurrentView('meals');
  const handleViewOrderHistory = () => setCurrentView('orders');
  const handleViewFavorites = () => setCurrentView('favorites');
  const handleViewProfile = () => setCurrentView('profile');

  // =======================
  // Product interactions
  // =======================
  const handleRateProduct = (productId, rating) => {
    setVendorProducts((prev) =>
      prev.map((p) => (p.id === productId ? { ...p, rating, totalRatings: (p.totalRatings || 0) + 1 } : p))
    );
    const product = vendorProducts.find((p) => p.id === productId);
    if (product) alert(`You rated "${product.name}" ${rating} stars!`);
  };

  const handlePlaceOrder = (product) => {
    const quantity = parseInt(prompt('Enter quantity:', '1'));
    if (!quantity || quantity <= 0) return alert('Please enter a valid quantity');

    const totalCost = product.price * quantity;

    // Update user stats
    setUserStats((prev) => {
      const totalOrders = prev.totalOrders + 1;
      const totalSpent = prev.totalSpent + totalCost;
      const points = prev.points + Math.floor(totalCost);
      const avgOrderValue = totalSpent / totalOrders;
      const lastOrder = new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'short', day: 'numeric' });

      return { ...prev, totalOrders, totalSpent, points, avgOrderValue, lastOrder };
    });

    // Update recent orders
    setRecentOrders((prev) => [
      { id: prev.length + 1, vendor: product.vendor, items: quantity, total: totalCost.toFixed(2), status: 'Placed' },
      ...prev,
    ]);

    alert(`Order placed: ${quantity} x "${product.name}" = NPR ${totalCost.toFixed(2)}`);
  };

  const handleAddToFavorites = (product) => {
    if (favoriteItems.some((item) => item.id === product.id)) return alert('This item is already in your favorites!');
    setFavoriteItems((prev) => [...prev, product]);
    setUserStats((prev) => ({ ...prev, favoriteItems: favoriteItems.length + 1 }));
    alert(`"${product.name}" added to favorites!`);
  };

  const handleRemoveFromFavorites = (itemId) => {
    const item = favoriteItems.find((f) => f.id === itemId);
    if (item && window.confirm(`Remove "${item.name}" from favorites?`)) {
      setFavoriteItems((prev) => prev.filter((f) => f.id !== itemId));
      setUserStats((prev) => ({ ...prev, favoriteItems: favoriteItems.length - 1 }));
      alert(`"${item.name}" removed from favorites!`);
    }
  };

  const handleViewProductDetail = (productId) => {
    setSelectedProductId(productId);
    setShowProductDetail(true);
  };

  const handleCloseProductDetail = () => {
    setShowProductDetail(false);
    setSelectedProductId(null);
  };

  const handleOrderFromFavorites = (item) => {
    alert(`Ordering "${item.name}" from ${item.vendor} for NPR ${item.price}`);
  };

  const handleViewCart = () => alert('Cart feature coming soon!');
  const handleQuickOrder = () => setCurrentView('meals');
  const handleGetRecommendations = () => setCurrentView('meals');

  const handleViewInventory = () => {
    const inventoryList = vendorProducts
      .map((product) => `${product.name} - ${product.vendor} - NPR ${product.price} - ${product.inStock ? 'In Stock' : 'Out of Stock'}`)
      .join('\n');
    alert(`Available Products:\n\n${inventoryList}\n\nTotal Products: ${vendorProducts.length}`);
  };

  const handleViewAnalytics = () => {
    const totalValue = vendorProducts.reduce((sum, product) => sum + product.price, 0);
    const avgPrice = vendorProducts.length ? (totalValue / vendorProducts.length).toFixed(2) : 0;
    const avgRating = vendorProducts.length ? (vendorProducts.reduce((sum, p) => sum + (p.rating || 0), 0) / vendorProducts.length).toFixed(1) : 0;
    alert(`Product Statistics:\nTotal Products: ${vendorProducts.length}\nAverage Price: NPR ${avgPrice}\nAverage Rating: ${avgRating}`);
  };

  const getViewTitle = () => {
    switch (currentView) {
      case 'dashboard':
        return 'User Dashboard';
      case 'meals':
        return 'Browse Meals';
      case 'orders':
        return 'Order History';
      case 'favorites':
        return 'My Favorites';
      case 'profile':
        return 'Profile Settings';
      default:
        return 'User Dashboard';
    }
  };

  if (loading)
    return (
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
          <CircularProgress size={60} />
        </Box>
      </Container>
    );

  return (
    <Box sx={{ flexGrow: 1 }}>
      {/* App Bar */}
      <AppBar position="static" sx={{ mb: 3 }}>
        <Toolbar>
          {currentView !== 'dashboard' && (
            <IconButton edge="start" color="inherit" onClick={handleBackToDashboard} sx={{ mr: 2 }}>
              <ArrowBack />
            </IconButton>
          )}
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            {getViewTitle()}
          </Typography>
          <IconButton edge="end" color="inherit" onClick={handleMenuOpen}>
            <AccountCircle />
          </IconButton>
          <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleMenuClose}>
            <MenuItem onClick={handleMenuClose}>
              <ListItemIcon>
                <Person />
              </ListItemIcon>
              <ListItemText>{userData?.name || 'User'}</ListItemText>
            </MenuItem>
            <MenuItem
              onClick={() => {
                handleMenuClose();
                handleLogout();
              }}
            >
              <ListItemIcon>
                <Logout />
              </ListItemIcon>
              <ListItemText>Logout</ListItemText>
            </MenuItem>
          </Menu>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ py: 2 }}>
        {/* ================= Dashboard View ================= */}
        {currentView === 'dashboard' && (
          <>
            {/* Welcome Section */}
            <Box sx={{ mb: 4, display: 'flex', alignItems: 'center', gap: 2 }}>
              <Avatar sx={{ bgcolor: 'primary.main', width: 56, height: 56 }}>
                <Person sx={{ fontSize: 32 }} />
              </Avatar>
              <Box>
                <Typography variant="h6">Welcome back, {userData?.name || 'User'}!</Typography>
                <Typography variant="body2">
                  Member since {userStats.memberSince}  {userStats.points} points
                </Typography>
              </Box>
            </Box>

            {/* Stats Cards */}
            <Grid container spacing={3} sx={{ mb: 4 }}>
              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <Avatar sx={{ bgcolor: 'success.main' }}>
                      <ShoppingCart />
                    </Avatar>
                    <Box>
                      <Typography variant="h4">{userStats.totalOrders}</Typography>
                      <Typography>Total Orders</Typography>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
              <Grid item xs={12} sm={6} md={3}>
                <Paper sx={{ p: 3, textAlign: 'center', bgcolor: 'success.main', color: 'white' }}>
                  <Typography variant="h4">NPR {userStats.totalSpent.toFixed(2)}</Typography>
                  <Typography>Total Spent</Typography>
                </Paper>
              </Grid>
              <Grid item xs={12} sm={6} md={3}>
                <Paper sx={{ p: 3, textAlign: 'center', bgcolor: 'warning.main', color: 'white' }}>
                  <Typography variant="h4">{userStats.favoriteItems}</Typography>
                  <Typography>Favorite Items</Typography>
                </Paper>
              </Grid>
              <Grid item xs={12} sm={6} md={3}>
                <Card>
                  <CardContent sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <Avatar sx={{ bgcolor: 'primary.main' }}>
                      <Star />
                    </Avatar>
                    <Box>
                      <Typography variant="h4">{userStats.points}</Typography>
                      <Typography>Points</Typography>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>

            {/* Rewards Progress */}
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" sx={{ mb: 2 }}>
                Rewards Progress
              </Typography>
              <Box sx={{ mb: 1, display: 'flex', justifyContent: 'space-between' }}>
                <Typography variant="body2">Points to Next Reward</Typography>
                <Typography variant="body2" color="primary.main">
                  {2000 - userStats.points} points
                </Typography>
              </Box>
              <LinearProgress variant="determinate" value={(userStats.points / 2000) * 100} sx={{ height: 8, borderRadius: 4 }} />
              <Typography variant="body2" color="text.secondary">
                {userStats.points} / 2000 points
              </Typography>
            </Paper>
          </>
        )}

        {/* ================= Meals / Products View ================= */}
        {currentView === 'meals' && (
          <Grid container spacing={3}>
            <Grid item xs={12} md={8}>
              <Paper sx={{ p: 3 }}>
                <Typography variant="h6" fontWeight="bold" sx={{ mb: 3 }}>
                  Available Products ({vendorProducts.length})
                </Typography>
                {vendorProducts.length === 0 ? (
                  <Typography>No products available.</Typography>
                ) : (
                  <Grid container spacing={2}>
                    {vendorProducts.map((product) => (
                      <Grid item xs={12} sm={6} md={4} key={product.id}>
                        <Card sx={{ cursor: 'pointer' }} onClick={() => handleViewProductDetail(product.id)}>
                          <img src={product.image} alt={product.name} style={{ width: '100%', height: 200, objectFit: 'cover' }} />
                          <CardContent>
                            <Typography variant="h6">{product.name}</Typography>
                            <Typography variant="body2">{product.vendor}</Typography>
                            <Typography>NPR {product.price}</Typography>
                            <Rating
                              value={product.rating || 0}
                              precision={0.1}
                              onChange={(e, value) => value && handleRateProduct(product.id, value)}
                            />
                            <Box sx={{ display: 'flex', gap: 1, mt: 1 }}>
                              <Button size="small" variant="contained" onClick={() => handlePlaceOrder(product)}>
                                 Order
                              </Button>
                              <Button size="small" variant="outlined" onClick={() => handleAddToFavorites(product)}>
                                 Favorite
                              </Button>
                            </Box>
                          </CardContent>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>
                )}
              </Paper>
            </Grid>
          </Grid>
        )}

        {/* ================= Favorites View ================= */}
        {currentView === 'favorites' && (
          <Paper sx={{ p: 3 }}>
            <Typography variant="h5" fontWeight="bold" sx={{ mb: 3 }}>
              My Favorites ({favoriteItems.length} items)
            </Typography>
            {favoriteItems.length === 0 ? (
              <Typography>No favorites added yet.</Typography>
            ) : (
              <Grid container spacing={2}>
                {favoriteItems.map((item) => (
                  <Grid item xs={12} sm={6} md={4} key={item.id}>
                    <Card>
                      <img src={item.image} alt={item.name} style={{ width: '100%', height: 200, objectFit: 'cover' }} />
                      <CardContent>
                        <Typography variant="h6">{item.name}</Typography>
                        <Typography variant="body2">{item.vendor}</Typography>
                        <Typography>NPR {item.price}</Typography>
                        <Rating value={item.rating || 0} precision={0.1} readOnly size="small" />
                        <Box sx={{ display: 'flex', gap: 1, mt: 1 }}>
                          <Button size="small" variant="contained" onClick={() => handleOrderFromFavorites(item)}>
                            Order Now
                          </Button>
                          <Button size="small" variant="outlined" color="error" onClick={() => handleRemoveFromFavorites(item.id)}>
                            Remove
                          </Button>
                        </Box>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            )}
          </Paper>
        )}

        {/* ================= Profile View ================= */}
        {currentView === 'profile' && (
          <Paper sx={{ p: 3 }}>
            <Typography variant="h5" fontWeight="bold" sx={{ mb: 2 }}>
              Profile Settings
            </Typography>
            <Typography>Name: {userData?.name}</Typography>
            <Typography>Email: {userData?.email}</Typography>
            <Typography>Role: {userData?.role}</Typography>
            <Typography>Member Since: {userStats.memberSince}</Typography>
            <Button variant="outlined" sx={{ mt: 2 }}>
              Edit Profile
            </Button>
          </Paper>
        )}

        {/* ================= Product Detail Dialog ================= */}
        <Dialog open={showProductDetail} onClose={handleCloseProductDetail} maxWidth="md" fullWidth scroll="paper">
          {selectedProductId && (
            <>
              <DialogTitle>Product Details</DialogTitle>
              <DialogContent>
                {(() => {
                  const product = vendorProducts.find((p) => p.id === selectedProductId);
                  if (!product) return <Typography>Product not found.</Typography>;
                  return (
                    <Grid container spacing={3}>
                      <Grid item xs={12} md={6}>
                        <img src={product.image} alt={product.name} style={{ width: '100%', borderRadius: 8 }} />
                      </Grid>
                      <Grid item xs={12} md={6}>
                        <Typography variant="h6">{product.name}</Typography>
                        <Typography variant="body2">{product.vendor}</Typography>
                        <Typography>NPR {product.price}</Typography>
                        <Typography>{product.description || 'No description available.'}</Typography>
                        <Rating
                          value={product.rating || 0}
                          precision={0.1}
                          onChange={(e, value) => value && handleRateProduct(product.id, value)}
                        />
                        <Box sx={{ display: 'flex', gap: 1, mt: 2 }}>
                          <Button variant="contained" onClick={() => handlePlaceOrder(product)}>
                             Order
                          </Button>
                          <Button variant="outlined" onClick={() => handleAddToFavorites(product)}>
                             Favorite
                          </Button>
                        </Box>
                      </Grid>
                    </Grid>
                  );
                })()}
              </DialogContent>
            </>
          )}
        </Dialog>
      </Container>
    </Box>
  );
};

export default SimpleUserDashboard;
