import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Box,
  Alert,
  CircularProgress,
  useTheme,
  Tabs,
  Tab,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl
} from '@mui/material';
import {
  Restaurant,
  Person,
  Email,
  Lock,
  Login,
  Store
} from '@mui/icons-material';
import { useAuth } from '../contexts/AuthContext';
import { authAPI } from '../services/api';

const Register = () => {
  const navigate = useNavigate();
  const theme = useTheme();
  const { register, login, loading, error, isAuthenticated } = useAuth();

  const [userType, setUserType] = useState('customer');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    phone: '',
    address: '',
    businessName: '',
    businessType: ''
  });

  const [formErrors, setFormErrors] = useState({});

  // Removed problematic useEffect that was causing auto-looping

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));

    // Clear errors when user starts typing
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: '',
      }));
    }
  };

  const handleUserTypeChange = (e, newValue) => {
    setUserType(newValue);
    setFormData({
      name: '',
      email: '',
      password: '',
      confirmPassword: '',
      phone: '',
      address: '',
      businessName: '',
      businessType: ''
    });
  };

  const validateForm = () => {
    const errors = {};

    // Common validations
    if (!formData.name.trim()) {
      errors.name = 'Name is required';
    }
    if (!formData.email.trim()) {
      errors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = 'Email is invalid';
    }
    if (!formData.password) {
      errors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      errors.password = 'Password must be at least 6 characters';
    }
    if (formData.password !== formData.confirmPassword) {
      errors.confirmPassword = 'Passwords do not match';
    }

    // Vendor-specific validations
    if (userType === 'vendor') {
      if (!formData.businessName.trim()) {
        errors.businessName = 'Business name is required';
      }
      if (!formData.businessType.trim()) {
        errors.businessType = 'Business type is required';
      }
      if (!formData.phone.trim()) {
        errors.phone = 'Phone number is required';
      }
      if (!formData.address.trim()) {
        errors.address = 'Address is required';
      }
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    const { confirmPassword, ...registerData } = formData;
    let result;

    try {
      if (userType === 'vendor') {
        // Use vendor registration API - map frontend fields to backend fields
        const vendorData = {
          name: registerData.name,
          email: registerData.email,
          password: registerData.password,
          shopName: registerData.businessName, // Map businessName to shopName
          businessType: registerData.businessType,
          phone: registerData.phone,
          address: registerData.address
        };

        console.log('Vendor registration data:', vendorData);
        result = await authAPI.registerVendor(vendorData);
      } else {
        // Use regular user registration API
        result = await register({
          ...registerData,
          role: userType.toUpperCase()
        });
      }

      if (result.success) {
        console.log('Registration successful for:', userType);
        alert('Registration successful! Redirecting to login page...');
        // Reset form
        setFormData({
          name: '',
          email: '',
          password: '',
          confirmPassword: '',
          phone: '',
          address: '',
          businessName: '',
          businessType: ''
        });
        setFormErrors({});
        // Redirect to login page after successful registration
        setTimeout(() => {
          navigate('/login');
        }, 2000);
      } else {
        console.error('Registration error:', result.error);
        setFormErrors({ 
          submit: result.error || 'Registration failed' 
        });
      }
    } catch (error) {
      console.error('Registration error:', error);
      setFormErrors({ 
        submit: error.response?.data?.message || error.response?.data?.error || 'Registration failed' 
      });
    }
  };

  return (
    <Container component="main" maxWidth="sm">
      <Box
        sx={{
          minHeight: '100vh',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Paper elevation={3} sx={{ padding: 4, width: '100%', maxWidth: 500 }}>
          <Box sx={{ textAlign: 'center', mb: 3 }}>
            <Restaurant sx={{ fontSize: 40, color: theme.palette.primary.main, mb: 1 }} />
            <Typography variant="h4" component="h1" gutterBottom>
              Join MealBasket
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Register as Customer or Vendor
            </Typography>
          </Box>

          {/* User Type Selection */}
          <Box sx={{ mb: 3 }}>
            <Tabs
              value={userType}
              onChange={handleUserTypeChange}
              variant="fullWidth"
              indicatorColor="primary"
            >
              <Tab 
                value="customer" 
                label="Customer" 
                icon={<Person />}
              />
              <Tab 
                value="vendor" 
                label="Vendor" 
                icon={<Store />}
              />
            </Tabs>
          </Box>

          <Box component="form" sx={{ mt: 1 }}>
            {error && (
              <Alert severity="error" sx={{ mb: 2 }}>
                {error}
              </Alert>
            )}

            {formErrors.submit && (
              <Alert severity="error" sx={{ mb: 2 }}>
                {formErrors.submit}
              </Alert>
            )}

            {/* Common Fields */}
            <TextField
              margin="normal"
              required
              fullWidth
              name="name"
              label="Full Name"
              value={formData.name}
              onChange={handleChange}
              error={!!formErrors.name}
              helperText={formErrors.name}
              autoFocus
            />

            <TextField
              margin="normal"
              required
              fullWidth
              name="email"
              label="Email Address"
              type="email"
              value={formData.email}
              onChange={handleChange}
              error={!!formErrors.email}
              helperText={formErrors.email}
            />

            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              value={formData.password}
              onChange={handleChange}
              error={!!formErrors.password}
              helperText={formErrors.password}
            />

            <TextField
              margin="normal"
              required
              fullWidth
              name="confirmPassword"
              label="Confirm Password"
              type="password"
              value={formData.confirmPassword}
              onChange={handleChange}
              error={!!formErrors.confirmPassword}
              helperText={formErrors.confirmPassword}
            />

            {/* Vendor-specific Fields */}
            {userType === 'vendor' && (
              <>
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="businessName"
                  label="Business Name"
                  value={formData.businessName}
                  onChange={handleChange}
                  error={!!formErrors.businessName}
                  helperText={formErrors.businessName}
                />

                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="businessType"
                  label="Business Type"
                  value={formData.businessType}
                  onChange={handleChange}
                  error={!!formErrors.businessType}
                  helperText={formErrors.businessType}
                />

                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="phone"
                  label="Phone Number"
                  value={formData.phone}
                  onChange={handleChange}
                  error={!!formErrors.phone}
                  helperText={formErrors.phone}
                />

                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="address"
                  label="Business Address"
                  value={formData.address}
                  onChange={handleChange}
                  error={!!formErrors.address}
                  helperText={formErrors.address}
                  multiline
                  rows={2}
                />
              </>
            )}

            <Button
              type="button"
              fullWidth
              variant="contained"
              disabled={loading}
              onClick={handleSubmit}
              sx={{ mt: 3, mb: 2 }}
              startIcon={loading ? <CircularProgress size={20} /> : <Login />}
            >
              {loading ? 'Registering...' : `Register as ${userType.charAt(0).toUpperCase() + userType.slice(1)}`}
            </Button>

            <Box textAlign="center" sx={{ mt: 2 }}>
              <Typography variant="body2">
                Already have an account?{' '}
                <Link to="/login" style={{ color: theme.palette.primary.main }}>
                  Sign In
                </Link>
              </Typography>
            </Box>
          </Box>

          {/* Terms and Privacy */}
          <Box sx={{ textAlign: 'center', mt: 2, width: '100%' }}>
            <Typography variant="caption" color="text.secondary">
              By creating an account, you agree to our{' '}
              <Link to="/terms" style={{ textDecoration: 'none' }}>
                <Typography component="span" variant="caption" color="primary.main">
                  Terms of Service
                </Typography>
              </Link>{' '}
              and{' '}
              <Link to="/privacy" style={{ textDecoration: 'none' }}>
                <Typography component="span" variant="caption" color="primary.main">
                  Privacy Policy
                </Typography>
              </Link>
            </Typography>
          </Box>
        </Paper>
      </Box>
    </Container>
  );
};

export default Register;
